style.css:
@-moz-document url-prefix("https://juejin.im") {
	body {
		background-color: #222 !important;
		background-image: url(data:image/png;base64,) !important;
	}
	/*  小册	 */
	.section-page.book-section-view {
		max-width: 1000px !important;
	}
	.step-btn.step-btn--prev {
		left: -80px;
	}
	.step-btn.step-btn--next {
		right: -80px;
	}

	.timeline-entry-list {
		width: inherit !important;
	}
	.view-container {
		min-height: 100vh;
	}
	.container {
		max-width: 1270px;
	}
	.sticky-block-box {
		background-color: rgba(0, 0, 0, .15);
		border-radius: 2px;
		padding: 5px;
	}
	.sticky-block-box .catalog-title {
		color: #42aedd !important;
		font-weight: 600;
	}
	.main-area,
	.entry-public-main {
		width: 1020px !important;
		max-width: 1020px !important;
		letter-spacing: 0.5px;
	}
	.user-view {
		width: 1270px !important;
		max-width: 1270px !important;
	}

	.topic-view,
	.pin-view {
		width: 950px !important;
		max-width: 950px !important;
	}
	.list-header[data-v-38ed369c] {
		padding: 1.6rem 0;
	}
	/* 左边：喜欢、分享等按钮 */
	.panel-btn {
		background-color: rgba(243, 245, 247, .5) !important;
	}
	.panel-btn:hover {
		background-color: rgba(243, 245, 247, .9) !important;
	}
	.panel-btn::after {
		color: #2e3135 !important;
		background-color: #3cb40e !important;
	}
	.stream {
		flex-grow: 1;
	}
	/* 右边：关于作者 */
	.author-block {
		/*left: 250px;*/
		/* 	background-color: rgba(243, 245, 247, .5) !important; */
	}
	/* 右边：推介、下载、广告 */
	.wechat-sidebar-block,
	/* .recommend, */
	.banner-block,
	.sidebar-block.recommend,
	.app-download-sidebar-block {
		/*display: none;*/
	}
	.more-block.block .more-item {
		font-family: LiHei Pro Medium, 'DejaVu Sans Mono', monospace;
		color: #f1f1f1;
		font-weight: 200;
	}
	/* 右边：相关文章、目录 */
	.catalog-block,
	.related-entry-sidebar-block {
		transition: .2s;
		/* 	background-color: rgba(243, 245, 247, .5) !important; */
	}

	.pin-list {
		/* 	background-color: transparent !important; */
	}

	.catalog-list .item.d1,
	.catalog-list .item.d2,
	.catalog-list .item.d3 {
		color: #42aedd !important;
	}


	.catalog-block:hover,
	.related-entry-sidebar-block:hover {
		transition: .2s;
		/* 	background-color: rgba(243, 245, 247, .9) !important; */
	}
	.index-book-collect {
		display: none !important;
	}
	.collection-list {
		/* 		height: 30rem !important; */
	}

	.global-component-box .request-health-alert {
		display: none;
	}
}
