style.css:
@-moz-document url-prefix('https://private.hubteam.com/zorse/'), url-prefix('https://tools.hubteam.com/zorse/') {
  
:root {
  --main-bg-color: #212121;
  --secondary-bg-color: #121212;
  --primary-font-color: #eee;
  --secondary-font-color: #eeeeeeb8;
  --primary-link-color: #66d9efd1;
  --primary-border: 1px solid rgba(255, 255, 255, 0.12); 
  --primary-button: #ff7a59cc;
  --secondary-button: #eaf0f6db;
}

body {
    background-color: var(--main-bg-color) !important;
    color: var(--primary-font-color);
}

a, .private-button__link {
    color: var(--primary-link-color) !important;
}

/* Nav styles */
.zorse-dashboard>nav .product-name-container img {
  filter: hue-rotate(45deg);
}
nav {
    background-color: var(--main-bg-color) !important;
    border-bottom: -1px solid rgba(255, 255, 255, 0.12) !important;
}
.zorse-dashboard>nav .nav-items .private-tab.private-tab--active {
    background-color: var(--secondary-bg-color) !important;
}
.zorse-dashboard>nav .nav-items .private-tab.private-tab {
    color: #66d9ef !important;
}
.private-button--primary {
  background-color: var(--primary-button) !important;
}
.private-button--tertiary-light {
  background-color: var(--secondary-button);
}
.next-ticket-language-select {
  font-size: 12px !important;
}
  
/* Sidebar styles */
.dashboard-sidebar {
    background-color: #333 !important;
    border-right: var(--primary-border) !important;
    box-shadow: none !important;
}

.sidebar-filters ul li {
    color: #c5c3c3 !important;
}

.sidebar-filters ul li:hover {
    background-color: var(--secondary-bg-color) !important;
}

.private-form__control {
    background-color: #ccc !important;
    color: var(--main-bg-color) !important;
}

.sidebar-more-options {
    color: #c5c3c3 !important;
}

.private-alert--info {
  background-color: var(--secondary-bg-color) !important;
}
.private-alert--danger {
  background-color: rgb(197, 100, 106);
}

/* Sidebar dropdown */
.private-popover.private-popover--default,
.private-dropdown--list .private-list__item>button {
    background-color: #333 !important;
}
.private-dropdown--list .private-list__item>button:hover {
  background-color: var(--secondary-bg-color);
}

.private-search-control__wrapper {
    background-color: var(--secondary-bg-color) !important;
}

.Select-menu-outer, .Select-option, .Select-option-group-label {
    background-color: var(--main-bg-color);
}

 .Select-option.is-focused {
   background-color: var(--secondary-bg-color) !important;
 }

.private-dropdown .Select-option button {
    color: #eee !important;
}
  
/* Dashboard ticket styles */
.dashboard-content-flex .ticket-container {
  border-top: var(--primary-border) !important;
}
.ticket-list .ticket .ticket-avatar img {
  filter: brightness(0.7);
}
.ticket-name, .ticket-body-text, .ticket-date {
    color: #c5c3c3 !important;
}
body .ticket-list .ticket>div {
  border-bottom: var(--primary-border);
}
.ticket-list .ticket:hover {
    background-color: var(--secondary-bg-color) !important;
}
.tag {
  background-color: rgba(228, 230, 253, 0.75);
}

/* Dashboard header */
.dashboard-content-flex>header {
    background-color: #333 !important; 
}

/* Ticket Page */
.inbox-sidebar, header {
    background-color: #333 !important;
    box-shadow: none !important;
}

/* Ticket page header */
.inbox-content header {
}

.inbox-content-flex>header h2 {
    color: #eee !important;
}

  /* Activities Container */
.activities .activity-text .activity-text-bubble {
  background-color: rgba(156, 39, 176, 0.08);
  color: var(--secondary-font-color);
}
.activities .activity-text.activity-rep-text .activity-text-bubble {
  background-color: rgba(17, 203, 95, 0.08);
  color: var(--secondary-font-color);
}
.activities .activity-text .activity-text-bubble h4 {
  color: var(--primary-font-color);
}
.activities .activity-action-container {
  background-color: var(--secondar-bg-color) !important;
  border: var(--primary-border);
}
.activities .activity-action-container .activity-action-text {
  color: var(--primary-font-color) !important;
}
.activities .activity-text .activity-text-bubble .activity-attachment {
  background-color: var(--main-bg-color) !important;
}
.activities .activity-text .activity-text-bubble a {
  color: #00839c;
}
.activities .activity-text.activity-nps .activity-text-bubble {
  background-color: var(--secondary-bg-color) !important;
}
.activities .activity-text.activity-note .activity-text-bubble {
    background-color: var(--secondary-bg-color) !important;
}
.rich-text-editor-container.note-edit {
  background-color: var(--secondary-bg-color) !important;
}
.new-note-button {
  background-color: var(--main-bg-color);
}

/* Ticket editor */
.ticket-editor {
    background-color: var(--secondary-bg-color) !important;
    border-top: var(--primary-border) !important;
}

.rich-text-editor-container {
  color: var(--primary-font-color);
  background-color: var(--main-bg-color);
  border: var(--primary-border);
}

.ticket-editor a {
    color: var(--primary-font-color) !important;
}

.private-link.uiLinkWithoutUnderline.uiLinkDark {
  color: var(--primary-link-color) !important;
}

.public-DraftEditorPlaceholder-root {
  color: var(--main-bg-color);
}

.created-at-age {
    color: var(--primary-font-color) !important;
}

.ticket-editor .ticket-editor-locked-form {
    background-color: var(--main-bg-color) !important;
}

.private-dropdown--list .private-list__item>a {
    background-color: #333 !important;
}
.private-dropdown--list .private-list__item>a:hover {
  background-color: var(--main-bg-color) !important;
}
  
.private-typeahead-results .private-typeahead-result--highlighted {
  background-color: var(--secondary-bg-color) !important;
  color: var(--primary-font-color);
}
  
.private-typeahead-results .private-typeahead-result--disabled .private-typeahead-result-label {
  background-color: var(--main-bg-color) !important;
  border-bottom: var(--primary-border);
}
  
.private-dropdown__item__image {
  filter: brightness(0.7);
}

/* Sidebar ticket list */
.inbox-sidebar {
  border-right: var(--primary-border);
}
.sidebar-ticket-list .ticket:hover {
    background-color: var(--main-bg-color) !important;
}
.sidebar-ticket-list .ticket {
  border-bottom: var(--primary-border) !important;
}
.inbox-content-flex>header .ticket-actions {
  border-top: var(--primary-border) !important;
}

.ticket-body, .ticket-body-unread {
    color: #eee !important;
}
  
.sidebar-ticket-list .ticket.selected {
  background-color: var(--main-bg-color);
}

/* Overview Sidebar */
.overview-sidebar {
    background-color: #333 !important;
    box-shadow: none !important;
    border-left: var(--primary-border) !important;
}

.overview-sidebar-overview-tab {
    color: #eee !important;
}

.overview-sidebar .overview-details .detail-name , .detail-value {
    color: #eee !important;
}

.UIExpandableSection__ExpandableHeader-gJHpqS {
    border-top: var(--primary-border) !important;
    border-bottom: var(--primary-border) !important;
}

.overview-sidebar .usage-and-certifications .certifications h5, .overview-sidebar .usage-and-certifications .certifications ul li {
    color: #eee !important;
}
/* GRR Side Panel */
.private-panel.private-panel--right {
  box-shadow: -3px 0 8px 0 rgba(0, 0, 0, 0.21);
}
.private-panel__header {
  background-color: var(--main-bg-color);
  background-image: none;
}
body .private-panel {
  background-color: #333;
}
body .private-modal__footer {
  background-color: var(--main-bg-color);
}
.no-tickets-container img {
  filter: invert(0.884);
}
}
