style.css:
@-moz-document domain("mathoverflow.net") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #EB8522 #1c1c1c;
}
/*HEADER*/
.top-bar .-secondary .-item .-link.topbar-icon-on, .top-bar .-secondary .-item .-link.topbar-icon-on:hover{
    background: #EB8522;
}
.topbar-dialog{
    border-left-color: #EB8522;
    border-right-color: #EB8522;
    border-bottom-color: #EB8522;
}
.topbar-dialog .header{
    background: #EB8522;
}.topbar-dialog .header h3{
    color: #1c1c1c;
}
.topbar-dialog .modal-content{
    background: #1c1c1c;
    color: #fff;
    margin: 0;
}
.huge-button-container a{
    color: #fff !important;
}
.topbar-dialog a:not(.btn-secondary):not(.nav-links--link):hover{
    background: #EB8522;
    color: #1c1c1c;
}
.topbar-dialog.help-dialog .item-summary{
    color: #fff;
}
.topbar-dialog.siteSwitcher-dialog{
    scrollbar-color: #EB8522 #1c1c1c;
}
.topbar-dialog ul{
    background: #1c1c1c;
}
.topbar-dialog ul li:hover{
    background: #EB8522 !important;
}
.topbar-dialog ul li:hover a span{
    color: #1c1c1c;
}
.topbar-dialog ul li:hover .js-gps-track{
    color: #1c1c1c;
}
.site-filter-container input{
    background: #1c1c1c !important;
    color: #fff;
}
.site-filter-container input::placeholder{
    color: #fff;
}
/*HEADER*/
/*WELCOME-MESSAGE*/
.s-btn__filled.s-btn__inverted{
    background: #EB8522;
}
/*WELCOME-MESSAGE*/
/*MAIN-PAGE*/
.h-auto,.va-middle{
    background: #EB8522;
}
.nav-links .youarehere .nav-links--link{
    color: #EB8522 !important;
}
.nav-links .nav-links--link, .nav-links .nav-links--link:visited{
    color: #fff;
}
#content{
    background: #1c1c1c;
    border-color: #EB8522;
}
.fs-headline1{
    color: #EB8522;
}
.narrow .mini-counts{
    color: #EB8522;
}
/*USERS*/
.users-page .user-info .user-details > a{
    color: #EB8522;
}
.users-page .user-info:hover{
    background: #1c1c1c;;
    border: 1px solid #EB8522;
}
.subtabs a,.subtabs a:hover{
    color: #fff;
}
.subtabs a.youarehere,.subtabs a.youarehere:hover{
    color: #EB8522;
}
#users-legend a{
    color: #EB8522;
}
/*USERS*/
/*UNANSWERED*/
#mainbar > div > div{
    color: #fff;
}
.vote span,.status strong{
    color: #EB8522;
}
.excerpt{
    color: #fff;
}
#sidebar h4{
    color: #EB8522;
}
/*UNANSWERED*/
/*LOGIN*/
.fs-title{
    color: #fff;
}
.bg-black-050{
    background: #1c1c1c !important;
}
.grid--cell .wmx3{
    color: #fff;
}
.bg-white{
    background: #1c1c1c !important;
    border: 1px solid #EB8522;
}
.grid label{
    color: #fff;
}
/*LOGIN*/
/*TAGS*/
.post-tag{
    background: transparent;
    color: #fff;
    border-color: #EB8522;
}
.post-tag:hover{
    background: #1c1c1c;
    color: #EB8522;
    border-color: #EB8522;
}
.item-multiplier,.item-multiplier span{
    color: #fff;
}
.tag-popup .-container{
    background: #1c1c1c;
    border-color: #EB8522;
}
.tag-popup .-container *{
    color: #fff;
}
.tag-popup .-container a{
    color: #EB8522;
}
/*MORE-TAGS*/
#mainbar-full h1{
    color: #EB8522;
}
#mainbar-full p{
    color: #fff;
}
.s-btn__muted.s-btn__outlined.is-selected{
    background: #EB8522;
    color: #fff;
    border-color: #EB8522;
}
.s-input.s-input__search{
    background: #1c1c1c;
    color: #fff;
    border-color: #EB8522;
}
.s-input.s-input__search::placeholder{
    color: #fff;
}
#content div h1{
    color: #EB8522;
}
#content div p{
    color: #fff;
}
#synonyms-table td,#synonyms-table td span{
    color: #fff;
}
.page-numbers{
    border-color: #EB8522;
}
#tags_list .stats-row a{
    color: #fff;
}
/*MORE-TAGS*/
/*TAGS*/
/*GROUP*/
.s-btn__muted.s-btn__outlined{
    color: #EB8522;
}
.s-btn__muted.s-btn__outlined:hover{
    border-color: #EB8522;
    color: #EB8522;
}
/*GROUP*/
/*NEW-ACTIVITY*/
.js-new-post-activity{
    background: transparent !important;
    border: 1px solid #EB8522;
}
/*NEW-ACTIVITY*/
/*POST*/
#sidebar h4{
    color: #EB8522;
}
.question-hyperlink{
    color: #EB8522;
}
#question-header .question-hyperlink{
    color: #EB8522;
}
.ws-nowrap{
    color: #fff;
}
pre{
    background: transparent;
}
.post-text{
    color: #fff;
}
code{
    background: transparent;
    color: purple;
}
.comment-copy{
    color: #fff;
}
.answers-subheader h2{
    color: #EB8522;
}
.new-contributor-indicator{
    background: #EB8522;
}
.new-contributor-indicator span{
    color: #fff;
}
.js-vote-count{
    color: #EB8522 !important;
}
.js-add-answer-component h2{
    color: #EB8522;
}
.new-login-left h3,.new-login-right h3,.new-login-right label,.new-login-right p{
    color: #fff;
}
.privacy-policy-agreement{
    color: #fff;
}
.bottom-notice{
    color: #fff;
}
.s-sidebarwidget{
    border: none;
}
.s-sidebarwidget__yellow{
    background: #1c1c1c;
    border-color: #EB8522;
}
.s-sidebarwidget::before{
    display: none;
}
.s-sidebarwidget--header .s-sidebarwidget__small-bold-text{
    border-color: #EB8522;
}
.s-sidebarwidget__yellow .s-sidebarwidget--header{
    background: #1c1c1c;
    color: #fff;
}
.s-sidebarwidget--header, .s-sidebarwidget--content{
    border:1px solid #EB8522 !important;
}
blockquote{
    background: transparent;
}
.wmd-button-bar{
    border-color: #EB8522;
}
.wmd-button-row{
    border-bottom: 1px solid #EB8522;
}
.wmd-button > span{
    background-color: #EB8522;
}
.mdhelp{
    border-left: 1px solid #EB8522;
    border-right: 1px solid #EB8522;
}
.mdhelp-tabs{
    background: #1c1c1c !important;
}
.mdhelp-tabs li{
    color: #fff;
}
.wmd-help-button.active-help{
    background: transparent;
}
.grippie{
    background-color: #EB8522;
    border: 1px solid #EB8522;
}
.s-input{
    background: transparent;
    border-color: #EB8522;
    color: #fff;
}
.s-notice__warning{
    background: transparent;
    border-color: #EB8522;
}
.s-notice__warning{
    color: #fff;
}
.fc-dark{
    color: #fff !important;
}
.ps-relative textarea{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #EB8522;
}
.s-btn__muted.s-btn__outlined{
    color: #fff;
}
/*POST*/
/*MAIN-PAGE*/
/*FOOTER*/
.site-footer{
    background: #1c1c1c;
}
.site-footer .-title a{
    color: #EB8522;
}
.site-footer .-title a:hover{
    color: #fff;
}
.site-footer--copyright p{
    color: #fff;
}
.site-footer--copyright:after{
    content: "Dark Theme by Maciejozaman";
    color: #EB8522;
    font-size: 13px;
}
/*FOOTER*/
}
