/* НОМЕР ШАГА КВЕСТА */
.form-memo-pref {
	flex-grow: 0;
	flex-shrink: 0;
	flex-basis: 35px;
	font-size: 1rem;
	font-weight: bold;
	text-align: center;
	padding: 6px 3px;
	box-sizing: border-box;
	color: white;
	text-shadow: 1px 1px 2px #27ae60;
	border-bottom: 1px solid #27ae60;
	background-color: #5dd562;
	border-radius: 5px 0px 0px 0px;
}






/* * * * * * ФОРУМ * * * * * */

/* БЛОК */
.forum-line {
	border-image: url("https://i.imgur.com/06rdbWh.png");
}

/* БЛОК В ФОРУМЕ */
.chat-block .header {
	display: block;
	padding: 4px 20px;
	border-bottom: 1px dotted #d8dcdd;
}
.chat-block {
	text-align: left;
	display: block;
	padding: 0;
	/* margin: 34px 2px 15px 45px; */
	margin: 13px 0 0 0;
	background-color: #ecf0f1;
	border-radius: 4px;
	border: 1px solid #d8dcdd;
}

/* ЦИТАТА */
.quote {
	border: 1px solid #A1D686;
	border-radius: 1px;
	color: #6CA851;
	background-color: #F6F7EF;
	box-shadow: 0 0 3px rgba(0, 0, 0, 0.1);
}

/* СМАЛИЙИК ПОМЕНЬШЕ */
.txsmile {
	width: 40px;
	height: 40px;
	margin: 3px 3px -10px 3px;
	display: inline-block;
}

/* ТЕМА */
.chat-block .theme {
	display: block;
	padding: 5px 20px 5px 20px;
	color: white;
	background-color: #f39c12;
	text-shadow: 1px 1px 1px #bd790e;
}

/* ПРЕДУПРЕЖДЕНИЕ/БАН */
.forum-alert{
	color: #667B90;
	background-color: #E0E5EA;
	text-shadow: 1px 1px 1px #C6CCD2;
	border-top: 1px dashed #A0AEBC;
	border-bottom: 1px dashed #A0AEBC;
    padding: 6px 20px 6px 20px;
}

/* ПРЕФИКС (В ТЕМЕ) */
.chat-block .header .online, .chat-block .header .author, .chat-block .header .ban {
	position: relative;
	top: -1px;
	color: white;
	font-size: .6rem;
	text-transform: uppercase;
	padding: 3px 6px;
	background: linear-gradient(to bottom, #80C153, #469B10) no-repeat center;
	margin-left: 4px;
	border-radius: 3px;
	text-shadow: 1px 1px 1px #327b37;
}

/* ПРЕФИКС (АВТОР) */
.chat-block .header .author {
	background: linear-gradient(to bottom, #F4C269, #EF9E11) no-repeat center;
	color: #fff;
	margin-left: 10px;
	border-radius: 3px;
	text-transform: uppercase;
	text-shadow: 1px 1px 1px #B1882E;
}

/* КНОПКА "НАЗАД" */
.back-btn {
	width: 36px;
	height: 24px;
	background: url(https://i.imgur.com/v7ukfnt.png) no-repeat;
	margin: 12px 6px 6px -10px;
	background-size: 30px;
	background-position-x: center;
	background-position-y: 3px;
    float: left;
}

.faq-btn:hover, .back-btn:hover {
	background-position-y: -15px;
    background-color: #ecf0f100;
}








