.gradeTableNew .calculatedRow.row {
    border-left-color: #ddd;
    border-right-color: #ddd;
    /*! -webkit-box-shadow: 0px 0px 0px 0px #e2f9fe inset; */
    /*! -moz-box-shadow: 0px 0px 21px 4px #e2f9fe inset; */
    box-shadow: 0px 0px 0px 0px #e2f9fe inset;
}

.filterBarHorizontal {
    width: 100%;
    height: 50px;
    background-color: #edededba;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    z-index: 6;
}

.gradeTableNew .row {
    color: #fff;
}

.stream-detail-contents {
    line-height: 1.5;
    color: #333;
    font: 100% Georgia, serif;
    /* Do not put a bottom margin on this class margin-bottom: 20px; */
    /* Do not put top or bottom padding on this class */
    padding: 12px 0 0 0;
    /* If the stream details are going to include avatars then add the class has-avatar to it*/
    float: left;
    /* We only do fancy-scrollbar vertically, not horizontally, so only hide the y-overflow, not the x */
    overflow-y: hidden;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    margin-right: 15px;
    width: 100%;
    /* Need this width to make sure the details fill the right-side.. TODO: Fix issue with scrollbar overlapping contents */
    border: 1px solid #959595;
}

.receiptBarHorizontal {
    background-color: #ededed00;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 6;
    color: #FFF;
    padding-left: 2%;
}

