style.css:
@-moz-document domain("soundcloud.com") {
::-webkit-scrollbar {
    width: 0px;  /* remove scrollbar space */  
}

body {
    background-color: white;
    font-family: 'airbnb cereal app medium', sans-serif !important;
    transition: opacity 1s ease-in;
}

/* toolbar */

.header__link.header__goUpsell {
    color: #333;
    transition: all 2s ease;
    width: 0;
    position: fixed;
    bottom: 300%;
}
.header__navMenu>li>a {
	border: none;
    margin-left: 20px;
}
.g-dark input[type=password], .g-dark input[type=search], .g-dark input[type=text], .g-dark select, .g-dark textarea {background-color: #282F33;}

.g-dark input[type=search] {border-bottom: 1px solid transparent}
.g-dark input[type=search]:focus {border-bottom: 1px solid white;}

.headerSearch__submit:hover {filter:invert(90%);transition: all 0.3s ease;}

.notificationIcon:hover {background-color:rgba(31, 31, 31, 1);transition: all 0.3s ease;}

.uploadButton__title {padding-right: 10px;}

.dropbar__content {
    border: 1px solid black;
    background-color: rgba(255,255,255,0.8)
}

/* remove width  */
.l-container {width: auto;} 

/* fonts */

.sc-font-light {
	font-family: 'airbnb cereal app light', sans-serif !important;
    font-weight: 600;
}

.sc-font {
	font-family: 'airbnb cereal app book', sans-serif !important;
    letter-spacing: 1px;
}
.sc-type-large {
    font-family: 'airbnb cereal app book', sans-serif !important;
    font-weight: bolder;
}
.header {
    font-family: 'airbnb cereal app medium', sans-serif;
    background: #282F33;
}

.sc-type-h1, .sc-type-h2, .sc-type-h3, .sc-type-h4, .sc-type-h5, .sc-type-h6, h1, h2, h3, h4, h5, h6 {
	font-family: 'airbnb cereal app medium', sans-serif !important;
}
/* buttons */

.sc-button-group>.sc-button, .sc-button-toolbar>.sc-button {margin-right: 15px;}
.sc-button-medium.sc-button-icon:not(.sc-button-stretch-icon), .sc-button-medium.sc-button-responsive:not(.sc-button-stretch-icon) {
    color: black;
    font-weight: 500;
    font-family: 'Arial Nova', sans-serif; 
    border-radius: 0;
    border: 1px solid white;
    font-size: 18px;
    
    transition: all 0.4s ease-out;
    
}
.sc-button-group>.sc-button, .sc-button-toolbar>.sc-button {margin-right: 15px;}
.sc-button-medium.sc-button-icon:not(.sc-button-stretch-icon), .sc-button-medium.sc-button-responsive:not(.sc-button-stretch-icon):hover {
	border-color: black;
    transition: all 0.2s ease-in;
}

/* player */

.playControls__bg, .playControls__inner {border-top: none;}

.l-container.l-fullwidth {
	width: auto;
    padding: 0 30px;
}

.volume.expanded .volume__sliderWrapper {
    background-color: rgba(242,242,242,0.7);
    border: none;
}
.volume__sliderProgress {width: 5px;}
.volume__sliderBackground {width: 5px;}
.volume__sliderHandle {left: 1.5px;}

/* general song page */ 

.playableTile__mainHeading {font-size: 16px;}
.usersList {
    width: 250px;
    margin-left: 20px;
    overflow:visible;
}

.backgroundGradient__buffer {
    opacity: 0.7;
    -webkit-mask-image: -webkit-gradient(linear, left bottom, left top, 
    from(rgba(0,0,0,1)), to(rgba(0,0,0,0)));
}
.backgroundGradient {
    opacity: 1;
 	background-color: #333 !important; 
    background: linear-gradient(60deg, rgba(40,47,51,1) 62%, rgba(255,255,255,1) 100%);
}

.fullHero__artwork {
    filter: drop-shadow(0 0 2rem #333);
    animation: var(--soundcloud-anime-reverse);
}
.playButton.m-stretch:active, .playButton.m-stretch:focus, .playButton.m-stretch:hover {
    background-color: rgba(31, 31, 31, 0.3);
    border-color: transparent;
}
.userBadge {
    transform: scale(1.1);
    padding-top: 30px;
    font-size: 20px;
    border-radius: 0 !important;

}
.playButton.m-stretch {
    transition: all 0.3s ease-in;
    background-color: rgba(31, 31, 31, 0.3);
    border: 2px solid transparent; 
}
.playButton.m-stretch:hover {
    transition: all 0.2s ease-out;
    background-color: rgba(31, 31, 31, 0.7);
    border-color: white;
    /* filter: grayscale(1); */
}

/* comment */
.commentForm.m-large .commentForm__input {height: 50px;}

.commentForm.m-large .commentForm__wrapper {
    height: 55px;
    border: none;
    background-color: white;
}

.playControls__inner, .playControls__bg {border: none;}

.playbackTimeline__progressBackground {background-color: rgba(31, 31, 31, 1);}

/* RANDOM / UNSORTED */

.g-tabs {font-family: inherit;}

.soundTitle.streamContext .soundTitle__title {
    font-family: 'inter', sans-serif;
    letter-spacing: 0.6px;
    font-size: 18px;
}

.soundTitle__usernameHero {
	font-weight: bolder;
    transition: all 0.3s ease-in-out;
}

.g-dark-bg{
	background-color: rgba(17, 17, 17, 0.6);
    font-family:'Corbel', sans-serif;
}
.gritter-item {
	background-color: rgba(17, 17, 17, 0.6);
	font-family:'Arial Nova', sans-serif;
}
.profileMenu__link {
    padding: 10px;
    text-align: right;
}
.profileMenu__profile:after {
    text-align: right;
}
.profileMenu__list {
    background-color: rgba(17, 17, 17, 0.6);
    font-family:'Arial Nova', sans-serif;
    font-size: 15px;
    transition: all 1s ease;
}

.profileMenu__link:hover {
    transition: all 0.5s ease;
    background: rgba(17, 17, 17, 0.4) !important;
}
.profileMenu__link {transition: all 0.5s ease;}
.profileMenu__link:after {top: auto;}
.profileMenu__list:not(:last-child) {border: none;}




.notifications {
    overflow: visible;
    padding-left: 20px;
    padding-right: 20px;
}

.dropdownMenu .g-z-index-header-menu {
    margin: 0;width: 436px;
    left: 50px;
}
.g-dark .sc-button:not(.sc-button-cta) {border-color: white;}
.sc-button-small.sc-button-follow:before {filter:invert(100%);}
.dropdownMenu {font-size: 14px;}
.header__userNavUsernameButton {transition: all 0.5s ease;}
.header__userNavUsernameButton:hover {background-color: rgba(17, 17, 17, 0.6); transition: all 0.5s ease;}

.playbackSoundBadge__titleLink, .playbackSoundBadge__titleLink:visited {
	color: black;
    font-size: 11px;
    font-weight: bolder;
    font-family: 'arial nova', sans-serif;
    letter-spacing: 0.8px;
}
.audibleTile__usernameHeading {font-size: 14px;}
.image__lightOutline .image__full {box-shadow: none;}

.profileMenu {
    width: 139px !important;
    transition: all 1s ease;
    animation: d;
}


}

@-moz-document domain("soundcloud.com") {
/* Profile */


.profileHeaderBackground__visual {
	background-size: cover;
	background-position: center;
}

.profileHeaderBackground.m-visualLoaded .profileHeaderBackground__visual {
	-webkit-filter: blur(0px);
	-moz-filter: blur(0px);
	filter: blur(0px);
    opacity: 1;
    -webkit-mask-image: -webkit-gradient(linear, left bottom, left top, 
    from(rgba(0,0,0,0.3)), to(rgba(0,0,0,1)));
    -moz-mask-image: -webkit-gradient(linear, left bottom, left top, 
    from(rgba(0,0,0,0.3)), to(rgba(0,0,0,1)));
}

.profileHeader {
	height: 480px;
}

.profileHeader__info {
	position: absolute;
	z-index: 1;
	bottom: 0px;
	bottom: 40px;
	left: 30px;
	width: 900px;
}

.profileHeaderInfo__content {
	bottom: 0px;
	left: 240px;
	width: 100%;
	position: absolute;
}

.profileHeaderInfo__userName {
	font-size: 32px;
	line-height: 37px;
	padding: 9px 9px;
}

.profileHeaderBackground {
	background: #282F33;
}

.profileMenu__link:hover {
    background: #282F33;
}

.featuredProfiles {
    background-color: rgba(40, 47, 51, 0.7);
    border-radius: 4px;
    height: 480px !important;
    -webkit-transform: translateY(440px);
    transform: translateY(440px);
    transition: transform .5s ease;
}

.featuredProfiles:hover {
    border-radius: 0px;
}

.featuredProfiles__wrapper {
	height: 480px !important;
}

.g-type-shrinkwrap-inline, .g-type-shrinkwrap-inline:hover, .g-type-shrinkwrap-inline:visited, .g-type-shrinkwrap-block, .g-type-shrinkwrap-block:hover, .g-type-shrinkwrap-block:visited {
    position: relative;
    background-color: transparent;
    border-radius: 4px;
    -webkit-box-decoration-break: clone;
    box-decoration-break: clone;
}
.searchMenu__searchFor.g-dark-selected {
    background-color: #282F33;
}
.searchMenu__searchFor {
    padding: 0;
    background-color: #282F33;
    border-bottom: 0px solid rgba(0,0,0,0);
    border-left: 0px solid transparent;
    border-right: 0px solid transparent;
    text-align: left;
    box-shadow: 0 0px 0 rgba(255,255,255,.05);
    width: auto;
    height: 0px;
    opacity: 0;
}
.g-dark-list>li>a:hover, .g-dark-list>li>a:focus, .g-dark-selected {
    background: #282F33;
    width: auto;
    color: #fff;
}
.searchAutocomplete>.loading {
    background-color: transparent;
    padding: 5px 0;
}
.g-dark input[type="search"]:focus {
    color: #fff;
    background: #282F33;
    width: 100%;
}

.autocompleteItem {
    padding: 0;
    width: auto;
}
.g-dark-list {
    background-color: #111;
    width: auto;
}
}

