:focus,
input.bttn-outline--dark,input.bttn--primary {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.ratings-and-model-number-container {
    flex: none;
}

.HeaderIcon__secondary {
    overflow: visible;
}

/*___ Fonts ________________________________________________________________________*/

.cartItemDesktop .total_price_font, .cartItemMobile .total_price_font {
	font-size: 31px!important;
}

.overlay__side-content__label,
.product-details__title,
.product-pod__title__product,

body {
	font-size: 21px!important;
}

.EtchVisualNav3AcrossMobile .visualNav__title,.EtchVisualNav3AcrossMobile .visualNav__title-center,
.TaskLinks,
.accordion__wrapper--medium .accordion__label, .u__normal,
.article-list-page__article-listing__items__item__content__title,
.footer__trademark,
.frequently-bought-together,
.refinement__link h3,
.related-search__elements-text,
.visual-category-navigation .vis-nav__title
{
    font-size: 19px!important;
}

.overlay__side-content__alt-media,
.overlay__side-content__product-images,
.reviews.not_rated:after {
	font-size: 17px!important;
}

.buybox__quantity .quantity-btn {
    font-size: 0;
}

.product-pod__title__product {
	line-height: 25px;
}

/*___ Remove Items _________________________________________________________________*/

#searchFeedback,

.carousel__left-gradient,
.carousel__right-gradient {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.reviews.not_rated:after {
	margin-left: 10rem;
}

.hsFlyout {
    margin-left: 25%;
}

.price+.span,.price+div,
.rebate-container,

div+.price,
span+.price {
	margin-left: 4rem;
}

.buybox__quantity .quantity-btn {
    margin-top: 8px;
}

.SearchBox__button {
    right: 39px;
}

.carousel__next, .carousel__prev, .decarousel__wrapper .carousel__next, .decarousel__wrapper .carousel__prev {
    top: 33%;
}

.HeaderIcon__primarySvg {
    top: 6px;
}

.article-list-page__article-listing__items__item__content-requirements__content__text {
    bottom: -3px;
}

.swiper-button-next, .swiper-container-rtl .swiper-button-prev {
    right: 10rem;
    left: auto;
}

/*___ Margins ______________________________________________________________________*/

.cartItemDesktop,
.carousel .fixed-item.slide,
.col__3-12--md,.col__4-12.col__4-12--sm.col__3-12--md,.col__7-12--md,
.experience-default .plp-pod,.experience-inspirational .plp-pod,
.grid.isBound>.col__12-12.body__p-all--none,
.pip-row,.plp.grid.isBound.plp--multistore-messaging,
.review_item,

[data-module-id="grid_thd_1316"],[data-module-id="grid_thd_4c0b"],[data-module-id="grid_thd_88d8"],[data-module-id="grid_thd_ed40"]
{
	margin: 12px!important;
}

.grid.isBound {
	margin: 1px auto;
}

/*___ Paddings _____________________________________________________________________*/

.experience-inspirational .plp-pod,
.grid.isBound>.col__12-12.body__p-all--none,
.masonry-column,
.pip-row,.plp.grid.isBound.plp--multistore-messaging,

[data-module-id="grid_thd_1316"],[data-module-id="grid_thd_4c0b"],[data-module-id="grid_thd_88d8"],[data-module-id="grid_thd_ed40"]
{
	padding: 12px!important;
}

.carousel__next, .carousel__prev, .decarousel__wrapper .carousel__next, .decarousel__wrapper .carousel__prev {
    padding: 30px 7px;
}

.bttn-outline .bttn__content {
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

.diyFlyout--open,
.footer__socialIcon,
.grid.isBound,
.hsFlyout--open,
.product-pod__title__product,
.thd-react-tooltip__content .tooltip-content--scrollable
{
	max-height: none!important;
}

.shop-by-category__category-icon-wrapper {
    height: auto!important;
}

.video-player__wrapper {
    height: 100%;
}

.endcap .products-section {
    height: 53rem!important;
}

.HeaderIcon__secondary--active {
	height: 48px;
}

.HeaderIcon__secondarySvg {
	height: 45px;
}

/*___ Widths _______________________________________________________________________*/

.experience-inspirational .plp-pod,
.footer__socialIcon,
.grid.isBound,
.pip-container-fluid
{
	max-width: none!important;
}

.bossbopis_msgs,
.carousel__wrapper .owl-stage[style="transform: translate3d(0px, 0px, 0px); transition: all 0s ease 0s; width: 1380px;"],.carousel__wrapper .owl-stage[style="transform: translate3d(0px, 0px, 0px);transition: all 0s ease 0s;width: auto;"],
.clear_btn span.short,
.list--save__summary>span,
