}

.col__9-12,
.thd-overlay__content iframe,
.video-player__wrapper
{
	width: 100%!important;
}

.review_item,
.grid.isBound,
.listOfLists__item
{
	width: 95%!important;
}

.col__3-12--md,
.experience-default .plp-pod,
.experience-technical .plp-pod {
	width: 23%;
}

.col__3-12 {
	width: 22%;
}

.experience-inspirational .plp-pod {
	width: 21%!important;
}

.related_articles_link-wrapper {
    width: 36rem;
}

.reviews,
.reviews .stars {
	width: 20rem;
}

}

