style.css:
@-moz-document url-prefix("https://skript.pl/") {

#ipsLayout_body {
    background-color: rgba(22, 22, 22, 0.9);
}
.ipsApp .ipsButton_important {
    background: #199FFF;
    color: #fff;
}

.ipsNavBar_primary:not( .ipsNavBar_noSubBars ) > ul:before, .ipsNavBar_secondary, .ipsNavBar_primary > ul > li.ipsNavBar_active > a, .ipsNavBar_secondary > li > a {
    background: #262626;
    color: #fff;
}
.ipsNavBar_primary {
    background: transparent;
}
.ipsNavBar_primary > ul > li > a {
    background-color: #2D2D2D;
    color: #fff;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title .cForumTitle {
    background-color: #111111;
    border-bottom: 1px solid #199FFF;
}
.ipsType_sectionTitle .ipsType_light, .ipsWidget_title .ipsType_light {
    color: #fff;
    font-weight: bold;
}
#elChatbox {
    background-color: #111111;
    font-weight: bold;
    
}
.cForumRow:not(.cForumRow_hidden) .cForumTitle, .ipsWidget_title, h2#elChatbox, .ipsMenu_headerBar {
    border-bottom: 1px solid #000000;
    background-color: #111111;
    border-bottom: 1px solid #199FFF;
}
.ipsType_sectionTitle, .ipsType_sectionTitle a {
    background-color: #111111;
    color: #fff;
    
}
.ipsWidget.ipsWidget_horizontal .ipsWidget_title
{
    background-color: #111111;
    border-bottom: 1px solid #199FFF;
}
.ipsBox {
    border-color: #000000;
    border-bottom: 2px solid #000000;
    
}

.ipsApp .ipsButton_primary {
    background: #199FFF;
    color: #fff;
}
.ipsApp .ipsButton_link {
    color: #fff;
}
.ipsComment div.ipsComment_content:not(.ipsModerated) 
{
    background-color: rgba(0, 0, 0, 0.0);

    padding-left: 10px;
}


.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated )
{
    background-color: rgba(0, 0, 0, 0.37);
}
.ipsAreaBackground {
    background: rgba(0, 0, 0, 0.37);
    
}
.ipsAreaBackground_light {
    background: rgba(0, 0, 0, 0.2) !important;
}
.ipsDataItem {
    background: rgba(30, 30, 30, 0.6);
}
pre.ipsCode {
    border-color: #199FFF !important;
}
.ipsComposeArea_dummy {
    background-color: rgba(0, 0, 0, 0.2);
    color: #fff;
    border-bottom: 2px solid #199FFF;
}
.ipsType_richText {
    color: #eee;
}
.ipsType_light {
    color: #eee;
}
.ipsFaded.ipsFaded_more {
    opacity: 0.9;
}
.ipsDialog > div, .ipsDialog_loading {
    background: rgba(0, 0, 0, 0.7);
}
.ipsTabs_item.ipsTabs_activeItem, .ipsTabs_panels, .ipsTabs_panel, .ipsStreamItem:not(.ipsAreaBackground_reset), .ipsWidget.ipsWidget_horizontal .ipsTabs_panel {
    background: rgba(0, 0, 0, 0.1);

}
.ipsApp input.bimcb_chatInput{
    border-bottom: 2px solid #199FFF !important;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even) {
    background: rgba(30, 30, 30, 0.3) !important;
}
.ipsApp .ipsToolList .ipsButton_link, .ipsApp .ipsButton_link.ipsButton_link_secondary {
    border-bottom: 2px solid #199FFF;}

.ipsColumn_medium {
    border-right: 2px solid #1f1f1f
}
}
