style.css:
@-moz-document domain("wayfarer.nianticlabs.com") {
/* General */
body,
html,
.niantic-loader {
    background-color: rgb(24, 24, 24);
}

.header {
    background-color: rgb(46, 46, 46);
}

#chart-contain h1 {
    color: #b3b3b3;
}

h3,
.help__footer {
    color: #fff;
}

h4 {
    color: #d0d0d0;
}

#SettingsController .settings-content .settings-item .item-header {
    color: #707070;
}

#SettingsController .settings-content .settings-item .item-text {
    color: #b3b3b3;
}

.niantic-loader__logo,
#nom-options-button,
#nom-table-title--arrow {
    filter: invert(50%);
}

/* Reviews */
.card,
.card__body,
.button-secondary,
.modal-body {
    box-shadow: 1px 2px 4px #282828;
}

.card-header__title,
span,
.nom-info-top-left h2,
p {
    color: #fff;
}

#photo-card .street-address,
.category__display-name,
.card-header__description,
.title-description,
.small,
.supporting-statement-central-field p,
div,
.comments-input,
.text-input.text-input,
.nom-info-top-left h3,
textarea {
    color: #d0d0d0;
}

.small.clickable {
    color: #00d6cb;
}

.card,
.card-style,
.low-quality-modal {
    background-color: rgb(46, 46, 46);
}

.supporting-central-field,
.modal-body,
.supporting-statement-central-field {
    background: rgb(42, 42, 42);
}

.cancel-btn::before {
    background: url(https://wayfarer.nianticlabs.com/img/cancel.svg) rgb(42, 42, 42);
}


/* Nomination Management */
#nom-search-title {
    background-color: rgb(46, 46, 46);
    color: #fff;
}

.nomination-title,
.nomination-category-header {
    color: #fff
}

#nom-description,
.nomination-category-body {
    color: #d0d0d0;
}

/* Tags */
.nomination-status--voting,
.nomination-status--queue {
    color: #d0d0d0;
    background: rgba(208, 208, 208, .169);
}

.nomination-status--duplicate {
    color: #ff2020bf;
    background: rgba(255, 0, 0, .2);
}

.nomination-status--upgrade {
    color: #00d2ee;
    background: rgba(0, 210, 238, .2);
}

.button-secondary.button-upgrade {
    color: #fff;
    background: rgb(0, 210, 238);
}

.button-secondary.button-upgrade:hover {
    color: #fff;
    background: rgb(1, 155, 176);
}

/* Buttons */
button[disabled],
button,
.button-secondary {
    color: #c9c9c9;
    background: rgb(46, 46, 46);
}

button[disabled]:hover,
button:hover,
.button-secondary:hover {
    background: #3d3d3d;
}

.item-edit {
    filter: invert(50%);
}

/* Showcase */
#gallery-info {
    background-color: rgb(46, 46, 46);
}
}
