style.css:
@-moz-document domain("decisionproblem.com") {
/*
Dark theme for Universal Paperclips
by mark g
beware of spoilers
Last updated Dec 8 2019
*/

body {
    background-color: #2b3032;
    color: #dcdcdc;
    font-family: sans-serif;
    font-size: 15;
}

#leftColumn { width: 268 }
#middleColumn { width: 255 }

p.consoleOld, p.console {
    font-size: 73%;
    line-height: 1.2;
    opacity: .89;
}


#prestigeDiv {
    color: hsl(0, 0%, 45%);
    background-color: #1119;
    font-style: italic;
    font-size: 13px;
}

/* Hover tooltips */
.toolTip .toolTipText, .toolTip2 .toolTipText2, .toolTip3 .toolTipText3  {
    color: #e9e9e9;
    background-color: #41434c;
}

/* Left column numbers */
#businessDiv { line-height: 1.4}
#manufacturingDiv { line-height: 1.4}
#creationDiv { line-height: 1.3}
#wireProductionDiv { line-height: 1.3}

/* Let "Make Paperclip" button always appear enabled, for less flickering visual noise  */
#btnMakePaperClip:disabled {
    background: #121212;
    color: #c2c2c2;
    opacity: 1;
}

/* Buttons */
/* Transparent border needed for disabled to not shift position */
.button2 {
    background: #121212;
    color: #c2c2c2;
    text-shadow: none;
    font-size: 11.7;
    padding: 2 5;
    box-sizing: content-box;
}

.button2:hover {
    background: #272727;
    color: #d3d3d3;
    text-shadow: none;
}

.button2:disabled {
    color: #fff6;
    background: #0008;
    border-color: #0000;
    text-shadow: none;
}

.engineText10 { padding: 0 2; }

/* Project buttons */
.projectButton {
    font-size: 12.3;
    background: #a5a5a5;
    color: #000;
    height: 49;
    width: 255;
    margin-bottom: 5;
    line-height: 1.2
}

.projectButton:hover {
    background: #c2c2c2;
    height: 49;
    width: 255
}

.projectButton:disabled {
    background: #565658;
    color: #141414
}

.projectButton:disabled:hover {
    background: #616161;
}

/* BIG project buttons to account for new text size */
/* "Human values, machine intelligence, a new era of trust." */
#projectButton27 {height: 60}
/* New Strategy: BEAT LAST - Choose the option... */
#projectButton66 {height: 60}
/* Self-correcting supply chain - Each factory added increases output by... */
#projectButton102 { height: 60}
/* Drone flocking - Each drone doubles every drone's output... */
#projectButton112 { height: 60}
/* Space exploration */
#projectButton46 { height: 60}
/* OODA Loop - Utilize speed to... */
#projectButton120 {height: 60}

/* Investments */
#investmentDiv1 {
    width: 80;
    padding: 0 4
}
#btnInvest { padding: 3 7}
#btnWithdraw { padding: 3 7}

/* Photonic chips */
.qChip {
    background-color: #fff;
    width: 20;
    height: 17;
    margin-top: 5;
    margin-left: 2;
    margin-right: 2;
    margin-bottom: 6;
}

#qComputing { height: 62}
#qCompDisplay { font-size: 90%}

/* Drop down menu - investing and strategy */
select {
    color: #c2c2c2;
    background-color: #1d1d1d;
    border-color: #787878;
}

/* Investments table cells */
table.table1 tr:nth-child(even) {
    background-color: #383c3f;
}
table.table1 tr:nth-child(odd) {
    background-color: #42464a;
}

/* Yomi tournament cells, must override html color setting */
#payoffCellAA, #payoffCellAB, #payoffCellBA, #payoffCellBB
{ background-color: #363839 !important}

/* Resize auto tourney text and ops cost text */
#autoTourneyStatusDiv {
    float: right;
    width: 110;
    font-size: 85%;
    color: #bcbcbc
}

.engineText8 {
    position: relative;
    top: 2; left: 3;
    font-size: 85%;
    color: #bcbcbc;
}

/* Release... */
#hypnoDroneText { color: #f00}

/* Adjustments for swarm computing column */
.slider { width: 95%}
#sliderLabelThink { left: 180}

#swarmEngine {
    font-size: 92%;
    line-height: 135%
}

#swarmGiftDiv {
    font-size: 90%;
    line-height: 1.8;
}

/* Exploration text adjustment */
#hazardBodyCount, #driftBodyCount, #combatBodyCount {
    font-size: 90%;
        line-height: 1.4
}
/* Combat */
#battleCanvasDiv {
    position:relative;
    top: 7
}
#honorDiv {font-size:85%; line-height:1.7}
canvas {background-color: #3c454e;}

/* Expand bottom of page */
.engineText1 {
    padding-bottom: 10
}

/* Final projects */
#projectButton140, #projectButton141, #projectButton142, #projectButton143, #projectButton144, #projectButton145, #projectButton146  {
    height: 80;
    color: #1145be;
    background-color: #ddd;
    font-size: 13;
}

/* Accept, reject */
#projectButton147, #projectButton148 {
    margin-top: 10;
    height: 85;
    font-size: 15;
}

/* Decide the future */
#projectButton200 {
    height: 90;
    color: #05008b;
    background-color: #e1d6f1;
    font-size: 13    
}
#projectButton201 {
    height: 90;
    color: #02409a;
    background-color: #c0dcff;
    font-size: 13
}

/* Finish */ 
#projectButton210, #projectButton211, #projectButton212, #projectButton213, #projectButton214, #projectButton215, #projectButton216 {
    height: 80;
    color: #fff;
    background-color: #000;
}
}
