@-moz-document url-prefix("https://www.horsereality.com"), url-prefix("https://www.horsereality.nl") {

  /*FONTS*/
  @font-face
  {
    font-family: 'Lobster Two';
    font-style: normal;
    font-weight: 400;
    src: local('Lobster Two'), local('LobsterTwo'), url(https://fonts.gstatic.com/s/lobstertwo/v11/BngMUXZGTXPUvIoyV6yN5-fN5qU.woff2) format('woff2');
  }
  @font-face {
    font-family: 'Quicksand';
    font-style: normal;
    font-weight: 400;
    src: local('Quicksand Regular'), local('Quicksand-Regular'), url(https://fonts.gstatic.com/s/quicksand/v9/6xKtdSZaM9iE8KbpRA_hK1QN.woff2) format('woff2');
  }
  @font-face { /*latin*/
    font-family: 'Parisienne';
    font-style: normal;
    font-weight: 400;
    src: local('Parisienne'), local('Parisienne-Regular'), url(https://fonts.gstatic.com/s/parisienne/v6/E21i_d3kivvAkxhLEVZpQyhwDw.woff2) format('woff2');
  }
  @font-face {
    font-family: 'Miniver';
    font-style: normal;
    font-weight: 400;
    src: local('Miniver'), url(https://fonts.gstatic.com/s/miniver/v8/eLGcP-PxIg-5H0vC37oIyg.woff2) format('woff2');
  }
  @font-face {
    font-family: 'Fredericka the Great';
    font-style: normal;
    font-weight: 400;
    src: local('Fredericka the Great'), local('FrederickatheGreat'), url(https://fonts.gstatic.com/s/frederickathegreat/v9/9Bt33CxNwt7aOctW2xjbCstzwVKsIBVV--Sjxbc.woff2) format('woff2');
  }
  
  /*GENERAL*/
  
  /*Background pic*/
  body.background {
    background: #4a7a9b url("https://i.imgur.com/XFUWycx.png") no-repeat fixed 0 0/100% auto !important;
  }
  
/*Body*/
  .advertising, .center, .container, .energybar, body, .training_left .horsetraining .traincon {
    background-color: #3b3946 !important;
  }

  
  /*NPC boxes*/
  .even, .breadcrumbs, .mail_row, .profile_account, .grid_12.deltapoints, .row_300, .mail_subheader, .grid_12.breeding, .tox .tox-toolbar, .tox .tox-toolbar__overflow, .tox .tox-toolbar__primary, #forum .row_960.no_padding, table tr, .exchange_books_window table tbody tr, .uni_statusbar, .adopt_blocks, .uni_blocks, .uni_blockimg, .block_tabclick:hover, .block_tabclick.tabsel, .npc_box, .container_12 .grid_8.training_left, .vet_blocks, .storeblocks, .horseblocks, #store_left.tabnav .store_tabclick:hover, #store_left.tabnav .store_tabclick.tabsel, input, select, html body#user.background.europe div.container div.container_12.center div.grid_12.stable_block form div.row_960, .tox .tox-statusbar, html body#forum.background.europe div.container div.container_12.center div.grid_12.stable_block div.row_960 {
    background-color: #54525b !important;
  }
  
  /*Text-color*/
  .breadcrumbs, .lab_selectall, #store_left, .top, p, h2, .breadcrumbs a:link, .breadcrumbs a:visited, .breadcrumbs a:active, .leftnav > ul > li > ul > li a:active, .leftnav > ul > li > ul > li a:link, .leftnav > ul > li > ul > li a:visited {
    color: #898989 !important;
  }
  .header-cta h2 span, .header-cta .cta-timer.alert.success, .alert.warning, .alert.success, .subtop, button, input[type="submit"], .subtitle, .mobile-menu-container .mobile-menu-scroller .sidemenu li .subtitle, form fieldset label, fieldset label {
    color: #000 !important;
  }
  
  /*Navigation*/
  .leftnav {
    background: -webkit-linear-gradient(top,#7f365e 0%,#400e2a 100%);
    border-radius: 0 0 85px 85px;
    border-bottom-width: 2px;
    border-right-width: 2px;
    border-left-width: 2px;
    border-color: #5e1e41;
    border-style: ridge;
    box-shadow: none;
    margin-left: -2px;
    width: 148px;
    height: 650px;
    margin-top: -1px;
  }
    
  /*Progress bars*/
  .trainbar, .energybar_half, .energybar, .horse_statusbar, .training_right > .block > .barcon > .trainbar{
    background-color: #6f6e74 !important;
    border: 2px solid #41575e !important;
  }
  .trainbar > .exp, .uni_xp, .energy, .horse_xp {
    background: -webkit-linear-gradient(top,#a61e70 0%,#2b1220 100%) !important;
  }
  .affection {
    background: -webkit-linear-gradient(top,#ff62c1 0%,#ce037e 100%) !important;
  }
  .disc {
    background: -webkit-linear-gradient(top,#63A902 0%,#2d5203 100%) !important;
  }
  
  /*Buttons*/
  button, input[type="submit"], button.yellow, input[type="submit"].yellow, button.dark, input[type="submit"].dark, button.orange, button.darkblue, input[type="submit"].darkblue, button.red, input[type="submit"].red, .alert.info, .alert.warning, .alert.success {
    background-color:  #7a4261 !important;
  }
  button:hover, input[type="submit"]:hover, button.yellow:hover, input[type="submit"].yellow:hover, button.dark:hover, input[type="submit"].dark:hover, button.orange:hover, button.darkblue:hover, input[type="submit"].darkblue:hover, button.red:hover, input[type="submit"].red:hover {
    background-color:  #622145 !important;
    color: #cea5c0 !important;
  }
  
  /*Links*/
    span.shoutout.popular, table thead tr.sub, .uni_statusbar, .alert.info, input, select, .col_150 a:active, .col_150 a:link, .col_150 a:visited, h1, h3, .curly-quotes::before, .curly-quotes::after, .quote, div.horse_left p, div.looking_at p, .energy_val, p a:active, p a:link, p a:visited, .horse_left .right a:active, .horse_left .right a:link, .horse_left .right a:visited, body, .col_300 a:active, .col_300 a:link, .col_300 a:visited, .column_200 a:active, .column_200 a:link, .column_200 a:visited, .col_300 a:active, .col_300 a:link, .col_300 a:visited, .col_500 a:active, .col_500 a:link, .col_500 a:visited, .subtop .col_900 a:link, .subtop .col_900 a:visited, .subtop .col_900 a:active, .col_400 a:active, .col_400 a:link, .col_400 a:visited, .col_100 a:active, .col_100 a:link, .col_100 a:visited, .adopt_blocktitle a:active, .adopt_blocktitle a:link, .adopt_blocktitle a:visited, table tr td a:active, table tr td a:link, table tr td a:visited, .energy_val_half, .col_200 a:active, .col_200 a:link, .col_200 a:visited, .adopt_blocktext p a:active, .adopt_blocktext p a:link, .adopt_blocktext p a:visited, .adopt_blocktitle a:active, .adopt_blocktitle a:link, .adopt_blocktitle a:visited, .store_title {
    color: #c2bebe !important;
  }
  
/*News bell*/
  li.special i.fa-bell, .header-cta h2 {
    color: #ce037e;
  }

/*Premium*/
  .premium-only {
    border: 2px #612948 solid !important;
    background:#6a465a !important;
  }
  
  /*Currency exchange*/  
  .exchange table thead th, table thead tr.top {
    background-color: #081b28 !important;
  }
  
  .exchange_orders_window_history .sell td:nth-child(2) {
    color: #eb008f !important;
  }
  
  .exchange_orders_window_history .buy td:nth-child(2) {
    color: #32b332;
  }
    
  /*DP*/
  .grid_12.deltapoints .buy_container .buy_block {
    background-color: #67656e !important;
  }
  
  span.shoutout.bestdeal {
    background-color: #e0007f !important;
  }
  
  span.shoutout.popular {
    background-color: #994474 !important;
  }

  /*Horses Page*/
  .horse_left, .looking_at {
    background-color: #612948d6 !important;
  }
  
  .genetic_potential {
    background-color: #3c3a41 !important;
  }
  
  .traincon.selected {
    background-color: #44444b !important;
  }  

  /*Messages*/
  .mail_row {
    border-bottom: #54525b !important;
  }
  
  /*Breeding Page*/
 .minidetails {
   background-color: #44424d !important;
 }

  /*Layout changes*/  
  .avatar, .mavatar {
    border: none;
  }

  /*Font changes*/
  div.horse_left h1 {
    font-family: "Lobster Two";
    font-size: 28px;
    text-align: left;
    color: #fff !important;
  }
  
  #bbmenu.user ul.slidedown li a.arrow {
    font-family: Quicksand;
  }
  
  .profile_username h1 {
    font-family: Parisienne;
  }
  
  .profile_menu ul li {
    font-family: Miniver;
  }
  
  .subtitle {
    font-family: Fredericka the great;
  }

  /*Drop down arrow*/
  .store_flex select {
    background: url(https://www.horsereality.com/images/bottom-arrow-blue.png) 100% 50% no-repeat #54525b !important;
  }
  
  /*Forum mods*/
  #forum .row_960 .col_750.mod {
    background: url(https://www.horsereality.com/images/moderator.png) no-repeat right top #54525b !important;
  }

/*?*/
  .numbers {
    background: #5d5b62 !important;
  }
  
  .odd, .exchange_books_window table tbody tr:nth-child(2n),  .energybar_half {
    background-color: #44434a !important;
  }
  
  .alert.info, .alert.warning {
    border: 1px solid #000 !important;
  }
  
  h1 a:active, h1 a:link, h1 a:visited {
    color: #a97592 !important;
  }
  
  .new {
    background-color: #ce037e !important;
  }
  
  input, select { 
    border-color: #34323c !important;
  }
  
  a:link, form fieldset a:visited {
    color: #969595 !important;
  } 
  
  .grid_6.genetics, .genetic_tests .test_block .first, .genetic_result, .horse_blockimg, .container_12 .grid_4.training_right, .training_right > .block, .training_right > .block > .comp img, .training_right > .block > .show img, .container_12 .grid_6.half_block, .pedigree {
    background-color: #535258 !important;
  }
  
  .subtop, table thead tr.sub, .tabclick, .tabclick2, .tabclick3, .training_left h6, .header-cta, .profile_menu {
    background-color: #612948 !important;
  }
  
  .genetic_table_row {
    border-bottom: 1px solid #535258 !important;
  }
  
  .exchange table td {
    border: 1px solid #000 !important;
  }
 
  .uni_statusbar, .energybar, .energybar_half, .alert.success {
    border: 2px solid #000 !important;
  }
  
}

@-moz-document url-prefix("https://v2.horsereality.com"), url-prefix("https://v2.horsereality.nl") {
    
  @font-face {
    font-family: 'Quicksand';
    font-style: normal;
    font-weight: 400;
    src: local('Quicksand Regular'), local('Quicksand-Regular'), url(https://fonts.gstatic.com/s/quicksand/v9/6xKtdSZaM9iE8KbpRA_hK1QN.woff2) format('woff2');
  }
  @font-face {
    font-family: 'Fredericka the Great';
    font-style: normal;
    font-weight: 400;
    src: local('Fredericka the Great'), local('FrederickatheGreat'), url(https://fonts.gstatic.com/s/frederickathegreat/v9/9Bt33CxNwt7aOctW2xjbCstzwVKsIBVV--Sjxbc.woff2) format('woff2');
  }
  
  /*Background pic*/
  body.background {
    background: #4a7a9b url("https://i.imgur.com/XFUWycx.png") no-repeat fixed 0 0/100% auto !important;
  }
  
  /*Navigation*/
  .side-nav {
    background: -webkit-linear-gradient(top,#7f365e 0%,#400e2a 100%);
    border-bottom-width: 2px;
    border-left-width: 2px;
    border-right-width: 2px;
    border-radius: 0 0 90px 90px;
    border-right-color: #5e1e41;
    border-bottom-color: #5e1e41;
    border-left-color: #5e1e41;
    border-right-style: ridge;
    border-bottom-style: ridge;
    border-left-style: ridge;
    box-shadow: none;
    width: 148px;
    height: 710px;
  }
  
  /*Bell*/
  .side-nav ul ul i.fa-bell {
    color: #ce037e;
  }
  
  .col-md-2{
    flex: 0 0 0 !important;
  }
  
  .progress, .advertising, .main-content, .shows .table .progress {
    background-color: #3b3946 !important;
  }
  
  .progress-bar, .btn.btn-cta, .component.frame .wrapper .header .action .light > .btn, .component.frame .wrapper .header .action > .btn, .btn.btn-darkblue {
    background-color: #7a4261 !important;
  }
  
  /*Progress bar*/
  .shows .table .progress .progress-bar, .competitions .table .progress .progress-bar {
    background: -webkit-linear-gradient(top,#a61e70 0%,#2b1220 100%) !important;
  }
    
  /*Clubs*/    
  .component.npc, .component.table .table tbody, .component.frame .wrapper .content, .component.comments .comment .profile-wrapper, .breadcrumb {
    background-color: #54525b;
  } 
  .alert-success, .alert-warning, .side-nav ul .subtitle, .table .thead-dark th, .btn.btn-cta, .component.frame .wrapper .header .action .light > .btn, .component.frame .wrapper .header .action > .btn {
    color: #000 !important;
  } 
  .component.frame .wrapper .header .action .light, .side-nav ul a, .component.frame .wrapper .header .title, .navbar-dark .navbar-nav .nav-link, a {
    color: #898989 !important;
  } 
  .component.table .table tbody tr:nth-of-type(2n+1) {
    background-color: #44434a !important;
  } 
  .component.table .table thead th, .component.frame .wrapper .subheader, .component.comments .comment .header {
    background-color: #612948 !important;
  } 
  .competitions .table .progress .progress-text, div.component table a, div.content > ul > li a, p a:active, p a:link, p a:visited, div.content > em a, .table, .shows .table .progress .progress-text {
    color: #c2bebe !important;
  } 
  .btn.btn-cta, .component.frame .wrapper .header .action .light > .btn, .component.frame .wrapper .header .action > .btn {
    border-color: #422435 !important;
  } 
  .table td, .table th {
    border-top: 1px solid #898989 !important;
  } 
  nav .pagination .page-item .page-link {
    background: #5d5b62 !important;
  } 
  .clubs nav.navbar.navbar, nav .pagination .page-item.active .page-link {
    background-color: #081b28 !important;
  } 
  .navbar-dark .navbar-nav .active > .nav-link, .navbar-dark .navbar-nav .nav-link.active, .navbar-dark .navbar-nav .nav-link.show, .navbar-dark .navbar-nav .show > .nav-link {
    color: #6a465a !important;
  } 
  .alert-warning, .alert-success {
    background-color: #9b9b9b !important;
    border-color: #000 !important;
  } 
  .component.frame .wrapper .subheader {
    color: #cacaca;
  }
  .form-control {
    background-color: #6f6d74 !important;
  }
  
  /*Competition*/
  .shows.show .mine td {
    background-color: #201e29 !important;
    border-bottom: 1px solid #ba0067 !important;
  }
  
  .shows .table .progress, .competitions .table .progress {
    border: 2px solid #000 !important;
  }

  /*Font changes*/
  header-menu nav .main-menu .user-nav > a {
    font-family: Quicksand;
  }
  
  .side-nav ul .subtitle {
    font-family: Fredericka the great;
  }
  
  /*Layout changes*/
  .component.comments .comment .profile-wrapper .profile .avatar {
    border: none !important;
  }
  
  /*Forum mods*/
  .component.comments .comment .content-wrapper.mod {
    background: url(https://www.horsereality.com/images/moderator.png) no-repeat right top #54525b !important;
  }
    
}
