style.css:
@-moz-document domain("czerwonapigulka.pl") {
html{
    background: #1c1c1c;
}
body{
    background: #1c1c1c;
}
body a{
    color: #EA0011;
}
:root{
    scrollbar-color: #EA0011 #1c1c1c;
}
/*HEADER*/
.fusion-header{
    background: #1c1c1c;   
}
.fusion-secondary-main-menu{
    background: #1c1c1c;
}
.fusion-main-menu > ul > li > a,.fusion-main-menu .fusion-widget-cart-counter > a::before{
    color: #fff;
}
.fusion-main-menu .sub-menu{
    background: #1c1c1c;
}
.fusion-main-menu .fusion-dropdown-menu .sub-menu li a{
    color: #fff;
}
.fusion-main-menu .fusion-dropdown-menu .sub-menu li a:hover{
    color: #EA0011;
    background: #1c1c1c;
}
.fusion-secondary-main-menu{
    background: #1c1c1c !important;
}
.fusion-is-sticky .fusion-main-menu > ul > li > a,.fusion-is-sticky .fusion-main-menu .fusion-widget-cart-counter > a::before{
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.fusion-fullwidth{
    background: #1c1c1c !important;
}
.fusion-text p strong,.fusion-text p strong span{
    color: #ff6600 !important;
}
.entry-title a{
    color: #EA0011;
}
.entry-title a:hover{
    color: #fff;
}
.recent-posts-content p{
    color: #fff;
}
#wrapper .meta{
    color: #fff;
}
.fusion-column-wrapper{
    border-color: #EA0011 !important;
}
.fusion-text h2{
    color: #fff;
}
.fusion-modal-content{
    background: #1c1c1c !important;
}
.fusion-modal .modal-header .modal-title{
    color: #fff;
}
.fusion-modal .modal-header .close{
    color: #fff;
}
.fusion-modal label{
    color: #EA0011;
}
.fusion-modal label input{
    background: #1c1c1c !important;
    color: #fff !important;
}
.content-container a,.content-container p{
    color: #fff;
}
.fusion-button.button-1,.fusion-button.button-2{
    background: #1c1c1c;
    border: 1px solid #EA0011;
}
.fusion-button.button-1 .fusion-button-text,.fusion-button.button-1 i,.fusion-button.button-2 .fusion-button-text, .fusion-button.button-2 i{
    color: #fff;
}
.fusion-button.button-1:hover,.fusion-button.button-2:hover{
    background: #EA0011;
    border: 1px solid #EA0011;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
#main{
    background: #1c1c1c;
}
.fusion-post-title{
    color: #EA0011 !important;
}
.fusion-column-wrapper p{
    color: #fff;
}
.post-content blockquote{
    background: #1c1c1c;
}
.post-content blockquote p a{
    color: #EA0011;
}
.post-content blockquote p a:hover{
    color: #fff;
}
.fusion-meta-info-wrapper{
    color: #fff;
}
.fusion-meta-info-wrapper a{
    color: #EA0011;
}
.fusion-meta-info-wrapper a:hover{
    color: #fff;
}
.fusion-single-sharing-box{
    background: #1c1c1c;
}
.fusion-sharing-box h4{
    color: #fff;
}
.title-heading-left{
    color: #fff !important;
}
.title-heading-left a{
    color: #EA0011 !important;
}
.title-heading-left a:hover{
    color: #fff !important;
}
.description{
    color: #fff;
}
.sidebar .widget .heading h4, .sidebar .widget .widget-title{
    color: #fff;
}
.fusion-content-widget-area .widget li a{
    color: #EA0011 !important;
}
.fusion-date,.fusion-inline-sep{
    color: #fff;
}
/*ARTYKUŁ*/
/*BLOG*/
.fusion-loading-msg{
    color: #fff;
}
/*BLOG*/
/*MESKI-GLOS*/
.fusion-load-more-button.fusion-blog-button{
    background: #1c1c1c;
    border: 1px solid #EA0011;
}
.fusion-load-more-button.fusion-blog-button:hover{
    background: #EA0011;
}
.fusion-load-more-button.fusion-blog-button, .fusion-load-more-button.fusion-blog-button{
    color: #fff;
}
.fusion-load-more-button.fusion-blog-button, .fusion-load-more-button.fusion-blog-button:hover{
    color: #fff;
}
/*MESKI-GLOS*/
/*SKLEP*/
.price del .amount{
    color: #fff;
}
#main .product .product_title{
    color: #EA0011;
}
.woocommerce-product-details__short-description p{
    color: #fff;
}
.quantity .qty{
    color: #fff;
}
.quantity .minus,.quantity .plus{
    background: #EA0011;
}
.posted_in,.tagged_as{
    color: #fff
}
.post-content p{
    color: #fff;
}
.woocommerce-tabs .entry-content h3{
    color: #EA0011;
}
.woocommerce-noreviews,.woocommerce-verification-required{
    color: #fff;
}
/*KOSZYK*/
.woocommerce-message{
    background: #1c1c1c;
    color: #fff;
}
.woocommerce-message::before{
    color: #EA0011;
}
.woocommerce h2{
    color: #EA0011;
}
.shop_table thead th{
    color: #EA0011;
}
.woocommerce-Price-amount{
    color: #fff;
}
.cart-collaterals .shipping-coupon .coupon #avada_coupon_code{
    background: #1c1c1c;
    color: #fff;
}
.cart-collaterals .shipping-coupon .coupon #avada_coupon_code::placeholder{
    color: #fff;
}
.cart-subtotal,.order-total{
    color: #EA0011;
}
/*KOSZYK*/
/*KASA*/
.avada-myaccount-user .username .hello{
    color: #EA0011;
}
.woocommerce-info{
    background: #1c1c1c;
    color: #fff;
}
.woocommerce-info:before{
    color: #EA0011;
}
.woocommerce .checkout_coupon .coupon-input .input-text{
    background: #1c1c1c;
    color: #fff;
}
.woocommerce .checkout_coupon .coupon-input .input-text::placeholder{
    color: #fff;
}
.woocommerce .checkout h3{
    color: #EA0011;
}
.required{
    color: #EA0011;
}
.fusion-disable-outline .woocommerce-billing-fields .form-row .input-text{
    background: #1c1c1c;
    color: #fff;
}
.fusion-disable-outline .woocommerce-billing-fields .form-row .input-text::placeholder{
    color: #fff;
}
.checkout .input-text{
    background: #1c1c1c;
    color: #fff;
}
.checkout .input-text::placeholder{
    color: #fff;
}
.select2-container--default .select2-selection--single .select2-selection__rendered{
    background: #1c1c1c;
    color: #fff;
}
.select2-container--default.select2-container--open .select2-dropdown .select2-search{
    background: #1c1c1c;
}
.select2-container--default .select2-dropdown .select2-search input{
    background: #1c1c1c;
    color: #fff;
}
.select2-results__option{
    background: #1c1c1c;
    color: #fff;
}
.select2-container--default .select2-results__option[data-selected="true"]{
    background: #EA0011;
}
.product-info{
    color: #EA0011;
}
.payment_methods li label{
    color: #fff;
}
.checkout .payment_methods .payment_box{
    background: #1c1c1c;
}
.tpay-insidebg{
    background: #1c1c1c;
}
.tpay-input-wrapper .tpay-input-value{
    color: #fff;
}
.tpay-input-wrapper .tpay-input-value::placeholder{
    color: #fff;
}
.tpay-amPmCheckbox input[type="checkbox"] + label{
    color: #fff;
}
.woocommerce .cart-empty{
    height: 100px;
}
/*KASA*/
/*SKLEP*/
/*POLITYKA-PRYWATNOŚCI*/
.post-content h2,.post-content h3{
    color: #EA0011;
}
.fusion-text ol li::marker,.fusion-text ol li,.fusion-text ol li::marker,.fusion-text ol li span{
    color: #fff;
}
.fusion-text ol li::marker,.fusion-text ol li ul li b{
    color: #fff;
}
/*POLITYKA-PRYWATNOŚCI*/
/*LOGIN*/
.woocommerce-form.woocommerce-form-login input,.woocommerce-form.woocommerce-form-register input{
    background: #1c1c1c;
    color: #fff;
}
.woocommerce-error li{
    background: #1c1c1c;
}
/*LOGIN*/
/*FOOTER*/
.fusion-footer-copyright-area{
    background: #1c1c1c;
}
.fusion-copyright-notice{
    color: #fff;
}
.fusion-footer-copyright-area a{
    color: #EA0011;
}
.fusion-copyright-notice:after{
    content: "Ciemny motyw by Maciejozaman";
}
/*FOOTER*/
}
