::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	/* Chrome, Firefox, Opera, Safari 10.1+ */
	color: #f48f8f;
	opacity: 1;
	/* Firefox */
}

}

