@-moz-document domain("podparadise.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.dropdown-menu>li>a:hover,
	
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.dropdown-menu>li>a,
	
body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.modal-header,
	
h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.col-md-12,
	
h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.col-sm-3 i,
.plyr__time--current,
	
time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

.btn-default,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("podparadise.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.a2a_menu a.a2a_i:active,
.a2a_menu a.a2a_i:focus,
.a2a_menu a.a2a_i:hover,
.a2a_menu a.a2a_more:active,
.a2a_menu a.a2a_more:focus,
.a2a_menu a.a2a_more:hover,
.dropdown-menu>li>a:focus,.dropdown-menu>li>a:hover,
.dropdown-toggle,
.modal-content,
.plyr--audio .plyr__controls button.tab-focus:focus,
.plyr--audio .plyr__controls button:hover,
.plyr--audio .plyr__controls,
.plyr__play-large,
.row,
.well
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.btn-primary:not(.listen):not(.dropdown-toggle)
{	
	background: radial-gradient(circle, #0a587f 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: 1rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-primary:not(.listen):not(.dropdown-toggle)::before
{
	background: radial-gradient(circle, #000 0%, #0a587f 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-primary:not(.listen):not(.dropdown-toggle):hover::before
 {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.listen
{	
	background: radial-gradient(circle, #022d01 0%, #228005 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: 1rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.listen::before
{
	background: radial-gradient(circle, #3de10b 0%, #066503 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.listen:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.container
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.a2a_menu,
.dropdown-menu,
.modal.fade .modal-dialog,
.popover,
.thumbnail
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.container:hover,
.dropdown-menu:hover,
.modal.fade .modal-dialog:hover,
.popover:hover,
.thumbnail:hover,

selector:hover.a2a_menu:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.col-md-9 .row.episodeRow:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}



}

@-moz-document domain("podparadise.com") {
/* Image Settings */

.a2a_menu a,
.thumbnail
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.a2a_menu a:hover,
.thumbnail:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

a,
a:hover
 {
	opacity: 1!important;
}

}

@-moz-document domain("podparadise.com") {
/* New Icons */

button,
html input[type=button],
input[type="checkbox"],
input[type="radio"],
input[type=reset],
input[type=submit]
{
    appearance: none!important;
}

.glyphicon-search:before {
    content: "";
}

.plyr__controls button {
    color: #0000;
}

/* Arrows _______________________________________________________________________*/

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

.caret
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

selector
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

selector
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.caret
{
    background-size: 25px!important;
    height: 26px!important;
    padding-left: 26px!important;
    width: 10px!important;
}

/* Pause ______________________________________________________________________________*/

selector {
    content: "";
}

[data-plyr="pause"] svg
{
	background: url(https://i.ibb.co/vkjYYs8/red-pause.png) no-repeat!important;
	box-sizing: border-box;
}

[data-plyr="pause"] svg:hover
{
	background: url(https://i.ibb.co/D7QSSmZ/red-pause-hover.png) no-repeat!important;
	box-sizing: border-box;
}

[data-plyr="pause"] svg,
[data-plyr="pause"] svg:hover
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Play ______________________________________________________________________________*/

selector {
    content: "";
}

[data-plyr="play"] svg
{
	background: url(https://i.ibb.co/7VB6KK3/red-play.png) no-repeat!important;
	box-sizing: border-box;
}

[data-plyr="play"] svg:hover
{
	background: url(https://i.ibb.co/qdS3kwB/red-play-hover.png) no-repeat!important;
	box-sizing: border-box;
}

[data-plyr="play"] svg:hover,
[data-plyr="play"] svg
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 100%!important;
}

/* Search _______________________________________________________________*/

.a2a_menu_find_container svg,
.glyphicon-search:before {
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
}

.a2a_menu_find_container svg,
.glyphicon-search:before {
    /* width: 10px!important; */
    background-size: 17px!important;
    height: 23px!important;
    padding-left: 20px!important;
}

/* Volume ______________________________________________________________________________*/

selector {
    content: "";
}

selector {
    top: -2px;
    left: -8px;
}

[data-plyr="mute"] svg
{
	background: url(https://i.ibb.co/xzPf8xZ/red-volume-on.png) no-repeat!important;
	box-sizing: border-box;
}

[data-plyr="mute"] svg:hover
{
	background: url(https://i.ibb.co/CQr6dDV/red-volume-on-hover.png) no-repeat!important;
	box-sizing: border-box;
}

[data-plyr="mute"] svg,
[data-plyr="mute"] svg:hover
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}
}

@-moz-document domain("podparadise.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
