input[type=checkbox].checkbox-control {
 position:absolute;
 top:0;
 left:0;
 margin-left:0;
 margin-top:0;
 z-index:1;
 cursor:pointer;
 opacity:0;
}
input[type=checkbox].checkbox-control+span {
 font-size:.875rem;
 line-height:1.5;
 font-weight:400;
 cursor:pointer;
 display:block;
 width:20px;
 height:20px;
 border:solid #0577af/*#44519e*/;
 background:#fff;
 border-width:2px;
 -webkit-transition:all;
 -moz-transition:all;
 -ms-transition:all;
 -o-transition:all;
 transition:all;
 transition-duration:0s;
 -webkit-transition-duration:250ms;
 position:relative;
}
input[type=checkbox].checkbox-control+span i {
 color:#fff;
 position:absolute;
 left:0;
 top:-4px;
 font-size:16px;
}
input[type=checkbox].checkbox-control:checked+span {
 background:#0577af/*#44519e*/;
}
.checkboxes-column {
