}
.radio-control input[type=radio]+label:before {
 position:absolute;
 content:"";
 top:2px;
 left:0;
 border:solid #0577af/*#44519e*/;
 background:#fff;
 border-width:2px;
 width:20px;
 height:20px;
 border-radius:50%;
 -webkit-transition:all;
 -moz-transition:all;
 -ms-transition:all;
 -o-transition:all;
 transition:all;
 transition-duration:0s;
 -webkit-transition-duration:250ms;
 /*transition-duration:250ms;*/
}
