@-moz-document domain("attattinsama.wordpress.com") {
html,
body,
header,
header > div,
nav ul,
nav li {
    border: 0 !important;
}

html,
body,
div#page {
    background-color: #222222;
    color: #d4d4d4;
}

div#primary main#main article.post {
    margin: 0 10% !important;
}

div#primary main#main article.post p {
    font-family: "/*[[asfont]]*/", sans-serif !important;
    font-size: /*[[assize]]*/ !important;
}
}
