@-moz-document domain("novelonlinefull.com") {
html {
    background-color: #111111;
    scrollbar-color: #666666 #222222;
}
body,
body#trang_doc {
    background: url(https://novelonlinefull.com/themes/home/images/use_your_illusion.png);
}
input {
    border: 1px solid #111111 !important;
}
body:not(#trang_doc) div:not(.owl-buttons):not(.owl-next):not(.owl-prev),
div#header,
div#header div,
div#header input,
div.breadcrumb,
#trang_doc div.menu_doc,
h1.name_chapter,
div#vung_doc,
div.top_thang,
div.truyen_info,
div.entry-header,
div.truyen_if_wrap,
div.truyen_if_wrap li,
div#noidungm,
div.chapter-list div.row span a,
table.theloai tbody tr,
table.theloai tbody tr td a,
div.itemupdate.first ul li a,
div.update_item h3 a,
div.update_item.list_category,
div.doc_nhieu ul li a,
div.top_header a#logo.logo,
div.top_header div.header_user a,
div.lem_bem,
div.lem_bem div,
div.cmt_doc,
div.new_update_trangdoc,
div.wrap_update.tab_anh_dep.danh_sach,
div.wrap_update.tab_anh_dep.danh_sach div,
div.wrap_update.tab_anh_dep.danh_sach div h3 a,
div.lam_nham_chapter,
div#footer div.footer_btm {
    color: #cacbcd !important;
    background: #262626 !important;
    background-color: #262626;
}
table.theloai tbody tr:nth-child(even),
table.theloai tbody tr:nth-child(even) td a,
body:not(#trang_doc) div.wrap_update.home div.update_item:nth-child(even),
body:not(#trang_doc) div.wrap_update.home div.update_item:nth-child(even) *,
div.doc_nhieu ul li:nth-child(even),
div.doc_nhieu ul li:nth-child(even) a,
body:not(#trang_doc) div.chapter-list div.row:nth-child(even),
body:not(#trang_doc) div.chapter-list div.row:nth-child(even) span a {
    background: #2d2d2d !important;
}
body:not(#trang_doc) div.update_item.list_category:nth-child(4n),
body:not(#trang_doc) div.update_item.list_category:nth-child(4n-3),
body:not(#trang_doc) div.update_item.list_category:nth-child(4n) *,
body:not(#trang_doc) div.update_item.list_category:nth-child(4n-3) *,
div.itemupdate.first:nth-child(4n) ul li a,
div.itemupdate.first:nth-child(4n-3) ul li a,
div.itemupdate.first:nth-child(4n),
div.itemupdate.first:nth-child(4n-3) {
    background: #2d2d2d !important;
}
div.top_header div.header_user span{
    filter: invert(0.7);
}
div#footer div.footer_btm img {
    filter: invert(0.6) saturate(7) hue-rotate(90deg);
}
ins.adsbygoogle {
    display: none !important;
}
div#vung_doc {
    font-family: "/*[[noffont]]*/", sans-serif !important;
    font-size: /*[[nofsize]]*/ !important;
    line-height: 160% !important;
}
}
