    /*dropdown menu's*/
    ol[role="menu"]                             { border: solid var(--border) !important; box-shadow: var(--box-shadow); background-color: var(--background-color-header); }
    li[role="menuitem"]:after                   { background-color: var(--header-dropdown-devider-line) !important; }
