style.css:
@-moz-document url-prefix('https://avansti.github.io/TI1.1-OGP0-OGP1-Lesmateriaal') {
	/* Global Style */
 body {
    background-color: #171717;
    color: white;
}
/* Navigation */
 .nav {
    position: sticky;
    top: 0;
    z-index: 5;
}
.nav-pills {
    background-color: #7d0000;
}
.nav-pills .show>.nav-link {
    color: white;
}
.dropdown-menu {
    background-color: #7d0000;
}
/* Break at start of page */
 hr {
    border-color: rgb(105, 105, 105);
}
/* All blockquotes */
 .container > blockquote {
    background-color: rgb(105, 105, 105);
}
/* Line-by-line executor */
 .col ~ :not(.language-java) {
    background-color: rgb(105, 105, 105) !important;
    border-color: white !important;
}
/* Variable values for Line-by-line executor */
 .list-group-item {
    background-color: rgb(30, 30, 30);
}
/* Exercise container */
 .container > .exercises {
    background-color: #7d0000;
}
/* Code field */
 .language-java {
    border-color: white;
    background-color: rgb(30, 30, 30);
}
/* Code in tekst */
 .language-plaintext {
    color: rgb(220, 220, 220) !important;
    background-color: rgb(30, 30, 30);
    padding-left: 5px;
    padding-right: 5px;
    border-radius: 2px;
}
/* Tip box */
 .tip {
    background-color: #7d0000 !important;
}
/* Link color */
 a {
    color: #4db2ff
}
/* ------------------------------
	Begin of code highlight
   ------------------------------
*/
 .highlight * {
    font-weight: normal !important;
    font-style: normal !important;
}
/* Overig */
 .highlight .o {
    color: rgb(220, 220, 220);
}
/* Class */
 .highlight .nc {
    color: rgb(78, 201, 176);
}
/* Variable name */
 .highlight .n {
    color: rgb(156, 220, 254);
}
/* Keywords (like 'new', 'if', etc.) */
 .highlight .k {
    color: rgb(61, 142, 214);
}
/* 'import' keyword */
 .highlight .kn {
    color: rgb(61, 142, 214);
}
/* Access modifiers */
 .highlight .kd {
    color: rgb(61, 142, 214);
}
/* Methods */
 .highlight .nf {
    color: rgb(220, 220, 170);
}
/* Primitives */
 .highlight .kt {
    color: rgb(61, 142, 214);
}
/* Attributes */
 .highlight .na {
    color: rgb(220, 220, 220);
}
/* Int Numbers */
 .highlight .mi {
    color: rgb(181, 206, 168);
}
/* Double Numbers */
 .highlight .mf {
    color: rgb(181, 206, 168);
}
/* Binary Numbers */
 .highlight .mb {
    color: rgb(181, 206, 168);
}
/* Octal Numbers */
 .highlight .mo {
    color: rgb(181, 206, 168);
}
/* Hexadecimal Numbers */
 .highlight .mh {
    color: rgb(181, 206, 168);
}
/* Strings */
 .highlight .s {
    color: rgb(206, 145, 120);
}
/* // Commands */
 .highlight .c1 {
    color: rgb(87, 166, 74);
}
/* /* Commands */
 .highlight .cm {
    color: rgb(87, 166, 74);
}
/* true/false */
 .highlight .kc {
    color: rgb(61, 142, 214);
}
/* Don't know where they use it for, set it to the same color as Overig */
 .highlight .no {
    color: rgb(220, 220, 220);
}
/* Annotations */
 .highlight .nd {
    color: rgb(153, 153, 149);
}
/* Package name */
 .highlight .nn {
    color: rgb(220, 220, 220);
}  
}
