}

.inner .logo-img {
    display: none;
}

.innner.logo-img {
    display: none;
}

.item-topbar .close-button {

    background: var(--gray-800);
}


/* ////// SPACE SWITCHER - Menu lateral ////// */


.space-switcher .space-switcher-sidebar {
    transition: all .5s cubic-bezier(.04,.91,.24,.99) 0s;
    height: calc(100vh - 66px);
    top: 54px;
    z-index: 10000;
    background: var(--gray-700);
    border-right: none;
    left: -8px;
    border-radius: var(--theme-radius);
    box-shadow: 0px 16px 32px 0px rgba(0,0,0,.5);
}

.space-switcher .space-switcher-sidebar.visible {
    left: 12px;
}

.left-nav {
    background: var(--gray-700);
    color: var(--gray-200);
}

.left-nav .org-navigation>li {
    border-bottom: none;
    border-top: none;
}

.left-nav .org-navigation>li:last-child .header {
    background: var(--gray-800) !important;
    margin-bottom: 16px;
    color: var(--gray-400) !important;
    font-size: 1.75rem;
}

.left-nav .org-navigation ul.org-spaces {
    margin-bottom: 8px;
    font-size: 1.375rem;
}

.space-switcher .left-nav-container .org-navigation ul.org-spaces li.employee-network>a,
.space-switcher .left-nav-container .org-navigation ul.org-spaces li.grants>a,
.space-switcher .left-nav-container .org-navigation ul.org-spaces li.regular-space>a {
    padding-right: 16px;
}

.left-nav .org-navigation ul.org-spaces li.grants>a, 
.left-nav .org-navigation ul.org-spaces li.regular-space>a,
.left-nav .org-navigation ul.org-spaces li.employee-network a {
    color: var(--gray-400);
    padding: 6px 0px 6px 50px;
    line-height: 21px;
}

.left-nav .org-navigation ul.org-spaces li.grants>a .icon-16,
.left-nav .org-navigation ul.org-spaces li.regular-space>a .icon-16,
.left-nav .org-navigation ul.org-spaces li.employee-network a .icon-16 {
    top: 8px;
    left: 24px;
}

.left-nav .org-navigation ul.org-spaces li.grants:hover,
.left-nav .org-navigation ul.org-spaces li.regular-space:hover,
.left-nav .org-navigation ul.org-spaces li.employee-network:hover{
    background-color: var(--gray-800);
}

.left-nav .org-navigation ul.org-spaces li.grants:hover>a,
.left-nav .org-navigation ul.org-spaces li.regular-space:hover>a,
.left-nav .org-navigation ul.org-spaces li.employee-network:hover a{
    color: var(--gray-400);
}

.left-nav .org-navigation ul.org-spaces li.grants.current>a,
.left-nav .org-navigation ul.org-spaces li.regular-space.current>a,
.left-nav .org-navigation ul.org-spaces li.employee-network.current>a,
.left-nav .org-navigation ul.org-spaces li.employee-network.current {
    background-color: var(--gray-600);
    border-top: none;
}

.left-nav .org-navigation ul.org-spaces li {
    transition: all 0s ease-in;
}

.left-nav .org-navigation ul.org-spaces>li.space-more-link,
.left-nav .org-navigation ul.org-spaces>li.space-less-link {
    background-color: rgba(255,255,255,.06);
    padding: 6px 0;
    margin: 16px 22px 16px;
    border-radius: var(--btn-radius)
}

.left-nav .org-navigation ul .space-less-link a, .left-nav .org-navigation ul .space-more-link a {
    font-size: 1.25rem;
    letter-spacing: 1px;
    margin-left: 16px;
    color: var(--gray-100);
    padding-right: 20px;
}

.left-nav .org-navigation ul .space-more-link a {
    background-position: right -257px;
    opacity: .8;
}

.left-nav .org-navigation ul .space-less-link a {
    background-position: right -992px;
    opacity: .8;
}

.left-nav .org-navigation>li .body .nav-button {
    margin-left: 22px;
    min-width: unset;
    width: calc(100% - 61px);
}

.left-nav .button-new.white {
    background: rgba(255,255,255,.9);
    border: 1px solid transparent;
}



/* ////// ITEM OVERLAY ////// */



.content-overlay.background {
    background-color: rgba(203,209,216, .95);
}

.items-section .body .item-content {
    border-radius: var(--theme-radius);
}

.items-section .body .content-row>ul>li:first-child {
    border-top-right-radius: var(--theme-radius);
    border-top-left-radius: var(--theme-radius);
}

.items-section .body .content-row>ul>li:first-child .frame-wrapper>.frame-label {
    border-top-left-radius: var(--theme-radius);
}

.items-section .body .content-row>ul>li:last-child {
    border-bottom-right-radius: var(--theme-radius);
    border-bottom-left-radius: var(--theme-radius);
    margin-bottom: 12px;
}

.items-section .body .content-row>ul>li:last-child .frame-wrapper>.frame-label {
    border-bottom-left-radius: var(--theme-radius);
}

.items-section .body .content-row>ul>li>.frame-wrapper>.frame-label, 
.items-section .body .placeholder-row .field-label {
    background: var(--gray-100);
    border-right: 1px solid var(--gray-300);
}

.items-section .body .hidden-fields {
    margin-bottom: 12px;
    border-radius: 0 0 var(--theme-radius) var(--theme-radius);
}

.items-section .body .hidden-fields--column {
    background: var(--gray-100) repeating-linear-gradient(
    -55deg,
    transparent,
    transparent 10px,
    rgba(0,0,0,.02) 10px,
    rgba(0,0,0,.02) 20px
  	);
    border-top: 1px solid var(--gray-200);
    border-bottom-left-radius: var(--theme-radius);
    
}

.items-section .body .hidden-fields--line span {
    font-size: 1.3rem;
}

.content-row + .placeholder-row {
    display: none !important;
}

.items-section .body .item-content .item-content-body .metadata-row {
    color: var(--gray-600);
    border-radius: var(--theme-radius);
    opacity: .5;
}

.item-activity-wrapper {
    -webkit-flex: 0 0 360px;
    -moz-flex: 0 0 360px;
    -ms-flex: 0 0 360px;
    flex: 0 0 360px;
}

.item-activity-wrapper .item-activity {
    border-radius: var(--theme-radius);
    width: 360px;
}

.items-section .item-activity .activity-header {
    border-top-right-radius: var(--theme-radius);
    border-top-left-radius: var(--theme-radius);
}

.composer-new {
    background: var(--gray-200);;
    border-bottom-right-radius: var(--theme-radius);
    border-bottom-left-radius: var(--theme-radius);
}

.items-section .body .content-row>ul .contact-card {
    border: 2px solid var(--gray-300);
    background: var(--gray-100);
    margin-bottom: 10px;
    border-radius: var(--theme-radius);
    padding: 10px;
}

.items-section .body .content-row>ul .contact-card .details h4 {
    font-size: 1.6rem;
    font-weight: 600;
    color: var(--gray-600);
    margin-bottom: 0;
}

