.ts_cont_wrap {
  position: absolute;
  top: 39px;
  left: 173px;
  background: var(--font_color);
  width: 248px;
  z-index: 800;
  border-radius: 4px;
  box-shadow: 0 1px 4px rgba(0, 0, 0, .2);
  overflow: hidden;
  display: none
