style.css:
@-moz-document regexp("https:\\/\\/.*hattrick.org\\/?([a-z\\-]+\\/)?Club\\/Matches\\/Match.aspx?\\\\?.*") {
body {
    background: url("https://img.fifa.com/image/upload/t_fe-auto/assets/img/tournaments/107/2019/common/footer_bg.png"),linear-gradient(#8A1538, #E9BF7D);
}

.live-matchtime[_ngcontent-c3], .htbox .htbox-header .htbox-header-icon {
    background: #8A1538;
    border-left: 4px solid #E9BF7D;
    font-weight: normal;
}

.htbox .htbox-header :not(.htbox-header-icon) + .htbox-header-right {
    flex-grow: 0;
    background: #8A1538;
    padding: 5px;
}

.htbox .htbox-header, .live-scoreboard-teamname[_ngcontent-c3], .alert, .ht-bar-level[_ngcontent-c17], #menu, #menu .menu-submenu {
    background: linear-gradient(to right, #E9BF7D 0%, #8A1538 100%);
    border: none;
    font-weight: normal;
}

.htbox .htbox-header .live-scoreboard[_ngcontent-c3] {
    flex-grow: 1.5;
    margin-left: -10px;
}

.live-scoreboard-teamname[_ngcontent-c3] {
    padding: 7px 10px;
    border-right: 4px solid #E9BF7D;
    border-left: 4px solid #E9BF7D;
    flex-grow: 1;
    text-align: center;
}

.live-scoreboard-teamname[_ngcontent-c3]:first-of-type {
    background: linear-gradient(to left, #E9BF7D 0%, #8A1538 100%);
}

.live-scoreboard-score[_ngcontent-c3], .match-overlay[_ngcontent-c3] .match-overlay-score[_ngcontent-c3] p {
    background: #FFF;
    color: #000;
    padding: 7px 17px;
    border-left: 4px solid #6ECDEA;
    border-right: 4px solid #D15E5E;
}

#top, #header, .htbox .htbox-content, .htbox-footer, #bottom, .pitch-players-replaced .playerName, .bench[_ngcontent-c3] .playerName[_ngcontent-c3] .fullName[_ngcontent-c3], .commentary[_ngcontent-c3] .commentary-event-content[_ngcontent-c3] {
    background: rgba(0, 0, 0, .5);
    color: #FFF;
    border: none;
    border-radius: 0;
}

.mainFull #mainBody, #content, .live-left-container[_ngcontent-c3], .htbox .htbox-content .htbox-content, .ht-tabs > *, h3, .shy, .main table th, #menu > div, .live-bubble[_ngcontent-c2] {
    background: transparent;
    color: #FFF;
    text-transform: uppercase;
}

.local-link, a.ht-matchevent {
    color: #FFF;
    border-bottom: 1px dotted #FFF;
}

a {
    color: #E9BF7D;
}

a:hover, .ht-tabs > :hover {
    color: #8A1538;
}

.matchMinute[_ngcontent-c2] {
    border-radius: 0;
    background: #F00 !important;
}

.matchMinute[_ngcontent-c2] * {
    background: #E600OF;
    color: #fff;
    border-color: currentColor;
}

.possession[_ngcontent-c3], .playerOrders.bench.bench-away[_ngcontent-c3], .playerOrders.bench.bench-home[_ngcontent-c3], .playerOrders[_ngcontent-c3] table[_ngcontent-c3] td[_ngcontent-c3], .playerOrders[_ngcontent-c3] table[_ngcontent-c3] th[_ngcontent-c3] {
    color: #000;
}

.match-overlay[_ngcontent-c3] .match-overlay-title[_ngcontent-c3] {
    background: #FFF;
    color: #000;
    padding: 7px;
    border-left: 3px solid #E9BF7D;
    border-right: 3px solid #E9BF7D;
    margin: 10px auto;
    width: fit-content;
    font-size: 1.5em;
}

table.htbox-table thead th {
    border-color: currentColor;
}

.match-overlay[_ngcontent-c3] .match-overlay-logos-and-score[_ngcontent-c3] >:not(.match-overlay-score) .match-overlay-image-holder {
    border: 2px solid #E9BF7D;
    background: #8A1538;
    max-width: 90px;
    max-height: 60px;
    overflow: hidden;
}

.match-overlay[_ngcontent-c3] .match-overlay-logos-and-score[_ngcontent-c3] >:not(.match-overlay-score) img {
    transform: rotate(-30deg);
    margin: 35px 0 0 17px;
}

.match-overlay[_ngcontent-c3] .match-overlay-logos-and-score[_ngcontent-c3] > .match-overlay-home[_ngcontent-c3] span[_ngcontent-c3], .match-overlay[_ngcontent-c3] .match-overlay-logos-and-score[_ngcontent-c3] > .match-overlay-away[_ngcontent-c3] span[_ngcontent-c3] {
    padding: 5px;
}

.match-overlay[_ngcontent-c3] .match-overlay-logos-and-score[_ngcontent-c3] > .match-overlay-home[_ngcontent-c3] span[_ngcontent-c3] {
    background: linear-gradient(to left, #E9BF7D 0%, #8A1538 100%);
}

.match-overlay[_ngcontent-c3] .match-overlay-logos-and-score[_ngcontent-c3] > .match-overlay-away[_ngcontent-c3] span[_ngcontent-c3] {
    background: linear-gradient(to right, #E9BF7D 0%, #8A1538 100%);
}

.playerSprite .playerName .lastName, .playerSprite .playerName .fullName {
    color: #000;
    background: #FFF;
    border-left: 2px solid #E9BF7D;
    border-right: 2px solid #E9BF7D;
    font-size: .95em;
}

.ltr[_nghost-c3] .playerTeamDetails[hidden][_ngcontent-c3], .ltr [_nghost-c3] .playerTeamDetails[hidden][_ngcontent-c3] {
    opacity: 0;
    transition: opacity .5s ease, left 1s ease;
}

.ltr[_nghost-c3] .commentary[_ngcontent-c3] .commentary-event-content[_ngcontent-c3]:after, .ltr [_nghost-c3] .commentary[_ngcontent-c3] .commentary-event-content[_ngcontent-c3]:after {
    border: none;
}

.match-clock-added-time[_ngcontent-c1] {
    color: #FFF;
}
}
