style.css:
@-moz-document domain("boards.4channel.org"), domain("boards.4chan.org"), domain("") {
@font-face {
    font-family: 'Noto Sans';
    font-style: normal;
    font-weight: 400;
    unicode-range: U+0102-0103, U+1EA0-1EF9, U+20AB;
}

body {
    margin: 0px;
    background: none;
    background-color: #404552 !important;
    color: #ffffff;
    font-family: 'Noto Sans', monotype;
}

:root.yotsuba-b div.boardTitle, :root.yotsuba div.boardTitle {
    font-family: 'Noto Sans' !important;
    letter-spacing: -1px;
    text-shadow: none;
    color: #a0b2c6;
}

div.pagelist.desktop {
    background-color: #404552;
    border: none;
}

span.boardList {
    display: none;
    color: #6BA4E7;
}


div.boardBanner {
    color: #a0b2c6;
}

a {
    font-size: 16px;
}

span {
    font-size: 16px;
}

.postMessage {
    font-size: 16px;
}

.board {
    background-color: #404552;
}

div.reply {
    background-color: #373D48;
    border: none;
}

ul li {
    background-color: #14141f;
}

div.post.reply.highlight {
    background-color: #1f1f2e !important;
    border: none;
}

div.post div.postInfo span.postNum a {
    color: #6BA4E7;
}

.quoteLink, .quotelink, .deadlink {
    color: #6BA4E7 !important;
    transition: all .3s ease;
}

a:hover {
    color: #6BA4E7 !important;
    transition: all .2s ease;
}

a,
a:visited,
a.replylink:not(:hover),
div#absbot a:not(:hover) {
    color: #ccc !important;
}

div.post div.postInfo span.postNum a:hover,
.posteruid .hand:hover {
    color: #6BA4E7 !important;
}

.burichan_new .backlink a,
.yotsuba_b_new .backlink a {
    color: #ccc !important;
}

.burichan_new .backlink a:hover, .yotsuba_b_new .backlink a:hover {
    color: #6BA4E7 !important;
}

.subject {
    font-weight: bolder !important;
    color: #6BA4E7!important;
}

.post.op .postMessage {
    color: #ccc !important;
}

.fileThumb img:hover {
    transition: all .3s ease;
    transform: scale(1.03);
}

.fileThumb img {
    border-radius: 5px;
    transition: all .2s ease-in-out;
}

.fileThumb.image-expanded-anti img {
    transition: none
}

.fileThumb.image-expanded-anti img:hover {
    transition: none
}

#quote-preview.highlight {
    background-color: #000;
    color: #ccc;
}

#quote-preview.reveal-spoilers {
    background-color: #000;
    color: #ccc;
}

div.post div.postInfo span.nameBlock span.name {
    color: #6BA4E7;
}

.board {
    padding-left: 0px;
}

@media screen and (max-width: 50em) {
    .board {
        padding-left: inherit;
    }
}

.post.op .fileThumb {
    padding: 20px 0 0 0;
}

::-webkit-scrollbar {
    height: 12px;
    width: 4px;
    background: #000;
}

::-webkit-scrollbar-thumb {
    background: #393812;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}

::-webkit-scrollbar-corner {
    background: #000;
}

a.quotelink.forwardlink, a.backlink.forwardlink {
    text-decoration: none;
    font-weight: bold;
}

.postMessage {
    color: #ccc;
}

.post.reply {
    border-radius: 10px;
}

.qphl {
    outline: 2px solid transparent;
    box-shadow: 0 0 10px #6BA4E7;
}

span#board-list {
    background-color: #313742;
}

:root.yotsuba-b #header-bar.dialog, :root.yotsuba #header-bar.dialog {
    background-color: #313742;
    border: none;
}

#custom-board-list {
    color: #6BA4E7;
    font-weight: bold;
}

.current {
    position: relative;
    top: 2px !important;
}

.show-board-list-button {
    position: relative;
    top: 2px;
}

.external {
    position: relative;
    top: 2px;
}

#fourchanx-settings {
    background-color: #313742;
    width: 1200px;
}

#fourchanx-settings.section-main {
    background-color: #404552;
}

#fourchanx-settings div {
    background-color: #404552;
}

#fourchanx-settings label, #fourchanx-settings span.description {
    text-decoration: none;
    font-size: 16px;
}

#fourchanx-settings div.sections-list, #fourchanx-settings div.credits, :root.yotsuba #header-bar.dialog {
    background-color: #313742;
    border: none;
}

form div.persona input.field, div.textarea textarea {
    background-color: #404552;
    border: none;
}

.field, .field:hover, .field:focus, .field.focus {
    color: #ccc;
}

#qr select {
    background: #404552;
    border: none;
}


#qr {
    background-color: #313742;
}


#file-n-submit > input, #qr-draw-button, #qr-filename-container {
    background: #404552;
    border: none;
    height: 100%;
}

.unread-line {
    border-color: #6BA4E7
}

:root.yotsuba-b .qr-link, :root.yotsuba .qr-link {
    background: #313742;
    border: none;
}

:root.yotsuba-b .qr-link:hover, :root.yotsuba .qr-link:hover {
    background: #313742;
}

#index-search {
    background: #444A58;
    border: 1px solid #2B2E39;
}

.inline .post {
    border: none !important;
}

div.post.reply.highlight {
    background: none;
    border: none;
}

:root.yotsuba-b .dialog, :root.yotsuba .dialog {
    background: #313742;
    border: 1px solid #0000;
}

div.pagelist {
    background: #313742;
    border: none;
    font-size: 16px !important;
}

div.pages {
    position: relative;
    top: -3px;
}

div.pagelist div.cataloglink {
    border-left: none;
}

:root.yotsuba-b.highlight-own .yourPost > .reply, :root.yotsuba.highlight-own .yourPost > .reply {
    border: none !important;
}

.reply:target, .reply.highlight {
    background: #313742 !important;
    box-shadow: 0 0 10px #6BA4E7;
}

navLinks navLinksBot desktop {
    position: absolute;
    font-size: 16px;
}

brackets-wrap qr-link-container-bottom qr-link-bottom {
    position: absolute;
    font-size: 16px;
}

:root.yotsuba-b.highlight-you .quotesYou > .reply, :root.yotsuba.highlight-you .quotesYou > .reply {
    border-left: 3px solid #6BA4E7cc !important;
}

.qr-link-container-bottom {
    width: auto;
    position: relative;
    text-align: right;
    float: right;
    left: 0px;
}

body {
    font-size: 16px;
}

div.pagelist div.cataloglink::before {
    content: '[';
}

div.pagelist div.cataloglink::after {
    content: ']';
}

:root.yotsuba-b.catalog-hover-expand .catalog-container:hover > .post, :root.yotsuba.catalog-hover-expand .catalog-container:hover > .post {
    background: #313742 !important;
    border: white;
    border-radius: 5px
}

.catalog-reply {
    border: none;
}

.catalog-thumb {
    border-radius: 5px;
    border: transparent !important;
}



.catalog-link img:hover {
    transition: all .3s ease;
    transform: scale(1.03);
}

.catalog-link img {
    border-radius: 5px;
    transition: all .2s ease-in-out;
}

#thread-watcher {
    border-radius: 5px !important;
}

select {
    color: #fff !important;
}

input {
    color: #fff !important;
}

.section-advanced textarea, .section-advanced input, .section-advanced li, .section-filter textarea, .section-filter input, .section-filter li, .section-sauce textarea, .section-sauce input, .section-sauce li, #sauce-doc {
    background-color: #313742;
}

#fourchanx-settings code {
    background-color: #313742 !important;
    color: #fff !important;
    font-family: 'Noto Mono' !important;
}

#fourchanx-settings button {
    color: #fff !important;
}

.inline .post {
    background-color: #31374299 !important;
    border-radius: 5px !important;
}

.post-reply {
    border-radius: 5px;
}

.postContainer {
    border-radius: 5px;
}

.postInfo {
    border-radius: 5px;
}

a.sauce:before {
    color: #6BA4E7;
    content: '[';
}

a.sauce:after {
    color: #6BA4E7;
    content: ']';
}

span.webm-title:before {
    color: #6BA4E7;
    content: '[';
}

span.webm-title:after {
    color: #6BA4E7;
    content: ']';
}

a.sauce, span.webm-title {
    text-transform: capitalize;
    font-weight: bold;
    text-decoration: none !important;
}

span.webm-title a {
    text-decoration: none;
}

span.webm-title a::before {
    text-decoration: none;
    transition: all 0.2s ease;
}

span.webm-title span {
    transition: all 0.2s ease;
    color: #ccc;
}

span.webm-title span:hover {
    color: #6BA4E7;
}

.subject:after {
    color: #fff;
}

.section-keybinds tr td input {
    border: #404552;
    border-radius: 5px;
    background-color: #313742 !important;
    color: #fff !important;
}

kbd {
    border: #404552 !important;
    box-shadow: 0 1px 0 #aaaa, 0 0 0 2px #404552 inset;
    border-radius: 5px;
    background-color: #313742 !important;
    color: #fff;
    font-size: 16px;
}

:root.yotsuba-b .inline, :root.yotsuba .inline {
    box-shadow: 0 0 10px #343944;
    border-color: #0000;
    border-radius: 5px;
    border-width: 0.13em;
}

#bannerCnt {
    border: 3px solid #343944;
    border-radius: 5px;
}

img {
    border-radius: 4px;
}

:root.yotsuba .dialog {
    background: #313742 !important;
    box-shadow: 0 0 10px #6BA4E7;
    border: none;
    border-radius: 5px;
}

:root.yotsuba .inline {
    background: #313742 !important;
    box-shadow: 0 0 10px #6BA4E7;
    border: none;
    border-radius: 5px;
}

.current {
    position: relative;
    top: 0px;
}

:root.yotsuba #header-bar, :root.yotsuba #notifications {
    color: #6BA4E7;
}

:root.yotsuba .qr-link {
    color: #fff !important;
    border-color: #343944 !important;
    background-color: #383c4a !important;
    background-image: none;
}

:root.yotsuba .qr-link:hover {
    color: #fff !important;
    border-color: #343944 !important;
    background-color: #414a59 !important;
    background-image: none;
}


.aboveMidAd {
    display: none;
}


#index-sort, #index-size, #index-mode {
    background: #343944;
    border: none;
    margin: 0px 5px;
}
}
