style.css:
@-moz-document url-prefix("https://gb.browardschools.com/Pinnacle/Gradebook") {
body{
    height: 100vh
}
body, .menu-list .list-group-item, #Main, .side-bar, #Content, .list-group-item, .list-group-hover .list-group-item:hover, .row.subscription-row{
    color: #FFF;
    background-color: #2C2F33;
    border: none;
}
#Content{
    border-left: 0px;
}
.side-bar .user-info{
    background-color: #436cf2;;
    padding: 31px .5em;
}
#ContentMain{
    background-color: #2C2F33;
    padding: 2em;
}
.heading, div.heading, a.heading{
    background-color:#23272A;
    border: none;
}
div.grid div.teacher{
    color: #99AAB5;
}
a.letter-container:hover{
    background-color: #436cf269;
}
.percent{
    color: #000;
}
#ContentHeader{
    height: 82px;
    background-color: #436cf2;;
    text-align: left;
    border-bottom: 0px;
    padding: 0px;
    padding-left: 15px;
    padding-right: 2em;
}

ul.mtabs {
    top: -12px;
}

.breadcrumb {
    background-color: #436cf2;;
    border-bottom: 0px;
    padding: 4px 15px;
}
#ContentHeader h1{
    font-weight: bold
}
#ContentHeader h2, span, .breadcrumb .active, .breadcrumb>li+li:before, .fa{
    color: #fff;
}

.menu-list section div a:first-child {
    border: 0px;    
}
.h1 .small, .h1 small, .h2 .small, .h2 small, .h3 .small, .h3 small, .h4 .small, .h4 small, .h5 .small, .h5 small, .h6 .small, .h6 small, h1 .small, h1 small, h2 .small, h2 small, h3 .small, h3 small, h4 .small, h4 small, h5 .small, h5 small, h6 .small, h6 small {
    color: #FFF;
}

.menu-list section,.menu-list section:first-child, .menu-list .list-group-item.list-group-item-heading, .side-bar .user-info{
    border: 0px;
}
.menu-list .list-group-item.list-group-item-heading{
    background-color: #3156d1;
    color: #fff
}
#Main {
    border-left: 250px solid #2C2F33;
    padding-top: 0px;
}
.side-bar {
    top: 0px;
    height: 100vh;
}
#header {
    display: none;
}
.menu-list a.list-group-item.list-group-item-heading:hover{
    background: #436cf2d4;
    color: #ffffffa3
}
.menu-list a.list-group-item:hover{
    background: #436cf259;
    color: #fff
}
#ContentHeader a:not(.action-link), #ContentHeader span.period{
    color: #fff;
}
ul.mtabs li{
    border: 0px;
    background-color: #23272A;
}
ul.mtabs li.active{
    border: 0px;
    background-color: #0f0f10;
}
td.icon-date div, td.icon-date time, time.icon-date{
    background-color: #23272A
}
table.grid td.icon-date span.a{
    background-color: #0009;
}
.assignment:hover{
    background-color: #23272a99;
}
#AssignmentDetails th, .heading{
    color: #bfbfbf
}
#ContentHeader h1{
    line-height: 0.75em
}
tbody tr.hover {
    background-color: #436cf269;
}
#ctl00_NavBar_HomeTab {
    color: #000
}

select:not(.form-control) {
    background-color: #23272A !important;
}

table.cal td.n, able.cal td, table.cal td  {
    border: none;
}
table.cal td  {
    background-color: #23272a;

}
table.cal td.n {
  background-color: #436cf2;  
}
#calendarLegend td.label {
    background-color: #2c2f33;
    color: #FFF;
}
#Calendar {
    display: flex;
    flex-wrap: wrap;
    overflow-x: hidden;
    overflow-y: hidden;
    border: none;
}
.footer {
    position: fixed;
    left: 0;
    bottom: 0;
}
.days-check-box + span {
    background-color: #23272a;
}
.days-check-box + span:hover {
  background-color: #436cf2;  

}
   
.toggle-container:hover {
  background-color: #436cf2; 
}

.content-header {
    background-color: #23272a;
    border: none;
}
}

@-moz-document url-prefix("https://fs.browardschools.com") {
body, #content, #contentWrapper{
    height: 100vh;
    color: #FFF;
    background-color: #2C2F33;
    border: 0px
}
input[type="submit"]:hover, span.submit:hover{
    background-color: #436cf2cc;
}
span.submit{
    background-color: #436cf2;
    border:0px
}
.illustrationClass {
	background-image: url(https://i.imgur.com/5apVfSP.png);
}
}
