style.css:
@-moz-document domain(blog.acolyer.org) {
body.custom-background {
	margin: 0 20px;
	background: #f8e9d9;
}
body,

#page {
    width: 90%;
}
#wrapper {
	margin: 0;
	padding: 0;
	width: 100%;
}
#content {
	float: left;
	width: 100%;
	padding: 20px 0;
}
#sidebar {
	float: right;
	width: 20%;
	padding: 0;
}
.entry {
	max-width: 100%;
}
.site {
	color: #222;
	background: #fefcfa;
}
.thin-sidebar {
	width: 100%;
	padding: 0 10px;
}
iframe#twitter-widget-0 {
	width: 100% !important;
	padding: 0 10px;
}
blockquote {
	background: #f2f2f2;
	border-left: 10px solid #888;
	margin: 1em 10px;
	padding: 0.9em 10px;
	quotes: "\201C""\201D""\2018""\2019";
}
blockquote:before {
	color: #888;
	content: open-quote;
	font-size: 4em;
	line-height: 0.1em;
	margin-right: 0.25em;
	vertical-align: -0.4em;
}
blockquote p {
	display: inline;
}
}
