--black:#000;
--white:#fff;
--lgray:#c0c0c0;
--dgray:#222;
--ddgray:#111;
--gray:#666;
--accent:/*[[USERACCENT]]*/;
--iaccent:#000}

/*-webkit-filter: invert(100%);filter: invert(100%)*/ /*THE INVERT CODE*/
    body{color:var(--gray)}
    
/*MAINPAGE*/
.footer .links{color:var(--white)}
#app, #app-layout{background-color:var(--black)!important}
.imgs{-webkit-filter: invert(100%);filter: invert(100%)}
.footer .icons a i{border:1px solid var(--dgray)!important}
hr{border-top: 1px solid var(--dgray)}
#guest-subscription button {background-color: rgba(0, 0, 0, .5);border: 1px solid var(--accent);color: #fff;}
.guest-home-sections .caption ul li a{color:var(--accent)!important}
.intercom-namespace .intercom-17nwwz0{background:var(--black)}
    
/*LIBRARY*/
