    .search-main .results-header span.results-options{color:var(--white)}
    .search-sidebar .category-list a{color: var(--lgray)}
    .search-sidebar h5{color:var(--white)}
    .search-main .asset-container{border:1px solid var(--black)}
    .search-sidebar .normal-checkbox .custom-control-label {color:var(--lgray)}
    .search-sidebar .normal-checkbox .normal-checkbox-after{color:var(--gray)}
    .popup-caption{-webkit-filter: invert(100%);filter: invert(100%);background-color:var(--white)}
    .search-sidebar {border-right: 1px solid var(--dgray);background-color: var(--black);}
    .popup-arrows {-webkit-filter: invert(100%);filter: invert(100%)}
    button.clear-filters {background-color: var(--black);border: 1px solid var(--dgray);color: var(--white);}
    button.clear-filters i{-webkit-text-stroke:0px var(--white);color:var(--lgray)}
    .facet-header{color:var(--white)}
    
/*PRODUCTDETAIL*/
