/*PRICING*/
.page {background: var(--black)}
.plans-tabs{border-bottom: 1px solid var(--dgray);}
.pricing-table .table .seats select{background-image: url(https://cdn.poliigon.com/images/frontend/pricing/caret.svg);-webkit-filter: invert(100%);filter: invert(100%);color:var(--dgray)}
.nav-tabs > li.main-nav-tabs-li > a {color: var(--lgray);}
.pricing-table .pricing-table-row .title{color:var(--lgray)}
.pricing-table .pricing-table-row {border-bottom: 1px solid var(--dgray);}
.pricing-table .table td{color:var(--lgray)}
.pricePlans .recommended{background-color:var(--accent)}
.table-bordered, .table-bordered > tbody > tr > td, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > td, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > thead > tr > th {border: 1px solid var(--dgray);}
.pricing-table .table img{filter:brightness(2)}
.faq h2 {color:var(--white)}
.faq h5 {color:var(--lgray)}
.faq p {color:var(--gray)}
body .btn, body b, body strong {color:var(--lgray)}
.worksInSection.pricing .container{border-bottom:1px solid var(--dgray)}
.feedback{background-color:var(--ddgray)}
.slick-dots li.slick-active button::before{background-color:var(--accent);border:1px solid var(--accent)}
.pricePlans .nav-tabs{border:1px solid var(--accent); background:var(--black)}
.pricePlans .nav-tabs > li.active > a {color: var(--white);background: var(--accent);}
.pricePlans .nav-tabs > li > a{color:var(--accent)}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover{border-color:var(--accent)}
.nav-tabs > li > a:hover {border-color: transparent transparent var(--dgray);}
.nav > li > a:focus, .nav > li > a:hover {background-color: var(--dgray);}
.btn-primary {color: var(--white);background-color:var(--black);border-color:var(--accent);}
.nav-tabs > li.main-nav-tabs-li.active > a, .nav-tabs > li.main-nav-tabs-li.active > a:focus, .nav-tabs > li.main-nav-tabs-li.active > a:hover{color:var(--accent);border-bottom: 4px solid var(--accent);}
.pricePacks .pack-item:hover{border:1px solid var(--accent)}
.pricePacks .pack-item{border:1px solid var(--dgray)}
.pricePacks .icon{-webfilter:brightnes(4);filter:brightness(4)}

/*FAQ*/
body{background-color:var(--black)}
.link__arrow{color:var(--gray)}
.link__arrow a{color:var(--lgray)}
.link__arrow a:hover {color: var(--white);}
.collection__headline, .paper__stack.animation__clicked{background:var(--ddgray)}
.t__h1{color:var(--white)}
.c__darker{color:var(--lgray)}
.collection__photo svg {stroke: var(--white)}
.section__bg::before{background-color:var(--ddgray)}
.g__space{background-color:var(--ddgray)}
.paper__article-preview{border: 1px solid var(--dgray)}
.paper{background-color:var(--ddgray)}
a.paper:hover {background-color: var(--black)}
.footer{background:var(--black)}
    /*REACTIONMENU*/
    .intercom-reaction-picker {color: var(--gray);background-color: var(--black);}
    /*ARTICLES*/
    .h1, h2, h3, h4, h5, h6{color: var(--white)}
    article {color: var(--gray)}
    /*TERMS*/
    .h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {color: var(--white);}
    .tc-page .tc-download .btn.light {color: var(--lgray);background-color: var(--black)}
    
/*REGISTER/LOGIN*/
form .form-control {box-shadow: none; color: var(--lgray); background-color:var(--black); border: 1px solid var(--dgray)!important}
.g-recaptcha{-webkit-filter: invert(100%);filter: invert(100%)}
.checkbox label input[type="checkbox"]:checked + .cr {background: var(--accent)}
.checkbox label{color:var(--lgray)}
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover{background:var(--accent);border:1px solid var(--accent)}
    .form-control{background-color:var(--black)}

/*NAVBAR*/
    /*SEARCH*/.searchNav form .input-group-btn .btn{background-color:var(--black)}
    .searchNav .input-group i.fa-search{-webkit-text-stroke:0px                     var(--accent);color:var(--gray)}
    .dropdown-menu{background-color:var(--black);color:var(--lgray)}
    .btn-default.active.focus, .btn-default.active:focus, .btn-default.active:hover,  .btn-default:active.focus, .btn-default:active:focus, .btn-default:active:hover, .open > .dropdown-toggle.btn-default.focus, .open > .dropdown-toggle.btn-default:focus, .open > .dropdown-toggle.btn-default:hover {color:       var(--gray);background-color:var(--ddgray)}
.navbar-form .input-group > .form-control{background-color:var(--black); border: 1px solid var(--lgray)}
.searchNav form .input-group-btn .btn{border: 1px solid var(--dgray)}
.searchNav form .input-group-btn::before{background-color:transparent}
    /*MAIN*/
    .searchNav {background-color: var(--iaccent)}

/*ERRORMODAL*/
.modal-content{background-color:var(--ddgray)}
.notification-popup h5{color:var(--white)}
.notification-popup .close{color:var(--white)}
.notification-popup .md.md-alert-circle-o {color:var(--accent);}

/*RANDOMACCENTTHINGSIDONTKNOWWHERETOPUT*/
#guest-home-slider .feedback .slick-dots li.slick-active button::before{color:var(--accent)}
.guest-home-sections .caption a.filled-button{background-color:var(--accent)}
a:focus, a:hover{color:var(--accent)}
.article a, .c__primary{color:var(--accent)}
.search-main .results-header span.results-options select{color:var(--accent)}
.search-main .asset-container:hover{border:2px solid var(--accent)}
.back-to-top{color:var(--white);background-color:var(--black)}
.search-pagination li a{color:var(--accent)}
.search-sidebar .refine-by input:checked + label{color:var(--accent)}
.search-main .asset-col .free-div{background:var(--accent)}
.header, .avatar__image-extra{background:var(--black)}
.search__input{background:var(--ddgray)!important}
a{color:var(--white)}

/*BLOG*/
.small-button-style-solid .sqs-block-button .sqs-block-button-element--small, .medium-button-style-solid .sqs-block-button .sqs-block-button-element--medium, .large-button-style-solid .sqs-block-button .sqs-block-button-element--large{border: 2px solid var(--accent);color:var(--accent);background:var(--black)}
#header .main-nav{background-color:var(--ddgray)}
#header .poliigon-header{background-color:var(--black)}
.summary-title a, .summary-title a:link, .summary-title a:visited{color:var(--gray)}
.summary-title a, .summary-title a:link, .summary-title a{color:var(--white)}
.sqs-block-summary-v2 .summary-block-setting-primary-metadata-date .summary-metadata--primary .summary-metadata-item--date, .sqs-block-summary-v2 .summary-block-setting-primary-metadata-event-time .summary-metadata--primary .summary-metadata-item--event-time, .sqs-block-summary-v2 .summary-block-setting-primary-metadata-cats .summary-item-has-cats .summary-metadata--primary .summary-metadata-item--cats, .sqs-block-summary-v2 .summary-block-setting-primary-metadata-tags .summary-item-has-tags .summary-metadata--primary .summary-metadata-item--tags, .sqs-block-summary-v2 .summary-block-setting-primary-metadata-author .summary-item-has-author .summary-metadata--primary .summary-metadata-item--author, .sqs-block-summary-v2 .summary-block-setting-primary-metadata-comments .summary-item-has-comments-enabled .summary-metadata--primary .summary-metadata-item--comments, .sqs-block-summary-v2 .summary-block-setting-primary-metadata-location .summary-item-has-location .summary-metadata--primary .summary-metadata-item--location{color:var(--accent)}
#footer .html-block a, #footer .html-block a:link, #footer .html-block a{color:var(--white)}
#footer .html-block a, #footer .html-block a:link, #footer .html-block a:visited{color:var(--accent)}
a, a:link, a:visited{color:var(--gray)}
h1 a, h1 a:visited{color:var(--gray)}
.sqs-block-content{color:var(--gray)!important}
a:link{color:var(--white)!important}
h1, h2, h3, h4, .comment-count{color:var(--lgray)}
    
/*ACCOUNTSTUFF*/
    .blue{color:var(--accent)}
    .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {background-color:var(--dgray)}
    /*CART*/
        .dashboard .dasboardContent{background-color:var(--black)}
        .dashboard .dasboardContent.cart .empty p{color:var(--lgray)}
        .dashboard .dasboardContent h4{color:var(--lgray)}
        .asset-popup .slick-dots li.slick-active button::before{color:var(--accent);-webkit-text-stroke:var(--accent);background:transparent;border:0px solid}
    .dashboard .sidebar{border-right:1px solid var(--ddgray)}
    .dashboard .dasboardContent.tabs-content, .dashboard .dasboardContent.tabs-content::after{background-color:var(--black)}
    .dashboard .dasboardContent .userInfo h4{color:var(--white)}
    .dashboard .blue{color:var(--accent)}
    .dashboard .sidebar.account{background-color:var(--black)}
    .sidebar .sidebar-nav > li.active > a, .sidebar .sidebar-nav > li > a:hover{color:var(--accent)}
    .sidebar .sidebar-nav > li > a::after{background:var(--accent)}
    .alert-info {color: var(--white);background-color: transparent;border-color:var(--accent)}
    .newModal .pm-footer-row{background-color:var(--ddgray)}
    #editBillingAddress .modal-dialog .modal-content{background:var(--black)}
    .newRegiser .reg-2nd-step .country_input{color:var(--gray);background-color:var(--black)}}
