@-moz-document url-prefix("https://aternos.org/start/") {
/*Main*/
@keyframes shake {
  10%, 90% {
    transform: translate3d(-1px, 0, 0);
  }
  
  20%, 80% {
    transform: translate3d(2px, 0, 0);
  }

  30%, 50%, 70% {
    transform: translate3d(-4px, 0, 0);
  }

  40%, 60% {
    transform: translate3d(4px, 0, 0);
  }
}

::-webkit-scrollbar {
	display: none !important;
}

body {
	background-image: url(https://www.pixelstalk.net/wp-content/uploads/images3/Photos_Download_3D_Images_Download.jpg) !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
}

.ad {
	opacity: 0.8 !important;
}

/*Alert*/
.alert main{
	background: rgba(1,1,1,0.8) !important;
    color: #05CB04 !important;
}

/*Header*/
.header {
	background: rgba(0,0,0,0.5) !important;
}

header .logo > img {
display: none;
}

header .logo {
	content: url(https://i.imgur.com/kWaevbZ.png);
}

.social * {
	color: #05CB04 !important;
}

.social *:hover {
	color: #038507 !important;
    animation: shake 2s infinite !important;
}

.header .btn-blue {
	background: rgba(45,135,211,0.5) !important;
}

.header .btn-blue:hover {
	background: rgba(45,135,211,0.8) !important;
}

.statusinfo {
	color: #05CB04 !important;
    font-family: 'Audiowide', cursive !important;
}

.header .btn-white {
	background: rgba(240,240,240,0.8) !important;
}

.header .btn-white:hover {
	background: rgba(240,240,240,1) !important;
}

.username * {
	color: #05CB04 !important;
    font-family: 'Fjalla One', sans-serif !important;
}

.user .fas {
	color: #05CB04 !important;
}

.user .fas:hover {
	color: #038507 !important;
}

.userlinks * {
	color: #038507 !important;
}

.usertext .fas {
	color: #038507 !important;
    border: none !important;
}

.friend-access-dropdown {
	background: rgba(1,1,1,1) !important;
    border: none !important;
}

.friend-access-switch {
	border-bottom: 1px solid #05CB04 !Important;
}

.friend-access-switch-ip {
	color: #05CB04 !important;
}

.friend-access-switch-user {
	color: #038507 !important;
}

/*Sidebar*/
.sidebar {
	background: rgba(0,0,0,0.5) !important;
}

.navigation .item {
	border: none !important;
}

.navigation .item:hover {
    background: rgba(5, 203, 4, 0.2) !important;
}

.navigation .navigation-item-label {
    text-transform: uppercase !important;
}

.navigation a:nth-child(odd) {
	color: #05CB04 !important;
}

.navigation a:nth-child(even) {
	color: white !important;
}

.navigation .fas, .navigation .far, .navigation .fab {
	font-weight: 10 !important;
}

.navigation a:after {
	color: #05CB04 !important;
}

.navigation-toggle .far {
	color: #05CB04 !important;
    font-weight: 10 !important;
}

/*Menu*/
.navigation {
	background: rgba(0,0,0,0.5) !important;
}

/*Content*/
.content {
	background: rgba(0,0,0,0.5) !important;
}

.content h1 {
	color: #05CB04 !important;
}

.content h2 {
	color: #038507 !important;
}

.start-activity {
	background: rgba(45,135,211,0.5) !important;
    color: #05CB04 !important;
}

.start-activity .fas, .start-activity .far, .start-activity .fab {
	font-weight: 10 !important;
}

.start-activity:hover {
	background: rgba(45,135,211,0.8) !important;
}

.start-activity-help .start-activity {
	background: rgba(31,215,141,0.5) !important;
}

.start-activity-help .start-activity:hover {
	background: rgba(31,215,141,0.8) !important;
}

.tutorial {
	border: 2px solid #05CB04 !important;
    background: rgba(1,1,1,0.5) !important;
}

.tutorial:hover {
    background: rgba(5, 203, 4, 0.2) !important;
}

.tutorial-darker {
	color: #05CB04 !important;
}

.tutorial-text {
	color: #038507 !important;
}

