style.css:
@-moz-document domain("zrzutka.pl") {
html,body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #E64164 #1c1c1c;
}
/*HEADER*/
body.navbar-sticky nav.navbar-home{
    background: #1c1c1c;
}
.navbar-light .navbar-nav .nav-link{
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.navbar-brand{
    background: linear-gradient(90deg,#e83e3e,#e83e62);
    border-radius: 4px;
}
.page-home,.single-chipin,.card,.home-app-promo .background{
    background: #1c1c1c;
}
.home-chips-featured header h1,.single-chipin h5 a,.home-chips-browser header h1,.nav-tabs .nav-link,.card.card-blog h5,.card.card-blog p a,.home-chips-browser header p a{
    color: #fff;
}
.single-chipin h5 a:hover{
    color: #E64164;
}
.progress-with-amounts h4.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active,.progress-with-amounts h4,.home-app-promo .container h1{
    color: #E64164;
}
/*STATYSTYKI*/
.home-stats,.home-how{
    background: #1c1c1c;
}
.home-stats header h1,.home-stats .col-stat span,.home-how header h1{
    color: #fff;
}
/*STATYSTYKI*/
/*DLACZEGO-ZRZUTKA*/
.home-whyus{
    background: #1c1c1c;
}
.home-whyus header h1,.home-whyus .benefit p{
    color: #fff;
}
/*DLACZEGO-ZRZUTKA*/
/*STRONA-GŁÓWNA*/
/*ZRZUTKA*/
.chip-header h1,#chip-stat-progress h4,.section-chip header .chip-stat h4,.bar-chip-summary h6,.bar-chip-summary h4.chip-funds{
    color: #E64164;
}
.chip-header .chip-organiser a span,.section-chip header .chip-stat h4 small,.text-muted,.chip-header h1 small, .chip-header h4 small,.section-chip header ul.chip-actions li a,button.text-link,.card-chipin .card-header,.user-box-name,.progress.progress-thin .progress-bar .progress-label{
    color: #fff !important;
}
.badge-info{
    background-color: #0b96dd !important;
}
.progress,.dropdown-menu.dropdown-menu-right.show,.banner,.banner.banner-light{
    background: #1c1c1c;
    border: 1px solid #fff;
}
.progress .progress-bar,.section-chip header{
    box-shadow: none;
}
.user-avatar{
    border-radius: 0;
}
.section-chip .card-chip-header,.card-chipin .card-header,.bar{
    background: #1c1c1c;
}
/*ZRZUTKA*/
/*PODSTRONY*/
/*KATALOG*/
.bg-white{
    background: #1c1c1c !important;
}
h1.site-header{
    color: #E64164;
}
.search-results .form-filters--side h6,.form-check-label{
    color: #fff;
}
[type="checkbox"] + label::before, [type="radio"] + label::before{
    background: #1c1c1c;
}
.input-group.input-group-blend .btn{
    background: #1c1c1c;
    border: 1px solid #fff;
}
.input-group.input-group-blend .btn .icon_search::before{
    filter: invert(100%);
}
.form-control,.form-control:focus{
    background: #1c1c1c;
    color: #fff;
}
/*KATALOG*/
/*JAK-TO-DZIAŁA*/
.section-howitworks .sidebar a,.card-body,.section-howitworks .card h2{
    color: #fff;
}
.card{
    box-shadow: none;
    border: 1px solid #fff;
}
/*JAK-TO-DZIAŁA*/
/*LOGIN*/
.modal-content{
    background: #1c1c1c;
}
.modal .form-header h3{
    color: #E64164;
}
p.smaller{
    color: #fff;
}
/*LOGIN*/
/*BLOG*/
article.blog-post header h1{
    color: #E64164;
}
article.blog-post .blog-post-content ol, article.blog-post .blog-post-content p, article.blog-post .blog-post-content ul,.card-comments{
    color: #fff;
}
/*BLOG*/
/*PODSTRONY*/
/*NEWSLETTER*/
#popupNewsletter .modal-body--form{
    background: #1c1c1c;
}
#popupNewsletter h1{
    color: #E64164;
}
#popupNewsletter p{
    color: #fff;
}
.form-inline .form-control{
    background: #1c1c1c;
    color: #fff;
}
/*NEWSLETTER*/
/*POMOC*/
.help-answer-link{
    color: #E64164;
}
.help-answer-link:hover{
    color: #fff;
}
/*POMOC*/
/*REGULAMIN*/
.sidebar .nav-tabs li a{
    color: #E64164;
}
/*REGULAMIN*/
/*ZA-DARMO*/
.table,table.table thead th{
    color: #fff;
}
/*ZA-DARMO*/
/*DLA-MEDIÓW*/
section.static-media ul li a{
    background: #fff;
    border-color: #E64164;
}
section.static-media ul li a:hover{
    background: linear-gradient(90deg,#e83e3e,#e83e62);
}
/*DLA-MEDIÓW*/
/*FOOTER*/
.col-copyright p:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #E64164;
}
.dropdown-item:focus, .dropdown-item:hover{
    background: linear-gradient(90deg,#e83e3e,#e83e62);
}
.dropdown-item{
    color: #fff;
}
/*FOOTER*/
}
