style.css:
@-moz-document url-prefix("https://induste.com") {
/* En cas de bugs ou d'un oublie, contactez-moi ici https://induste.com/conversations/add?to=Romain+IAS */

/* Code couleur HEX noir profond : #1a1a1a */

/* Twitter : @romainlekps */
 html {
	color: #a6a6a6;
	background-color: #1a1a1a;
}
.p-body {
	background: #1a1a1a;
}
.block-body .block-row, .message-cell.message-cell--main {
	background: #1a1a1a;
}
.u-dt[title], .pairs.pairs--justified>dd {
	color: #7883c0;
}
.contentRow-minor {
	color: #a9a9a9;
}
.pairs>dt {
	padding: 0;
	margin: 0;
	color: #9b9b9b;
}
.username--style4 {
	color: #3850d7;
	font-weight: bold;
}
.block--messages .block-row {
	color: #c9c9c9;
	background: #1a1a1a;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
	position: relative;
	background: #1b2027;
}
.message-signature {
	color: #cfcfcf;
	border-top: 1px solid rgb(191, 191, 191);
}
a {
	color: #7883c0;
	text-decoration: none;
}
.block-footer {
	background: #1b2027;
}
.block-container {
	background: #1a1a1a;
	color: #cdcdcd;
	border: 1px solid #fff;
}
.block-body--shoutbox {
	background: #1a1a1a;
}
.p-title-value {
	color: #727ec0;
}
.block.block--category .block-header {
	background: #1b2027;
	border-bottom: 1px solid #fff;
}
.p-body-main.p-body-main--withSidebar .p-body-content.is-active .p-body-pageContent .block .block-container {
	background: #1a1a1a!important;
}
.node-body {
	background: #1a1a1a;
	border: 1px solid #fff;
}
.node-title {
	color: #abbee1;
}
.node-subNodeMenu .menuTrigger {
	color: #ddd;
}
.menu-row.menu-row--alt {
	color: #e1e1e1;
	background: #1b2027;
}
.menu-content {
	color: #d3d3d3;
	background: #1a1a1a;
}
.menu-linkRow {
	color: #c6ced1;
}
.p-breadcrumbs--container, .p-breadcrumbs.p-breadcrumbs--bottom {
	color: #626ca3;
	background: #1b2027;
}
.input {
	border: 1px solid #ffffff54;
	color: #b5b5b5 !important;
}
.p-body-sidebar .block-row {
	padding: 15px 20px;
	border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.memberOverviewBlock-seeMore {
	background: #1b2027;
	border: 1px solid rgba(255, 255, 255, 0.1);
}
.formRow>dt {
	border-right: 1px solid transparent;
	background: #1a1a1a;
	border-color: rgba(255, 255, 255, 0.1);
}
.formRow .formRow-explain {
	color: #bebebe;
}
.block-tabHeader {
	color: #cbccd1;
	background: #1b2027;
	border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.input::placeholder {
	color: #d1d1d1;
}
.p-description {
	color: #ccc;
}
.menu-row.menu-row--separated+.menu-row {
	border-top: 1px solid rgba(255, 255, 255, 0.1);
}
.menu-row.menu-row--clickable:hover {
	background: #1a1a1a;
}
.porta-article-item .porta-article-date .porta-date-block {
	background: #1a1a1a;
	border: 1px solid rgba(255, 255, 255, 0.4);
}
.message-attribution {
	color: #cdcdcd;
	font-size: 12px;
	padding-bottom: 3px;
	border-bottom: 1px solid rgba(15, 15, 15, .1);
}
.gridCard--container {
	height: 100%;
	background: #1b2027;
	box-shadow: 1px 2px 2px 0 rgba(255, 255, 255, 0.25);
}
.u-muted {
	color: #d2d2d2;
}
.groupViewNav {
	border-left: 4px solid #FFC107;
	background: #1a1a1b;
}
.groupViewNav--item:hover a, .groupViewNav--item.is-active a {
	background: #1a1a1b;
	color: #a19570;
}
.block-footer {
	padding: 6px 10px;
	font-size: 12px;
	color: #d0d0d0;
	background: #1a1a1a;
	border-top: 1px solid rgba(255, 255, 255, .1);
}
.shareButtons-button {
	color: #cecece;
}
.inputGroup.inputGroup--joined .inputGroup-text {
	color: #d1d1d1;
	background: rgba(255, 254, 254, 0.1);
}
.contentRow-extra.contentRow-extra--largest {
	font-size: 24px;
	color: #89addb;
}
.block-row.block-row--separated+.block-row {
	border-top: 1px solid rgba(255, 247, 247, 0.1);
}
.menu-linkRow {
	color: #c6ced1;
	border-top: 1px solid rgb(255, 255, 255, 0.1);
}
.structItem {
	border-top: 1px solid rgba(255, 255, 255, 0.1);
}
.tabs--standalone {
	border: 1px solid rgba(255, 255, 255, 0.1);
}
.tabs--standalone {
	border: 1px solid rgba(255, 255, 255, 0.1);
	background: #1b2027;
	font-weight: bold;
	color: #cecece;
}
.tabs--standalone .tabs-tab.is-active {
	color: #1a6e96;
	border-color: #1a6e96;
}
.overlay-content .block-container {
	color: #e5e5e5;
	border-radius: 0;
}
.block-header .block-desc {
	display: inline-block;
	color: #9ea7d8;
}
.memberHeader-main {
	background: #1b2027;
	border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.block-tabHeader .tabs-tab.is-active {
	border-color: #b51213 !important;
	background: #b96161;
}
.structItem-minor {
	font-size: 12px;
	color: #c2c7f1d1;
}
.memberTooltip-header {
	background: #1b2027;
	border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.tooltip--bookmark .tooltip-content, .tooltip--share .tooltip-content {
	color: #ccc;
	background: #fefefe;
	padding: 0;
	text-align: left;
	border: 1px solid rgba(255, 255, 255, .1);
	box-shadow: 0 5px 10px 0 rgba(216, 204, 204, 0.35);
}
.tooltip--member .tooltip-content {
	background: #1a1a1a;
}
.banniereModerateurs {
	background-color: #3850d7 !important;
	border: 1px solid rgb(255, 255, 255, .1);
}
.message-responseRow {
	background: #1a1a1a;
	border: 1px solid rgba(255, 255, 255, 0.1);
	color: #d1d1d1;
}
a.actionBar-action, .message-responseRow a, .likeIcon:before {
	color: rgb(77, 123, 169);
}
.fr-toolbar {
	background: #252628;
	padding: 0 1px;
	border: 1px solid rgba(255, 255, 255, .1);
	border-top: 3px solid #FFC107;
}
.fr-toolbar .fr-command.fr-btn, .fr-popup .fr-command.fr-btn {
	background: transparent;
	color: #d1c4c4;
}
.fr-toolbar .fr-command.fr-btn.fr-dropdown::after, .fr-popup .fr-command.fr-btn.fr-dropdown::after {
	position: absolute;
	width: 0;
	height: 0;
	border-left: 4px solid transparent;
	border-right: 4px solid transparent;
	border-top: 4px solid #a8c6dd;
}
.fr-command.fr-btn+.fr-dropdown-menu .fr-dropdown-wrapper {
	background: #1a1a1a;
	border: 1px solid rgba(255, 255, 255, 0.1);
}
.fr-box.fr-basic .fr-element {
	color: #d1d1d1;
}
.fr-element {
	background: #1a1a1af2;
	border: 1px solid rgb(255, 255, 255, .1);
}
.fr-element::placeholder {
	color: #d1d1d1;
}
.fr-toolbar .fr-command.fr-btn.fr-dropdown.fr-active, .fr-popup .fr-command.fr-btn.fr-dropdown.fr-active {
	background: rgba(237, 228, 228, 0.12);
}
::-webkit-scrollbar-track {
	background: #1a1a1a;
}
.block--messages .message {
	color: #d2d0d0;
}
.shareButtons-label {
	color: #79b0dc;
}
.username--style2 {
	color: #008fd0;
}
.tooltip--member .tooltip-content, .tooltip--share .tooltip-content {
	color: #adcdef;
}
.subNodeMenu .subNodeLink {
	color: #849cae;
}
.p-sectionLinks {
	background: #795e1e;
}
.button, a.button {
	background: #795e1e;
}
  .fr-toolbar {
    color: #cdcdcd;
  }
.contentRow-extra.contentRow-extra--large {
    font-size: 17px;
    color: #678eb7;
}
}
