style.css:
@-moz-document domain("www.drehscheibe-online.de") {
/************************************************************************************
***************** DREHSCHEIBE-ONLINE DARK THEME 2020 (C) koef3 v1.0.1 ***************
************************************************************************************/
body {
    margin: 5px;
    background-color: #404040;
    color: #d9d9d9;
    font: 13px Verdana, "Trebuchet MS", Arial, Helvetica, sans-serif;
}
/* STARTSEITE */
/* Benachrichtigung falls Forum über direkten Link aufgerufen wurde färben */
.notify {
    border: 1px solid #020202;
    background-color: #971c1c;
}
/* Kategorie-Überschriften (Darstellung, Foren usw.) färben */
#dsBody h2 {
    background-color: #1E1E1E;
    color: #FFFFFF;
}
/* Schrit auf Startseite färben */
#dsBody a.passiv {
    color: #c6c6c6;
}
/*Footer Startseite*/
#dsBody h4 a {
    color: #ff0004;
}
/* Header färben! */
#dsHeaderColoured {
    background-color: #5E5E5E;
    border: 2px solid #1C1C1C;
}
/* Navigationsbereich Magazin/Gemeinschaft Info */
#dsNavi ul#dsChannel {
    background-color: #bb0606;
}
#dsNavi ul#dsChannel {
    clear: both;
    padding: 0 0 0 2%;
    background: transparent;
    background-color: rgba(0, 0, 0, 0);
    float: left;
    min-width: 750px;
    width: 98%;
    height: 30px;
}
#dsNavi ul#dsChannel a.dsActive {
    background-color: #971C1C;
    /* Für entsprechend ausgewählten Bereich! */
}
/* Navigationsbereich Foren/Galerie/Treff */
#dsNavi ul#dsChannel ul {
    background: #971C1C;
    color: #fdfdfd;
}
#dsNavi ul#dsChannel li li a.dsActive {
    color: #ffcb00;
    background-color: #971c1c;
}
#dsNavi ul#dsChannel li li a:hover {
    /* Wenn gehovert wird */
    color: #FFF !important;
    text-decoration: underline;
}
/* Unterforen Überschriften */
h2 {
    font-size: 18px;
    background-color: #404040;
    color: #FF0;
}
/* Links für Moderatoren, Seitenzahlen in Threads, "begonnen von" umfärben */
a {
    color: #ff6f00;
}
/* Leiste für Navigation färben */
div.goto {
    border: 1px solid #484848;
    background: #000;
    font-size: 11px;
    margin-top: 13px;
    margin-bottom: 10px;
}
/* Forenübersicht */
table#forumindex tbody tr.even td {
    background: #222;
}
table#forumindex tbody tr.odd td {
    background: #373737;
}
/* Abstände so gut es geht anpassen */
table#forumindex h3 {
    font-size: 13px;
    font-weight: bolder;
    margin-top: 7px;
}
/* Dropdown-Menü */
#dsNavi ul.dsNaviLevel2 li a:hover {
    background-color: #6C6C6C;
    color: #FF0000 !important;
}
#dsNavi ul.dsNaviLevel2 li {
    display: block;
    float: none;
    background-color: #9F9F9F;
    padding: 0 !important;
    min-width: 175px;
}
/*Aktuelle Seite und "angemeldet: xxxxxxxx" färben */
div.pages {
    border: 1px solid #404040;
    background-color: #000;
    color: #b7b7b7;
}
div.pages p {
    padding-top: 4px;
    padding-bottom: 4px;
    margin: 0;
}
/* Überschriften-Leiste ("Thema" / "angeklickt"usw.) färben" */
table#forumlist th,
table#forumindex th {
    background-color: #971C1C;
    color: #eaeaea;
}
table#forumlist,
table#forumindex {
    border-color: #2d2d2d;
}
/* Unterforen Liste Threads! */
/* 1. zeile */
table#forumlist tbody tr.even td {
    background: #000;
    color: #b9b9b9;
    padding-top: 3px;
    padding-bottom: 3px;
}
/* andere Zeile */
table#forumlist tbody tr.odd td {
    background: #2d2d2d;
    color: #b9b9b9;
    padding-top: 3px;
    padding-bottom: 3px;
}
/* Borders zwischen den einzelnen Zeilen anpassen! */
table#forumlist tbody td {
    border-bottom: 1px solid #4a4a4a;
}
/* Link wurde noch nicht angeklickt! */
a.subjectlink {
    color: #d9d9d9;
}
/* Link wurde bereits angeklickt! */
a.subjectlink:visited {
    color: #ff9a2f;
}
a:visited {
    color: #aa801b;
}
/* THREADS!!!! */
/* Live-Sichtungen färben */
#dsBody .dsToggle1 {
    background-color: #151515;
}
#dsBody .dsToggle2 {
    background-color: #515151;
}
#dsBody a {
    color: #FF8F00;
    text-decoration: none;
}
#dsBody .dsTabelleKopf td {
    vertical-align: middle;
    background-color: #090909;
}
#dsBody .dsTabelleKopf {
    font-weight: bold;
    color: #FFFFFF;
    background-color: #090909;
}
#dsBody .dsTabelle {
    background-color: #000;
}
/* Gesamter Message-Block inkl. Header und Footer */
div.message {
    border: 1px solid #555;
}
/* Header der einzelnen Posts (Username, geschrieben am) anpassen */
div.message div.head h4 {
    margin: 0px;
    font-size: 14px;
    font-weight: bold;
    padding-bottom: 3px
}
div.message div.head p {
    font-size: 11px;
    padding-top: 3px;
    padding-bottom: 0px;
    margin-bottom: 2px;
}
div.message div.head h3 {
    background-color: #1c1c1c;
}
div.message div.head {
    background-color: #1c1c1c;
    border: 1px solid #373737;
}
/* Nachrichten-Bereich (Post) */
div.message div.body {
    background: #282828;
    margin: 0;
    padding: 11px 15px;
    font-size: 13px;
}
/* Striche links vom Quote */
blockquote.bbcode {
    font-size: 13px;
    margin-left: 5px;
    padding: 10px 20px;
    border-left: 5px solid #000;
}
/* Footer vom Post (Privatnachricht usw.) */
div.message div.options {
    border-top: 1px solid #424242;
    background-color: #1c1c1c;
    color: #848484;
    font-size: 10px;
}
div.message div.options h5 {
    font-size: 10px;
}
div.message div.options li {
    border-left: 1px solid #424242;
}
/* Privatnachrichten! */
.PhorumStdBlock {
    font-size: 12px;
    background-color: #535353;
    border: 1px solid #2d2d2d;
}
div.phorum-menu {
    font-size: 12px;
    background-color: #1a1a1a;
    border: 1px solid #4a4a4a;
    padding: 3px;
}
.phorum-gaugeprefix {
    border: none;
    background-color: white;
    padding-right: 10px;
    color: black;
}
.phorum-gauge {
    border: 1px solid #828282;
    background-color: #0d0d0d;
}
/* einstellungen Forum */
div#cc-content th {
    padding: 2px 5px;
    background: #242424;
    width: 150px;
    text-align: left;
    vertical-align: top;
}
/* Signatur-Box färben */
div[title="Signatur"] {
    background-color: #404040 !important;
    border-color: #535353 !important;
    margin-top: 20px;
}
/* INFO */
#dsBody table {
    border-spacing: 1px;
    width: 100%;
    background-color: #333;
}
}
