.jp-FileBrowser {
    display: flex;
    flex-direction: column;
    color: var(--jp-ui-font-color1);
    background: var(--jp-layout-color1) !important;
    font-size: var(--jp-ui-font-size1);
}
.jp-DirListing-content {
    flex: 1 1 auto;
    margin: 0;
    padding: 0;
    list-style-type: none;
    overflow: auto;
    background: var(--jp-layout-color1);
}
.jp-RunningSessions {
    display: flex;
    flex-direction: column;
    min-width: var(--jp-sidebar-min-width);
    color: var(--jp-ui-font-color1);
    background: var(--jp-layout-color1);
    font-size: var(--jp-ui-font-size1);
}
.p-CommandPalette {
    color: var(--jp-ui-font-color1);
    font-size: var(--jp-ui-font-size1);
    background: var(--jp-layout-color1);
}
.p-CommandPalette-item:hover:not(.p-mod-active):not(.p-mod-disabled) {
    background: var(--jp-layout-color2);
}
#tab-manager {
    background: var(--jp-layout-color1);
    overflow: visible;
    color: var(--jp-ui-font-color1);
    font-size: var(--jp-ui-font-size1);
}
#tab-manager .p-TabBar-tab:hover {
    background: var(--jp-layout-color0);
}
.jp-extensionmanager-view {
    padding-bottom: 0px;
    color: var(--jp-ui-font-color1);
    background: var(--jp-layout-color1);
    display: flex;
    flex-direction: column;
    font-size: var(--jp-ui-font-size1);
}

/* REPLACE "SHUT DOWN" w/ "KILL" IN LIST OF RUNNING NBs */
.jp-RunningSessions-itemShutdown.jp-mod-styled:before {
    margin: 0px;
    color: var(--jp-warn-color1);
    font-weight: 700;
    background-color: transparent;
    height: var(--jp-private-running-shutdown-button-height);
    line-height: var(--jp-private-running-shutdown-button-height);
    transition: background-color 0.1s ease;
    border-radius: 2px;
    content: 'X';
    visibility: visible;
    display: block;
    width: 4em;
    padding: 0px;
    max-width: unset;
}

.jp-RunningSessions-itemShutdown.jp-mod-styled {
    margin: 0px 4px;
    color: transparent;
    font-weight: 500;
    height: var(--jp-private-running-shutdown-button-height);
    line-height: var(--jp-private-running-shutdown-button-height);
    transition: background-color 0.1s ease;
    border-radius: 2px;
    width: 50px;
}

/* REPLACE "SHUT DOWN" w/ "KILL" IN LIST OF RUNNING NBs */
/*.jp-RunningSessions-itemShutdown.jp-mod-styled:after {
    visibility: hidden;
}*/

.jp-RunningSessions-itemShutdown.jp-mod-styled:hover {
    background-color: var(--jp-layout-color3);
	background: var(--jp-layout-color3);
}

button.jp-mod-styled {
    font-size: var(--jp-ui-font-size1);
    color: var(--jp-ui-font-color0);
    border: none;
    box-sizing: border-box;
    text-align: center;
    line-height: 32px;
    height: 32px;
    padding: 0px 0px;
    letter-spacing: 0.8px;
    outline: none;
    appearance: none;
    display: grid;
    width: unset;
    background-color: transparent;
    width: 4em;
    -webkit-appearance: none;
    -moz-appearance: none;
    overflow: clip;
}
button.jp-Button.bp3-button.bp3-minimal:hover {
    background-color: var(--jp-layout-color3);
}
/* ########################################################*/
/*                         LAUNCHER                        */
/* ########################################################*/
.jp-LauncherCard {
    display: flex;
    flex-direction: column;
    cursor: pointer;
    width: 30ex !important;
    height: var(--jp-private-launcher-card-size);
    margin: 5px;
    padding: 0px;
    border: 2px solid var(--jp-layout-color2);
    background: var(--jp-layout-color1);
    box-shadow: none;
    transition: 0.2s;
    border-radius: var(--jp-border-radius);
}

.jp-LauncherCard:hover {
    box-shadow: none;
    background: var(--jp-layout-color2);
}

/*######################################################*/
/*                   HIDE TOOLBAR                       */
/*######################################################*/

.jp-Toolbar > .jp-Toolbar-item {
    flex: 0 0 auto;
    display: flex;
    padding-left: 1px;
    padding-right: 1px;
    font-size: var(--jp-ui-font-size1);
    line-height: var(--jp-private-toolbar-height);
    height: 100%;
    visibility: visible;
}

/*######################################################*/
/*     ADD EXCEPTION FOR KERNEL STATUS &  ICONS         */
/*######################################################*/

div.lm-Widget.p-Widget.jp-Toolbar.jp-scrollbar-tiny.jp-NotebookPanel-toolbar > div.lm-Widget.p-Widget.jp-KernelName.jp-Toolbar-item {
	visibility: visible !important;
}

.jp-Toolbar-item.jp-Toolbar-kernelStatus {
    display: inline-block;
    width: 32px;
    background-repeat: no-repeat;
    background-position: center;
    background-size: 16px;
    visibility: visible !important;
    padding-top: 4px;
}

.jp-Notebook {
    padding-top: 0px;
}
.jp-NotebookPanel.jp-Document {
    top: 3px !important;
}

/* ########################################################*/
/*                       CODE MIRROR                       */
/* ########################################################*/

.jp-Notebook div.CodeMirror .CodeMirror pre {
    font-family: var(--jp-code-font-family-default);
    font-variant-ligatures: normal;
    line-height: 170% !important;
	font-weight: 500;
}

.jp-Notebook div.CodeMirror, div.CodeMirror pre {
 	font-family: var(--jp-code-font-family-default);
	line-height: 170%;
	color: var(--jp-cell-inprompt-font-color) !important;
	font-weight: 500;
}

.jp-Notebook .CodeMirror pre {
  font-family: var(--jp-code-font-family-default) !important;
  font-variant-ligatures: normal !important;
  line-height: 170% !important;
  font-weight: 500;
}

.jp-Notebook .jp-InputArea-editor {
    border: 2px solid var(--jp-cell-editor-border-color-inactive);
    border-radius: 0px;
    background: var(--jp-cell-editor-background);
    opacity: 1;
}
.jp-Notebook .jp-Cell:not(.jp-mod-active) .jp-InputArea-editor {
    border: 2px solid var(--jp-cell-editor-border-color-inactive);
    border-radius: 0px;
    background: var(--jp-cell-editor-background);
    opacity: 1;
}

.jp-Notebook.jp-mod-editMode .jp-Cell.jp-mod-active .jp-InputArea-editor {
    border: 2px solid var(--jp-cell-editor-border-color);
    box-shadow: none;
    background-color: var(--jp-cell-editor-active-background);
}

.jp-MarkdownOutput.jp-RenderedHTMLCommon {
    overflow: visible;
    background: var(--jp-md-cell-background);
}

.jp-Notebook.jp-mod-commandMode .jp-Cell.jp-mod-selected {
	background: transparent !important;
}
.jp-Notebook .jp-Cell:not(.jp-mod-active) .CodeMirror-matchingbracket {
	color: var(--jp-mirror-editor-variable-color);
    font-weight: 500;
    background-color: transparent !important;
}
.jp-Notebook .jp-Cell:not(.jp-mod-active) .CodeMirror-nonmatchingbracket {
	color: var(--jp-mirror-editor-variable-color);
    font-weight: 500;
    background-color: transparent !important;
}
.jp-Notebook .jp-Cell.jp-mod-active.jp-mod-selected  .CodeMirror-matchingbracket {
	color: var(--jp-matching-bracket-color) !important;
    font-weight: bold;
	background-color: var(--jp-matching-bracket-bg);
}
.jp-Notebook .jp-Cell.jp-mod-active.jp-mod-selected  .CodeMirror-nonmatchingbracket {
	color: var(--jp-matching-bracket-color) !important;
    font-weight: bold;
	background-color: var(--jp-nonmatching-bracket-bg);
}
.CodeMirror-cursor {
    border-left: 2px solid #0095ff !important;
}
.cm-s-jupyter span.cm-keyword {
    color: var(--jp-mirror-editor-keyword-color);
    font-weight: 500;
}
.cm-s-jupyter span.cm-def {
    color: var(--jp-mirror-editor-def-color);
    font-weight: bold;
    font-style: italic;
}
.jp-Cell-inputArea, .jp-Cell-outputArea {
    flex: 1 1 auto;
    border-top-right-radius: 6px;
    border-bottom-right-radius: 6px;
}


