style.css:
@-moz-document domain("mjakmama24.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #F90469 #1c1c1c;
}
/*HEADER*/
header .top_menu,header .bottom_menu,header .submenu.active::before{
    background: #1c1c1c;
}
header .logo img{
    background: #fff;
    border-radius: 15px;
    border: 3px solid #F90469;
}
.menu-box{
    background: #1c1c1c;
    border: 3px solid #F90469;
}
.menu-box li a{
    color: #fff;
}
.menu-box li a:hover{
    background: #1c1c1c;
}
header .submenu-box .gl_wrapper{
    background: #1c1c1c;
    border: 3px solid #F90469;
    border-top: 0;
    margin: -11px 0px 0px 0px;
}
header .show .submenu-box .gl_wrapper::before{
    display: none;
}
header .submenu ul li a{
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.element{
    background: #1c1c1c;
    box-shadow: none !important;
}
.element .element__content a,.element__headline a,.calc__descText, .calc__result,.calc__formText,.calc__input--select option,.paginacja li a, .paginacja li.separator{
    color: #fff !important;
}
.element .element__content::before{
    box-shadow: none !important;
}
.v23 .element:nth-child(2){
    background: #1c1c1c;
}
.calc{
    background: #1c1c1c;
    border: 3px solid #F90469;
}
.calc__headlineText,.main-title-listing,.element__headline a:hover{
    color: #F90469 !important;
}
.vdatetime-input,.calc__input--select select{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #F90469;
}
.zpr_inside_2, .zpr_inside_3, .zpr_side_2_box, .zpr_side_3_box, .zpr_side_4_box, .zpr_m_inside_2, .zpr_m_inside_3, .zpr_m_inside_4, .zpr_m_bottom_1,.zpr_box_bottom1 .zpr_bottom_1,.zpr_box_half_page .zpr_hp, .zpr_box_side_bottom .zpr_rect, .zpr_box_half_page2 .zpr_hp2, .zpr_box_half_page3 .zpr_hp3,.element__extras .extras__component--spon p,.zpr_inside_1_wide.active, .zpr_inside_2_wide.active, .zpr_inside_3_wide.active, .zpr_side_1_noscroll{
    display: none;
}
.paginacja li.disable a,.paginacja li:first-child a{
    margin: 2px 1px 0px -1px;
}
.paginacja li.next a{
    margin: 2px -1px 0px 1px;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
article,article h1,article .lead, .article h2,.rating__question .rating__questionText,.highlight__title .hightlight__text{
    color: #F90469;
}
article > p,.rating .rating__value,.article li,.recommendedArticle__title p,.highlight__textbox p{
    color: #fff;
}
.rating__scale .rating__scaleCol--up .rating__img::after{
    color: green;
}
.rating__scale .rating__img::after{
    color: red;
}
.rating__message{
    background: #F90469;
}
.recommendedArticle{
    background: #1c1c1c;
    border: 3px solid #F90469;
    box-shadow: none;
}
.breadcrumb__link{
    background: #1c1c1c;
    border: 1px solid #F90469;
    color: #fff;
}
.breadcrumb__link:hover{
    background: #F90469;
    border: 1px solid #fff;
}
/*QIUZ*/
.quiz:not(.item){
    box-shadow: none;
}
#ajax_quiz{
    background: #1c1c1c;
}
.quiz h2,.quiz .questions > li > span{
    background: #1c1c1c;
    border: 3px solid #F90469;
    letter-spacing: 1px;
    font-weight: normal;
}
.quiz p,.quiz div{
    color: #fff
}
.quiz span{
    color: #F90469;
}
.quiz .text-btn > span{
    letter-spacing: 1px;
}


/*QIUZ*/






/*KOMENTARZE*/
.comments__button .button{
    border: 1px solid #F90469;
}
.comments__button .button:hover{
    background: #1c1c1c;
    border: 1px solid #F90469;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
.listingItem__textLead{
    color: #fff !important;
}
/*KALKULATORY*/
.blockinfo__text h1, .blockinfo__text h2,.blockinfo strong{
    color: #F90469;
}
.blockinfo__text p.description__alternative,.blockinfo__text p,.highlight__textbox strong{
    color: #fff;
}
#app .container{
    background: #1c1c1c;
    border: 3px solid #F90469;
    color: #fff;
}
.container h2{
    color: #F90469;
}
label.col-xs-4.control-label{
    color: #fff;
}
.form-horizontal select, .form-horizontal input,.input__number{
    background-color: #1c1c1c;
    color: #fff;
    border: 1px solid #F90469;
}
.vdatetime-popup,#mcCalculator.container{
    background: #1c1c1c;
}
.vdatetime-calendar__month__day:hover > span > span{
    background: #F90469;
}
.vdatetime-calendar__month__day,.vdatetime-month-picker__item--disabled,.vdatetime-month-picker__item--disabled:hover{
    color: #fff;
}
.vdatetime-calendar__current--month,.vdatetime-calendar__month__weekday{
    color: #F90469;
}
.vdatetime-calendar__navigation--next svg path, .vdatetime-calendar__navigation--previous svg path{
    stroke: #fff;
}
.gl_plugin.listing{
    background: #1c1c1c;
}
.v27::before{
    background: #1c1c1c;
    box-shadow: none;
}
/*KALKULATORY*/
/*MIESIECZNIK*/
.header--mobile header .mobile-menu{
    background: #1c1c1c;
}
.header--mobile header .site-logo img{
    background: #fff;
    border-radius: 15px;
    border: 3px solid #F90469;
}
.header--mobile header .mobile-box .mobile-ham,.header--mobile header .menu-content.show .menu-mobile,.header--mobile .gl_plugin.main-menu, .header--mobile header .submenu-box, .header--mobile header .menu-content.show .menu-mobile > .display-flex,.header--mobile header .menu-content.show .submenu-box .submenu.opened .submenu-title,section{
    background: #1c1c1c;
}
.header--mobile header .submenu-title a,.header--mobile header .submenu ul li a,.menu-long .fa-caret-down::before,.edition__main span{
    color: #F90469;
}
.header--mobile header .mobile-box{
    box-shadow: none;
}
.magazineCover__text strong,.magazineCover__text p,.magazineCover__text li,.listing-horizontal-full-v25 .listing-lead p,.edition__side p{
    color: #fff;
}
.edition__side{
    border-color: #F90469;
}
/*FOOTER*/
.footer::before{
    background: none;
}
footer ul li a,footer .show-list{
    color: #fff;
}
footer .list b{
    color: #F90469;
}
/*FOOTER*/
/*MIESIECZNIK*/
/*NEWSLETTER*/
.big_foto__content{
    background: transparent;
}
.big_foto h1{
    color: #F90469;
}
.big_foto__content .container{
    background: transparent;
    border: none;
}
#newsletterEloqua .container{
    border: none;
}
.slider .bx-wrapper{
    background: #1c1c1c;
}
.slider .bx-wrapper .bx-prev,.slider .bx-wrapper .bx-next{
    filter: invert(100%);
}
.slider__elements--box{
    background: #1c1c1c;
    box-shadow: none;
}
.slider__elements--box:hover span{
    color: #fff;
}
/*NEWSLETTER*/










/*PODSTRONY*/
/*FOOTER*/
footer,footer .copyright{
    background: #1c1c1c;
}
footer .legal-info .h5,footer a,footer .copyright .copyright__text,footer .serwis-list a{
    color: #fff;
}
footer .copyright .copyright__text:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #F90469;
}
footer .h3,footer .serwis-list a:hover{
    color: #F90469;
}
footer .show-list{
    background: #1c1c1c;
    border: 3px solid #F90469;
}
footer .list{
    background: #1c1c1c;
    border: 3px solid #F90469;
    border-top: 0;
    margin: -11px 0 0 0;
}
footer .list::before{
    display: none;
}
/*FOOTER*/
}
