style.css:
@-moz-document domain("music.yandex.ru") {
:root {
	--color-transition-duration: 10s;
}

.centerblock-wrapper {
	position: absolute;
	z-index: 1000;
	width: 100vw;
	top: 65px;
	left: 0;
}

.head {
	position: absolute;
	z-index: 1001;
	width: 100vw;
	left: 0;
}

.bar {
	position: fixed;
	z-index: 1001;
	max-width: 100vw;
	left: 0;
}

.popup_fixed {
	z-index: 1003;
}

.progress__progress_muted .progress__line {
	background: url("data:image/gif;base64,") !important;
}

.progress__progress .progress__line {
       background: url("data:image/png;base64,") repeat-x !important; 
	background: linear-gradient(to bottom, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
	background: -webkit-linear-gradient(top, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
	background: -moz-linear-gradient(top, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
}

.playlist-notification > span {
	animation: ColorTransition var(--color-transition-duration) linear infinite;	
}

.deco-link:hover, .typo-nav:hover, .deco-link-hover-target:hover .deco-link, .deco-link_marked, .theme-black .deco-link-wrapper:hover .deco-link {
	animation: ColorTransition var(--color-transition-duration) linear infinite;
	animation-delay: -1s;
}

.typo-nav.current, .d-slider-vert__drag:active {
	animation: BorderColorTransition var(--color-transition-duration) linear infinite;
	animation-delay: -5s;
}

.album_selectable.album:hover:before, .playlist_selectable.playlist:hover:before, .d-track_selectable.d-track_selected:after, .d-track_selectable:hover:after {
	border: 2px solid;
	animation: BorderColorTransition var(--color-transition-duration) linear infinite;
	animation-delay: -5s;
}

.button_action.button, .d-slider-vert__filled, .deco-button-action .deco-button-stylable {
    animation: BTransition var(--color-transition-duration) linear infinite;
	animation-delay: -5s;
}

.deco-button-action .deco-button-stylable {
	color: hsla(0,0%,100%,.85) !important;
}

.progress__line__branding > .moving-slider, .paused-slider {
	visibility: hidden;
	height: 0;
	width: 0;
	margin: 0;
	padding: 0;
}

.progress__line__branding > .progress__head {
	display: block;
	float: right;
	background: url("data:image/gif;base64,") !important;
	width: 34px !important;
	height: 21px !important;
	border: none !important;
	margin-right: -20px !important;
	margin-top: -16px !important;
}

body .d-icon_heart-full {
	background-image: none !important;
	-webkit-mask: url('https://music.yandex.ru/i/D1u8Bdj1UVuu_9ajW69oi1hRTKU.svg') no-repeat;
	mask: url('https://music.yandex.ru/i/D1u8Bdj1UVuu_9ajW69oi1hRTKU.svg') no-repeat;
	margin: 6px 6px 6px 6px !important;
	animation: BgTransition var(--color-transition-duration) linear infinite;
	animation-delay: -5s;
}

.icon-theme-black .head__user-button:hover .d-icon_heart {
	background-image: none !important;
	-webkit-mask: url('https://music.yandex.ru/i/_zY_njX-CLgEydFRvD0s-K2VJx0.svg') no-repeat;
	mask: url('https://music.yandex.ru/i/_zY_njX-CLgEydFRvD0s-K2VJx0.svg') no-repeat;
	animation: BgTransition var(--color-transition-duration) linear infinite;
	animation-delay: -1s;
}

.d-icon_shuffle-gold {
	background-image: none !important;
	-webkit-mask: url('https://music.yandex.ru/i/0qNIEryCo_A1b30-HqdFbmA0lHQ.svg') no-repeat;
	mask: url('https://music.yandex.ru/i/0qNIEryCo_A1b30-HqdFbmA0lHQ.svg') no-repeat;
	animation: BgTransition var(--color-transition-duration) linear infinite;
	animation-delay: -1s;
}

.d-icon_repeat-gold {
	background-image: none !important;
	-webkit-mask: url('https://music.yandex.ru/i/prb64dfo9ZQJDGo5qR07edfbtPE.svg') no-repeat;
	mask: url('https://music.yandex.ru/i/prb64dfo9ZQJDGo5qR07edfbtPE.svg') no-repeat;
	animation: BgTransition var(--color-transition-duration) linear infinite;
	animation-delay: -1s;
}

.d-icon_repeat-one-gold {
	background-image: none !important;
	-webkit-mask: url('https://music.yandex.ru/i/JCpGQNTbiE5yO49ik9inhms1z6M.svg') no-repeat;
	mask: url('https://music.yandex.ru/i/JCpGQNTbiE5yO49ik9inhms1z6M.svg') no-repeat;
	animation: BgTransition var(--color-transition-duration) linear infinite;
	animation-delay: -1s;
}

.d-icon_play-small {
	background-image: none !important;
	-webkit-mask: url('https://music.yandex.ru/i/TyMldxG7JTqTkaqgZVR6k9vV-D4.svg') no-repeat;
	mask: url('https://music.yandex.ru/i/TyMldxG7JTqTkaqgZVR6k9vV-D4.svg') no-repeat;
	background-color: hsla(0,0%,100%,.85);
}

.bar-below_plus, .bar-below {
	visibility: hidden;
	height: 0;
	width: 0;
	margin: 0;
	padding: 0;
}

.theme-black .deco-progress .progress__progress, .theme-black .deco-progress .progress__progress_muted .progress__line, body .local-theme-black.deco-progress .progress__progress, body .local-theme-black .deco-progress .progress__progress, body .local-theme-black.deco-progress .progress__progress_muted .progress__line, body .local-theme-black .deco-progress .progress__progress_muted .progress__line {
    background-color: hsla(0,0%,100%,0);
}

@keyframes BTransition { 
	0% { background: #f00 }
	5% { background: #f00 }
	15% { background: #f80 }
	25% { background: #ff0 }
	35% { background: #8f0 }
	45% { background: #0f8 }
	55% { background: #08f }
	65% { background: #00f }
	75% { background: #80f }
	85% { background: #f08 }
	95% { background: #f00 }
	100% { background: #f00 }
}

@keyframes BgTransition { 
	0% { background-color: #f00 }
	5% { background-color: #f00 }
	15% { background-color: #f80 }
	25% { background-color: #ff0 }
	35% { background-color: #8f0 }
	45% { background-color: #0f8 }
	55% { background-color: #08f }
	65% { background-color: #00f }
	75% { background-color: #80f }
	85% { background-color: #f08 }
	95% { background-color: #f00 }
	100% { background-color: #f00 }
}

/* KeyFrames for text and currentColor */
@keyframes ColorTransition { 
	0% { color: #f00 }
	5% { color: #f00 }
	15% { color: #f80 }
	25% { color: #ff0 }
	35% { color: #8f0 }
	45% { color: #0f8 }
	55% { color: #08f }
	65% { color: #00f }
	75% { color: #80f }
	85% { color: #f08 }
	95% { color: #f00 }
	100% { color: #f00 }
}

@keyframes BorderColorTransition { 
	0% { border-color: #f00 }
	5% { border-color: #f00 }
	15% { border-color: #f80 }
	25% { border-color: #ff0 }
	35% { border-color: #8f0 }
	45% { border-color: #0f8 }
	55% { border-color: #08f }
	65% { border-color: #00f }
	75% { border-color: #80f }
	85% { border-color: #f08 }
	95% { border-color: #f00 }
	100% { border-color: #f00 }
}

}
