style.css:
@-moz-document domain("circleci.com")
{
    div.inner
    {
        background-color: #333;
    }
  
  .toggle-sorting, 
  .toggle-all-branches label, 
  .metadata-item.recent-time.duration,
  .metadata-item.recent-time.start-time{
    color: white !important;
  }
  
  .css-1r4tnvh, .css-a2yhqb, .map-container g a, .map-container g div{
    background: rgba(255, 255, 255, .05) !important;
	color: white !important;
  }
  
  .map-container g div.css-4xipnm, 
  .map-container g div.css-kkdnr9, 
  .map-container g div.css-106acdv, 
  .map-container g div.css-mec4ly {
    background: #3d3d3d !important;
  }
  
  .jobs-header .hr-title span{
    background: #333333 !important;
    color: white;
  }
  
  .aside-activity .branches>li:hover{
        background-color: rgba(255, 255, 255, 0.18) !important;
  }
  
  .inner .toggle-all-builds label{
     color: white !important;
  }

    [data-component="frontend.components.pieces.page-header/crumb-node"] > a:not(:hover)
    {
        color: #999;
    }

    [data-component="frontend.components.pieces.page-header/crumb-node"] > a:hover
    {
        color: #fff;
    }

    .aside-left-nav a.aside-item.current
    {
        background-color: #666;
        color: #999;
        border-right: 1px solid #999;
    }
  
  .breadcrumbs li:before {
    color: white !important;
  }

    .aside-left-menu .aside-activity header .toggle-all-branches input[type=radio]:checked+label
    {
        background-color: rgba(255, 255, 255, .25);
        color: #333;
    }

    .inner .toggle-all-builds label.checked
    {
        background-color: rgba(255, 255, 255, .25);
        color: #333;
    }

    [data-component="frontend.components.pieces.status/badge"]>.badge-label
    {
        color: #444;
    }

    [data-component="frontend.components.pieces.page-header/header"]
    {
        border-bottom: solid 1px #999;
    }

    .aside-left-menu .aside-activity header .toggle-sorting
    {
        padding-left: 10px;
        background-color: rgba(255, 255, 255, .25);
        color: #333;
    }

    .aside-left-menu .aside-activity header
    {
        border-bottom: solid 1px #999;
    }

    .aside-activity .project-heading
    {
        background-color: #333;
        color: #999;
    }

    .aside-activity .project-heading>.project-name
    {
        color: #999;
    }

    .aside-activity:hover .project-heading:hover
    {
        background-color: #eaeaea;
    }

    .aside-activity .projects>li
    {
        border-top: solid 1px #666;
    }

    .inner .build
    {
        background-color: #333;
    }

    .inner .build-info .recent-commit-msg
    {
        color: #eee;
    }

    .inner .build-info .contextual-identifier a
    {
        /* color: #333; */
        color: #eee;
    }

    .inner .build-engine .platform
    {
        color: aqua;
    }

    .inner .build-engine .platform
    {
        color: aqua;
    }

    .inner .dashboard a
    {
        color: aqua;
    }

    [data-component="frontend.components.pieces.card/full-bleed"]
    {
        background: #555;
        box-shadow: 0 1px 3px 0 rgba(255, 255, 255, 0.25);
    }

    [data-component="frontend.components.pages.build.head/build-head-content"] [data-element="frontend.components.pages.build.head/build-head-content/commits"]
    {
        color: #999;
    }

    [data-component="frontend.components.pieces.tabs/tab-row"]>li
    {
        display: block;
        flex-grow: 1;
        color: #999;
        background: #333;
        border-right: 1px solid #999;
    }

    [data-component="frontend.components.pieces.tabs/tab-row"]>li.active
    {
        background: #555;
        font-weight: 500;
    }

    .card
    {
        background: #555;
        box-shadow: 0 1px 3px 0 rgba(255, 255, 255, 0.25);
    }

    .inner .build-output .ah_wrapper .command
    {
        color: #999;
        height: inherit;
        overflow: hidden;
        line-height: 3em;
    }

    .inner .action_header .command-text
    {
        color: #999;
    }

    .inner .type-divider span
    {
        background: #555;
        color: #999;
    }

    [data-component="frontend.components.pieces.tabs/tab-row"]>li:hover
    {
        background: #777;
        font-weight: 500;
    }

    .inner .build-output .ah_wrapper
    {
        border: 1px solid #999;
        box-shadow: 0 0px #fff inset;
    }

    .dropdown-menu
    {
        background-color: #666;
        border: 1px solid #999;
    }

    [data-component="frontend.components.pieces.topbar/topbar"]>.navs-container>.nav-options>.org-dropdown>.dropdown-menu>.switch-org-text
    {
        font-size: 12px;
        line-height: 20px;
        font-weight: bold;
        text-transform: uppercase;
        color: #333;
        border-bottom: 1px solid #555;
        text-indent: 15px;
        padding: 5px 0;
    }

    [data-component="frontend.components.pieces.topbar/topbar"]>.navs-container>.nav-options>.org-dropdown>.dropdown-menu>li>.org-dropdown-menu__item
    {
        border-bottom: 1px solid #555;
    }

    .aside-user
    {
        background: #333;
        color: #999;
    }

    a.aside-item.active
    {
        background: #999;
        color: #333;
    }

    .branch .branch-info .branch-name{
        color: #eee !important;
    }

    .aside-left-menu{
        border-right: 1px solid #999999;
    }

    .main-display{
        background-color: #ffffff45;
    }

    a.aside-item.active
    {
        background: #999;
        color: #333;
    }
}
