@-moz-document url-prefix("https://aternos.org/start/") {
/*Main*/
@keyframes gradient {
    to {
      background-position: 200% center;
    }
  }

body {
	background: url(https://monodomo.com/free-wallpapers/purple-galaxy-wallpapers-high-resolution-For-Free-Wallpaper.jpg) !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
}

::-webkit-scrollbar {
	display: none !important;
}

/*Top Bar*/
.header {
	background: rgba(0,0,0,0.5) !important;
}

header .logo > img {
display: none;
}

header .logo {
	content: url(https://i.imgur.com/JwgxC3E.png);
}

.social .fab {
    color: #33a9ef;
}

.social .fab:hover {
    color: #0f80c3;
}

.statusinfo {
	background: linear-gradient(to right, #d428f0 20%, #33a9ef 40%, #33a9ef 60%, #d428f0 80%);
  background-size: 200% auto;
  color: #33a9ef;
  background-clip: text;
  text-fill-color: transparent;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient 2.5s linear infinite;
    font-weight: bold !important;
}

.username {
	background: linear-gradient(to right, #d428f0 20%, #33a9ef 40%, #33a9ef 60%, #d428f0 80%);
  background-size: 200% auto;
  color: #33a9ef;
  background-clip: text;
  text-fill-color: transparent;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient 2.5s linear infinite;
}

.userlinks a, .userlinks span {
	color: #33a9ef !important;
}

.userlinks a:hover, .userlinks span:hover {
	color: #0f80c3 !important;
}

.usericon > a > i {
  color: #33a9ef;
}

.usericon > a > i:hover {
  color: #0f80c3;
}

.friend-access-dropdown {
    background: rgba(0,0,0,0.8) !important;
    border: none !important;
}

.friend-access-switch {
	border-bottom: 0.1px solid #33a9ef !important;
}

.friend-access-switch-ip {
	background: linear-gradient(to right, #d428f0 20%, #33a9ef 40%, #33a9ef 60%, #d428f0 80%);
  background-size: 200% auto;
  color: #33a9ef;
  background-clip: text;
  text-fill-color: transparent;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient 2.5s linear infinite;
}

.friend-access-by {
	color: #d428f0 !important;
}

/*Sidebar*/
.sidebar {
	background: rgba(0,0,0,0.5) !important;
}

/*Menu*/
.navigation {
	background: rgba(0,0,0,0.5) !important;
}

.navigation .item {
	border: none !important;
    font-weight: bold !important;
    -webkit-border-top-right-radius: 10px !important;
    -webkit-border-bottom-right-radius: 10px !important;
}

.navigation .item:nth-child(odd) {
	color: rgba(51,169,239,0.8) !important;
}

.navigation .item:nth-child(even) {
	color: rgba(212,40,240,0.8) !important;
}

.navigation .item:nth-child(odd):hover {
	color: #33a9ef !important;
}

.navigation .item:nth-child(even):hover {
	color: #d428f0 !important;
}

.navigation .item:hover {
	background: linear-gradient(to right, rgba(212,40,240,0.2) 20%, rgba(51,169,239,0.2) 40%, rgba(51,169,239,0.2) 60%, rgba(212,40,240,0.2) 80%);
  background-size: 200% auto;
  color: #33a9ef;
  animation: gradient 2.5s linear infinite;
}

span.navigation-log-problems-badge {
    background: #d428f0 !important;
    color: white !important;
}

.navigation .item i {
	font-weight: 10 !important;
}

.item.active {
	background: linear-gradient(to right, rgba(212,40,240,0.2) 20%, rgba(51,169,239,0.2) 40%, rgba(51,169,239,0.2) 60%, rgba(212,40,240,0.2) 80%);
  background-size: 200% auto;
  color: #33a9ef;
  animation: gradient 2.5s linear infinite;
}

.navigation-toggle {
	color: #d428f0 !important;
}

/*Content*/
.content {
	background: rgba(0,0,0,0.5) !important;
}

.content h1 {
  color: #33a9ef !important;
    margin: auto !important;
    text-align: center !important;
}

.content h2 {
    background: linear-gradient(to right, #d428f0 20%, #33a9ef 40%, #33a9ef 60%, #d428f0 80%);
  background-size: 200% auto;
  color: #33a9ef;
  background-clip: text;
  text-fill-color: transparent;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient 2.5s linear infinite;
}

.tutorial {
	border: none !important;
    background: rgba(0,0,0,0.5) !important;
    border: 1px solid rgba(212,40,240,0.5) !important;
}

.tutorial:hover {
	background: linear-gradient(to right, rgba(212,40,240,0.2) 20%, rgba(51,169,239,0.2) 40%, rgba(51,169,239,0.2) 60%, rgba(212,40,240,0.2) 80%) !important;
    background-size: 200% auto !important;
    animation: gradient 2.5s linear infinite !important;
}

.tutorial-text {
	color: #d428f0 !important;
}

.tutorial-text .tutorial-darker {
	color: #33a9ef !important;
}

