style.css:
@-moz-document domain("finobe.com"), domain("fi.nobelium.xyz"), domain("www.finobe.com") {
body {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif!important;
    background: white;
}
.navbar-light.bg-faded {
    background: url(http://www.roblox.com/images/RevisedHeader/bg-rbx_header.png) repeat-x;
    height: 36px;
}
.navbar-light .navbar-nav .nav-link {
    color: white;
    font-weight: bold;
    font-family: "Arial";
    font-size:10pt
}
.navbar-light .navbar-nav .nav-link strong {
    font-weight: unset;
    font-family: Arial;
    font-size:10pt
}
.navbar-light .navbar-nav .nav-link:hover {
    background: url(http://www.roblox.com/images/RevisedHeader/bg-main_menu_hover.png) repeat-x;
    color: white;
}
.navbar-brand {
    background: url(http://www.roblox.com/images/RevisedHeader/btn-rbx_logo.png);
    width: 116px;
    height: 35px;
    margin-right: 4px;
    color: transparent!important;
}
.navbar-brand:hover {
    background-position: bottom;
}
.navbar-brandimg {
    display: none!important;
}
.nav-scroller {
    padding-left: 110px;
}
.nav-scroller {
    background: #191919!important;
    border: none;
    height: 25px!important;
    text-align: center!important;
}
.nav-scroller-inner>a.nav-link {
    font-size: 14px;
    font-weight: 400;
    margin-top: -9px;
    color: white;
}
.navbar-expand-lg>.container {
    width: 1000px;
}
.navbar-light .navbar-nav .show>.nav-link {
    background-color: #efefef!important;
    border-top: 1px solid #777;
    border-left: 1px solid #777;
    border-right: 1px solid #777;
    color: black!important;
}
.navbar-nav .dropdown-menu {
    border: 1px solid #777;
    border-top: none;
    background-color: #efefef!important;
    border-radius: 0px;
    top: 92%;
    padding: 0px;
}
.dropdown-item:hover {
    border-radius: 0px;
    background-color: #d8d8d8;
}
.dropdown-item {
    margin-bottom: 0px!important;
}
.game-card {
    border-radius: 0px;
    transition: none;
    box-shadow: none;
    width: 200px;
    height: 205px!important;
    margin-left: 13px;
    background: transparent;
    overflow: hidden;
    border: 1px solid transparent;
}
.game-card img {
    width: 200px!important;
    height: 90px!important;
    border-radius: 0px;
}
.game-card:hover {
    border: 1px solid #777;
    box-shadow: none;
    background: white;
}
.btn, .page-link {
    transition: none;
    border: 1px solid #777;
    color: #666666;
    font-weight: bolder;
    border-radius: 0px!important;
    background-image: linear-gradient(#FEFEFE, #DEDEDE);
    text-transform: none;
}
.btn:hover, .page-link:hover {
    background-image: linear-gradient(#FEFEFE, #C7D5FF);
    border: 1px solid #777;
    box-shadow: none;
    color: #777777;
}
h3 {
    color: #343434;
    letter-spacing: -1px;
    font-size: 32px;
    font-weight: bold!important;
}
.btn-success {
    border-color: #007001;
    background-color: #007001;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    color: white!important;
}
.btn-success:hover {
    border-color: #007001;
    background-color: #007001;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    color: white!important;
    background-position: left -128px;
}
.card {
    border: none;
}
.catalog-card>.card {
    border-radius: 0px;
    transition: none;
    border: 1px solid transparent;
}
.catalog-card>.card:hover {
    border: 1px solid #ccc;
    box-shadow: none!important;
}
.footer>.container {
    background: #123f83;
    font-size: 13px;
    color: White;
    margin: 0;
    width: 100%;
    padding: 2em;
    border: none;
}
.footer .container {
    max-width: 100000px;
}
.nav-link .badge-danger {
    color: Black;
    border: 1px solid Black;
    background-color: White;
}
.catalog-no-overflow-plz a {
    color: #0055B3;
}
.page-item.active .page-link, .page-item.disabled .page-link, .btn-primary.disabled {
    border: 1px solid #777;
    color: #777;
    background: #efefef;
}
a {
    color: #0055B3;
}
.btn-danger {
    border-color: #565656;
    background-color: #565656;
    background-image: url(https://static.rbxcdn.com/images/Buttons/StyleGuide/bg-btn-gray.png);
    color: white;
}
.btn-danger:hover {
    border-color: #565656;
    background-color: #565656;
    background-image: url(https://static.rbxcdn.com/images/Buttons/StyleGuide/bg-btn-gray.png);
    color: white!important;
    background-position: left -128px;
}
.btn-primary {
    border-color: #0852b7!important;
    color: white;
    background-color: #0852b7;
    border: 1px solid;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-blue.png);
}
.btn-primary:hover {
    border-color: #0852b7!important;
    color: white;
    background-color: #0852b7;
    border: 1px solid;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-blue.png);
    background-position: left -128px;
}
.home-main-content {
    background: url(https://imagizer.imageshack.com/img923/9208/qyFcnu.png) no-repeat 50%;
    background-size: cover;
    margin: 0;
    padding: 0;
    height: 300px;
    color: hsla(0,0%,100%,.92);
    display: flex;
    margin-bottom: .5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.2;
    color: black;

}
.h1, h1 {
    font-size: 2.1875rem;
    font-weight: bold;
}
.alert-info {
    background: #FDA702;
    color: #fff;
    border: 1px solid gray;
    font-size: 16px;
    font-weight: bold;
}
.alert-skinny {
    padding: .1rem!important;
        width: 908px;
    margin: 0 auto 5px;
}
.fa-plus:before {
    content: "";
}
.fa-user:before {
    content: "";
}
.fa-exchange-alt:before {
    content: "";
}
.fa-wrench:before {
    content: "";
}
.fa-user-plus:before {
    content: "";
}
.fa-sign-out:before {
    content: "";
}
.nav-link {
    display: block;
    padding: .5rem 0rem;
}
.navbar-nobelium-dropdown.dropdown-menu.dropdown-menu-right>a {
    padding-left: 0;
}
.alert {
    background: #FDA702;
    color: #fff;
    border: 1px solid gray;
    font-size: 16px;
    font-weight: bold;
        padding: .1rem!important;
        width: 908px;
    margin: 0 auto 5px;
}
.home-text {
    text-shadow: 0px 0px 0px #292929;
}
.font-weight-light {
    font-weight: bold!important;
}
.nav-pills .nav-link.active, .nav-pills .show>.nav-link {
    color: #f00;
    background-color: rgba(255, 255, 255, 0.1);
}
img[title="Diu"] {
    content: url("https://vignette.wikia.nocookie.net/roblox/images/6/62/TixROBLOX.png/revision/latest/scale-to-width-down/120?cb=20180908163400")!important;
    width: 16px;
    height: 16px;
}
.form-control {
    display: block;
    width: 100%;
    height: calc(2.0625rem + 2px);
    padding: .375rem .75rem;
    font-size: .875rem;
    font-weight: 400;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid #ced4da;
    border-radius: .0rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}
.badge-primary {
    color: #000000;
    background-color: rgba(255, 255, 255, 0.1);
    border: 1px solid #777;
}
.badge-danger {
    color: #000000;
    background-color: #ffffff;
    border: 1px solid #777;
}
.badge {
    border-radius: .0rem;
}
.fa-play {
    content: "";
    width: 0em;
}
.fa-play:before {
    content: "";
}
.badge.card__ribbon-left {
    position: absolute;
    left: -1px;
    top: 0;
    border: 1px solid #777;
    border-radius: 0;
    border-top-left-radius: 0px;
    border-bottom-right-radius: 0px;
    font-size: 12px;
}
}

@-moz-document url-prefix("https://finobe.com/catalog"), url-prefix("https://www.finobe.com/catalog"), url-prefix("https://nobelium.xyz/catalog") {
.fa-search:before {
    content: "Search";
    color: #000;
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    font-weight: bold;
    text-transform: none;
}
.form-control {
    border: 1px solid #a7a7a7;
    border-radius: 0px;
    margin-right: 10px;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}
.header {
    background: url(http://web.archive.org//web/20131216182636im_/http://www.roblox.com/images/UI/catalog/btn-browse_categories.png);
    width: 136px!important;
    height: 34px;
    color: transparent!important;
}
.finobe-basic-nav {
    width: 137px!important;
    border-left: 1px solid #777;
    border-bottom: 1px solid #777;
    border-right: 1px solid #777;
    background: #efefef;
}
.finobe-basic-nav>.link {
    color: black!important;
    padding: 7px 6px;
    margin-top: 0px!important;
    margin-bottom: 0px!important;
}
.finobe-basic-nav>.link:hover {
    background: #d8d8d8;
    text-decoration: none;
}
.catalog-card-name {
    color: #0055B3;
    font-weight: bold;
}
.catalog-card-name:hover {
    text-decoration: underline;
}
.badge.finobe__card-ribbon {
    right: unset;
    border-top-right-radius: 0px;
    border-bottom-left-radius: 0px;
    margin-top: 96px;
    padding: 0px;
    background-color: transparent;
    margin-left: 14px;
}
.badge.badge-warning.finobe__card-ribbon {
    content: url(http://images.rbxcdn.com/d649b9c54a08dcfa76131d123e7d8acc.png);
}
.badge.badge-success.finobe__card-ribbon {
    content: url(http://images.rbxcdn.com/793dc1fd7562307165231ca2b960b19a.png);
}
}

@-moz-document url-prefix("https://finobe.com/item/"), url-prefix("https://nobelium.xyz/item/"), url-prefix("https://www.finobe.com/item/") {
.text-center.card {
    background-color: #e1e1e1;
    border: 1px solid #a7a7a7;
    border-radius: 0px;
}
.card-header {
    display: none;
}
.col-md-8 h4.mb-0 {
    font-size: 32px;
    font-weight: bold;
    color: #343434;
    letter-spacing: -1px;
}
}

@-moz-document url-prefix("https://finobe.com/user/"), url-prefix("https://www.finobe.com/user/"), url-prefix("https://nobelium.xyz/user/") {
.card-header {
    border: none;
    border-radius: 0px;
    font-size: 30px;
    font-weight: normal;
    color: #343434;
    background: transparent;
    text-align: left!important;
    letter-spacing: -1px;
}
.place-thumbnail-small {
    border: solid 1px #555;
}
.collapse.show {
    border: 1px solid #ccc;
}
.card-img-top {
    border: solid 1px #CCC;
    height: 110px;
    border-radius: 0px;
    width: 110px;
}
.col-lg-2 {
    border-right: 1px solid #ccc;
}
.col-lg-2 .btn-primary {
    font-weight: bold!important;
    width: 124px!important;
    background: #EFEFEF!important;
    border: 1px solid #CCC!important;
}
.col-lg-2 .btn {
    background: transparent;
    color: #363636;
    border: none;
    font-weight: normal;
    width: 123px;
    text-align: left!important;
}
.col-lg-2 .btn:hover {
    background: #EFEFEF;
}
.card-header a {
    border-color: #0852b7!important;
    background-color: #0852b7;
    border: 1px solid;
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-blue.png);
    color: white;
    padding: 1px 7px 0 7px;
    height: 20px;
    float: right;
    min-width: 40px;
    font-size: 14px;
    line-height: 18px;
    background-position: left -160px;
}
.card-header a:hover {
    background-position: left -181px;
    text-decoration: none;
}
.mb-md-5 {
    color: #0055B3;
}
.img-fluid {
    max-width: 80%!important;
}
}

@-moz-document url-prefix("https://finobe.com/forum/post?id="), url-prefix("https://finobe.com/forum/new/reply?id="), url-prefix("https://nobelium.xyz/forum/post?id="), url-prefix("https://nobelium.xyz/forum/new/reply?id="), url-prefix("https://www.finobe.com/forum/post?id="), url-prefix("https://www.finobe.com/forum/new/reply?id=") {
.bg-info {
    background-color: #0058b1!important;
    color: white;
    border-radius: 0px!important;
    font-weight: normal;
}
.bg-primary {
    display: none;
}
.card-body {
    background-color: #F9F9F9;
    border-bottom: 1px solid #D8D8D8;
}
.mb-4, .my-4 {
    margin-bottom: 0px!important;
}
.thumbnail-user-forum {
    width: 100px;
}
.forum__color-ball {
    border: 1px solid black;
}
.badge.badge-admin {
    content: url(http://web.archive.org/web/20140122073542im_/http://www.roblox.com/Forum/skins/default/images/users_moderator.gif);
    background-color: transparent;
    animation: none;
}
.breadcrumb {
    background-color: transparent;
}
.breadcrumb-item+.breadcrumb-item:before {
    content: " Â» ";
}
.b-right {
    border-right: none;
    border-bottom: none;
}
}

@-moz-document url-prefix("https://finobe.com/place/"), url-prefix("https://www.finobe.com/place/"), url-prefix("https://nobelium.xyz/place/") {
.card-header {
    display: none;
}
}

@-moz-document url("https://finobe.com/users"), url("https://www.finobe.com/users"), url("https://nobelium.xyz/users") {
.card, .page-item {
    display: none;
}
}

