style.css:
@-moz-document domain("synergia.librus.pl") {

/* ogólne */
body,
div {
    background-color: #303030;
}

#page #header #top-banner-container #graphic-menu,
#page #header #user-section {
    background-color: transparent;
}

.ui-tooltip-content {
    background-color: #ffffff;
}

.container-background {
    border: 20px #5b5b5b solid;
    border-right-width: 5px;
    border-left-width: 5px;
    padding: 0;
    background-color: #3c3c3c;
    box-shadow: 9px 9px 10px #101010;
}

.container h2.inside {
    border-radius: 1px;
}

.ui-button {
    margin-top: 5px !important;
}

.welcome-page .container-background {
    background-repeat: no-repeat;
}

.welcome-page .container-background .content-box {
    background-color: transparent;
}
 
.warning-content>p {
    color: #d2cdcd;
}

.warning-box {
    background-color: #303030;
    border-radius: 0;
}

.warning-box.information .warning-title {
    background-image: none;
    background-color: #3d3d3d;
}

/* zakładki */
.container span.fold {   
    margin: 20px 20px -20px -10px;
    background-color: #5b5b5b;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
}

.container span.fold a.fold-link span.fold-start, 
.container span.fold a.fold-link span.fold-end {
    background-image: none;
}

.container span.fold a.fold-link span.fold-end-scroll {
    background-image: none;
}

.container span.fold a.fold-link span.fold-end-scroll::after {
    content: "\25BC";
    display: inline-block;
    color: #ffffff;
    padding-top: 5px;
}

.container h2.inside {
    background-color: #303030;
}

/* tabele */
.container-icon {
    margin-top: 30px;
}

table.decorated.stretch td {
    border-left: 1px solid #999999 !important;
}

table.decorated thead tr td.colspan span {
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
}

table.decorated tr.line0 td.spacing,
table.decorated tr.line0 th.spacing,
table.decorated tr.line1 td.spacing,
table.decorated tr.line1 th.spacing,
table.decorated thead td.spacing, table.decorated thead th.spacing {
    border-left: 1px #999999 solid !important;
}

table.decorated thead tr:first-child th {
    border-top-left-radius: 0;
}

table.decorated {
    border-radius: 0;
}

td.no-border-top {
    background-color: #333333 !important;
}

table.decorated thead th {
    background-color: #333333;
    color: #313332;
}

table.decorated thead td.colspan span {
    background-color: #333333;
    color: #cccccc;
}

table.decorated thead td {
    background-color: #333333;
}

table.decorated tbody tr.line0 th,
tr.line0 {
    color: #cccccc;
    background-color: #404040 !important;
}

table.decorated tbody tr.line1 th,
tr.line1 {
    color: #cccccc;
    background-color: #606060 !important;
}

table.decorated tfoot td,
table.decorated.stretch tfoot td {
    padding: 0;
    background-color: #3c3c3c;
    border-width: 0 !important;
    border-radius: 0 !important;
    border-top: 1px solid #d9d9d9 !important;
}

table.decorated thead tr:first-child td:first-child,
table.decorated thead tr:first-child td:last-child {
    border-radius: 0;
}

table.decorated tbody tr.bolded td,
table.decorated tr td.bolded,
table.decorated.form thead td {
    background-color: #808080;
}

#absence_form td .box {
    background-color: #808080 !important;
    margin-right: -1px;
}

#absence_form td .box a {
    color: #ffa140;
}

#absence_form td .box a[title*="nieobecność uspr"] {
    color: #55f743;
}

/* wiadomości */
table.message-folders td:first-child {
    background-image: none !important;
}

table.message-folders a {
    color: #939393;
}

input.medium {
    width: 150px;
}

 /* plan lekcji */
.plan-lekcji td div.text {
    background-color: #606060;
}

.plan-lekcji-info {
    background-color: #c80000;
}

table.kalendarz thead td.weekend {
    background-color: #699560 !important;
    border-left-width: 1px;
}

table.kalendarz tbody tr.line0 td.weekend {
    background-color: #404040 !important;
}

table.kalendarz tbody tr.line1 td.weekend {
    background-color: #606060 !important;
}

table.kalendarz .kalendarz-dzien,
table.kalendarz .kalendarz-dzien .kalendarz-numer-dnia {
    background-color: transparent !important;
}

table.kalendarz .kalendarz-dzien table {
    border: 1px solid #c0c0c0;
    border-radius: 0;
}

table.kalendarz .kalendarz-dzien table td {
    color: #000 !important;
}

/* tła */
#page.systema #header #top-banner-container {
    background-image: url("https://wiai.rf.gd/librus/img/background.png");
}

#page.systema #header {
    background-image: none !important;
    background-color: #303030 !important;
}

#page #header #main-navigation-container {
    background-color: #303030;
}

table.decorated thead td,
table.decorated tbody tr.line0 td,
table.decorated tbody tr.line1 td {
    border-width: 1px;
}

table.form.center,
table.filters {
    margin-top: 30px;
}

table.filters th,
table.filters td {
    background-color: #3d3d3d !important;
    color: #d9d9d9 !important;
}

.container.border-red .container-background {
    border-width: 2px;
    border-radius: 0;
    box-shadow: 0 0 0;
}

.container.green .container-background {
    background-color: #809680;
}

/* tekst */
body {
    color: #ffffff;
}

h3 {
    color: #d2cdcd;
}

span {
    color: #000000;
}

a,
div {
    font-size: 15px !important;
}

a {
    color: #a4b9e6;
}

td {
    color: #dddddd !important;
}

#page #header #top-banner-container #graphic-menu ul li a {
    color: #ffffff;
}

#page #header #top-banner-container #graphic-menu ul li a.button.counter.blue {
    right: 35px ;
    bottom: 40px;
}

span.tooltip {
    color: #ffffff;
}

.legend h3 {
    font-size: 20px;
}

.legend ul li p {
    font-size: 20px;
    line-height: 20px;
}

.legend ul li span {
    display: inline-block;
    height: auto;
    width: auto;
    padding: 4px;
    margin-right: 4px;
}

/* menu */
a#icon-oceny,
a#icon-nb,
a#icon-wiadomosci,
a#icon-ogloszenia,
a#icon-terminarz,
a#icon-zadania{
    margin-right: 30px !important;
}

#page #header #top-banner-container #graphic-menu ul li a .circle::before { 
    visibility: hidden;
}

a#icon-oceny span.circle {
    background-image: url("https://wiai.rf.gd/librus/img/oceny.png");
}

a#icon-nb span.circle {
    background-image: url("https://wiai.rf.gd/librus/img/plecak.png");
}

a#icon-wiadomosci span.circle {
    background-image: url("https://wiai.rf.gd/librus/img/list.png");
}

a#icon-ogloszenia span.circle {
    background-image: url("https://wiai.rf.gd/librus/img/megafon.png");
}

a#icon-terminarz span.circle {
    background-image: url("https://wiai.rf.gd/librus/img/kalendarz.png");
}

a#icon-zadania span.circle {
    background-image: url("https://wiai.rf.gd/librus/img/dom.png");
}

#page #header #user-section {
    padding-right: 10px;
}

/* powiększenie ocen */
span.grade-box {
    font-size: 20px;
    padding: 8px 5px;
}

}
