style.css:
@-moz-document url-prefix(https://www.avsforum.com/forum/){
html {
    background-size: 30px 30px;
    background-color: #101010;
    background-image: linear-gradient(45deg, rgba(39, 40, 56, .2) 25%, transparent 25%, transparent 50%, rgba(39, 40, 56, .2) 50%, rgba(39, 40, 56, .2) 75%, transparent 75%, transparent);
    height: 100% !important;
}
*{font-family: 'Helvetica Neue', 'Helvetica', 'Arial' !important;}
body {
    min-height: 100% !important;
    width: 100%;
    background: #101010!important;
    margin-top: 0!important;
    margin-bottom: 0!important;
    margin-left: auto!important;
    margin-right: auto!important;
    padding: 0px!important;
}
#posts,#posts .postbit_legacy_wrapper_bottom .main-column-text div.right,#posts .postbit_legacy_wrapper, #post .postbit_legacy_wrapper, #post .postbit_legacy_wrapper_bottom .main-column-text div.right{
    background-color:#101010;
}
#posts .main-column-text, #post .main-column-tex{
    border-left: 169px solid #101010;
    background-color:#151515;
}
::selection {
    background: #b85a5a;
}
input[type="button"]:focus {
    outline: none;
    border: 0px solid #b85a5a!important;
}
select:focus {
    outline: none;
    border: 1px solid #b85a5a!important;
}
textarea:focus {
    outline: none;
    border: 1px solid #b85a5a!important;
}
input._bginput:focus {
    outline: none;
    border: 1px solid #b85a5a!important;
}
input.bginput:focus {
    outline: none;
    border: 1px solid #b85a5a!important;
}
div.navbord a:visited,
div.navbord_avisited,
div.navbord a:link {
    color: #999 !important;
}
.vbseo_buttons .vbseo_liked{
    border-color: #11349d;
}
.contentBoxSub {padding:0 !important;}
.contentBoxContent{background:#252525;border:0 !important;}
.navbord {
    margin-left: 0px;
    margin-right: 0px;
    margin-top: -2px;
    margin-bottom: 30px;
    padding: 0px !important;
    border-top: 2px solid #353535!important;
    border-bottom: 4px solid #353535!important;
    border-left: 4px solid #353535 !important;
    border-right: 4px solid #353535 !important;
}
div.navbord > table td:hover {
    background: #252525!important;
}
div.navbord > table td {
    background: #353535 !important;
    border: 1px solid #252525;
}
body > table > tbody > tr > td:first-child {
    text-align: center!important;
}
html,
body,
table,
div,
tr,
td,
tbody,
span,
fieldset,
legend,
a,
strong {
    font-family: 'Open Sans', Verdana, Geneva, sans-serif;
}
strong,
b {
    font-weight: 500 !important;
}
div.page {
    padding-top: 0px !important;
    background: #151515;
}
.pagenav td {
    padding: 2px 4px 2px 5px;
}
div.pagenav {
    margin-bottom: 5px;
    margin-top: 5px;
}
.button,
.popup_pickbutton {
    height: 18px !important;
    border: none !important;
    color: #fff;
    background-color: #11349d;
    font: 11px 'Open Sans', Verdana, Geneva, sans-serif;
}
.button:hover,
.popup_pickbutton:hover {
    opacity: 0.4;
    border: none!important;
    font: 11px 'Open Sans', Verdana, Geneva, sans-serif;
}
.vbmenu_control {
    background: #202020!important;
    height: 15px;
}
.vbmenu_control a:visited,
.vbmenu_control_avisited,
a:link {
    color: #fff!important;
}
.vbmenu_control a:link,
.vbmenu_control_alink {
    color: #fff !important;
}
.vbmenu_control a:link:hover,
.vbmenu_control_alink:hover {
    color: #999 !important;
}
.vbmenu_control a:visited:hover,
.vbmenu_control_avisited:hover {
    color: #999 !important;
}
.tborder {
    border-color: #353535;
    border-width: 4px;
    border-style: solid;
    background: transparent!important;
}
.tcat {
    background: #000!important;
}
.thead {
    margin: 0 !important;
    background: #101010!important;
    color: #fff !important;
    border-left: 0px !important;
    border-right: 0px !important;
    border-top: 0px !important;
    border-bottom: 1px !important;
    border-color: #272838 !important;
    border-bottom-style: dotted !important;
    font-weight: normal !important;
    white-space: nowrap;
}
thead {
    background: #101010 !important;
}
.tfoot {
    display: none!important;
}
.smallfont {
    font: 10px 'Open Sans', Verdana, Geneva, sans-serif;
    color: #fff;
}
a:visited,
body_avisited {
    color: #d6d6d6!important;
}
.tcat a:link,
.tcat_alink {
    color: #fff !important
}
.tcat a:visited,
.tcat_avisited {
    color: #fff !important;
}
.tcat {
    color: #fff !important;
}
a:link,
body_alink {
    color: #d6d6d6 !important;
}
.alt1,
.alt1Active {
    color: #CCC !important;
	font-size:.9rem !important;	
}
.alt2,
.alt2Active {
    background: #151515;
    color: #fff !important;
}
div[style="width: 468px; height: 60px; background-color: #ffffff"] {display:none;}
.headerContent {width:100% !important;}
/*post content*/
.tborder td:nth-child(1) div img {max-width:64px !important;height:auto;border-radius:3px;}
.tborder td:nth-child(2) div {padding:1rem !important;line-height:1.5 !important; font-weight:200;letter-spacing:.03rem;}
.tborder td:nth-child(2) hr{border:0;color:#fff;}
.tborder td.alt1:nth-child(1) img, .tborder td.alt2:nth-child(2) img {filter: invert(100%) hue-rotate(180deg);}
.tborder td.alt1:nth-child(3) {padding:1rem;}
.alt2[style="margin:6px 0px 6px 0px; padding:6px; border:2px groove"] {border:0 !important;padding:1rem !important;line-height:1.5 !important; font-weight:200;letter-spacing:.03rem;}
input._bginput {
    background: #353535;
    height: 16px;
    color: #fff !important;
    padding: 0px!important;
    border: 1px solid #32bf38!important;
}
.navbar_wrapper, .wrapper,textarea,
.bginput {
    border: 1px solid #11349d!important;
    background: #050505;
    color: #fff !important;
    font: 10pt 'Open Sans', Verdana, Geneva, sans-serif;
}
.info-bar, .quote-block, .postquote{
    border: 4px solid #32bf38!important;
    background: #101010;
    color: #fff !important;
    font: 10pt 'Open Sans', Verdana, Geneva, sans-serif;
}

select {
    border: 1px solid #32bf38 !important;
    background: #404040;
    color: #fff;
    font: 11px 'Open Sans', verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
option {
    background: #353535!important;
    color: #fff!important;
}
div.imagebutton:hover {
    background: #bbb!important;
}
.alt1 {
    background-color: #303030!important;
}
.alt2 {
    background: #151515!important;
    color:#fff;
}
.vbmenu_option {
    color: #fff !important;
    background: #353535!important;
}
.time {
    color: #b85a5a !important;
    font-weight: normal!important;
    padding: 2px!important
}
.vbmenu_popup {
    background: #656565!important;
    border: 1px solid #32bf38!important;
}
.vbmenu_hilite {
    background: #b85a5a;
    opacity: 1 !important;
}
#dnd_drag_space {
    background: #656565!important;
    border: 1px solid #11349d!important;
}
.ocolor,
.ofont,
.osize,
.osmilie,
.osyscoloar,
.smilietitle {
    background: #404040!important;
    color: #fff!important;
    border: 0!important;
    margin: 0 !important;
}
#fontOut,
#sizeOut,
.popup_feedback {
    background: #353535!important;
    color: #fff!important;
}
img,
img:link {
    max-width: 600px!important
}
a[onclick*="return toggle_collapse"] {
    float: left!important;
    padding-top: 4px!important;
    padding-bottom: 0!important;
    padding-right: 5px!important;
    padding-left: 0!important;
}
table.tborder td,
a:hover,
a:active,
body_ahover {
    color: #fff!important
}
a.bigusername {
    font-weight: bold!important;
    color: #b85a5a!important;
    background: none!important;
}
a[href*="member.php?find=lastposter"] {
    color: #b85a5a!important;
    font-weight: normal;
}
.alt2Active {
    background: #060!important;
}
.alt1Active {
    background: #404040!important;
}
.panel {
    color: #fff !important;
    background: #151515!important;
    border: 0!important
}
legend {
    color: #fff !important;
}
.panelsurround {
    background: #151515!important;
}
div.smallfont[style="margin-bottom: 2px;"] {
    clear: both!important;
    width: 80px;
    margin: 0 auto 0 10px!important;
    border: 0;
    background: #11349d;
    text-align: center!important;
    text-transform: none !important;
}
div[id*="post_message_"] a {
    text-decoration: none !important;
}
div>a {
    text-decoration: none!important;
}
div.fieldset,
table#vB_Editor_QE_1_editor {
    border: 0!important;
    width: 90%!important;
}
div.fieldset>div {
    color: #999!important;
    padding: 3px!important;
}
div.imagebutton {
    background: transparent!important;
    border: 0!important;
    padding: 0!important;
}
font[color="#ffff00"] {
    color: #ccc !important;
    font-weight: normal;
}
input.bginput {
    height: 12px;
    background: #404040 !important;
    color: #999!important;
    padding: 2px!important;
}
input[type="button"] {
    color: #999;
    border: none !important;
    background-color: transparent;
    font: 10px 'Open Sans', sans-serif;
    margin-right: 3px !important;
}
td.alt2[width="175"] {
    /*width: 130px !important;*/
}
pre.alt2 {
    height: auto!important;
    width: 700px!important;
    border: 4px double #555!important;
}
body.wysiwyg, nav {
    background: #151515!important;
    width: auto!important;
    color:#fff;
}
fieldset,
.fieldset {
    border: 1px solid #272b35 !important;
}
table.fieldset {
    border: 0px!important;
}
td[class="vBulletin_editor"],
div[class="vBulletin_editor"] {
    background: #454545!important;
    border: 1px solid #272838!important;
    color: #fff!important
}
td[style*="border-right: 0px solid rgb(255, 255, 255)"] {
    border: 0!important;
    background: #353535!important;
}
.alt1,
a:link,
a:link,
a:visited {
    font-weight: 500 !important;
}
.alt2[style="margin: 0px; padding: 6px; border: 1px inset;"] {
    background: #101010!important;
    padding: 10px!important;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border-top-right-radius: 5px;
    margin: 0px!important;
    border: 1px solid #b85a5a !important;
}
td.alt2[style="border:1px inset"] {
    background: #101010!important;
    padding: 5px!important;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border-top-right-radius: 5px;
    margin: 5px!important;
    border: 1px solid #b85a5a!important;
}
td.popup_feedback,
div#vB_Editor_001_size_out,
div#vB_Editor_001_font_out,
td.alt_pickbutton {
    padding: 0!important;
    border: 1px solid #000!important;
    background: #fff;
    color: #060606;
    font: 10px 'Open Sans', verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
td[style="border: 1px solid red; padding: 0px; font-weight: bold;"][title="Today"] {
    border: 1px dashed #32bf38 !important;
}
div[style="border-bottom: 0px dotted; margin-bottom: 6px; padding-bottom: 2px;"] {
    border: 0!important;
}
div[style="border: 0px groove ; margin: 6px 0px; padding: 6px;"] {
    border: 1px solid #32bf38 !important;
}
td:hover[class="alt2"][style="border: 0px inset ;"] {
    opacity: 0.9!important;
}
div[style="margin: 5px 20px 20px;"] div.alt2 {
    background: #aaa!important;
}
div[style="margin: 5px 20px 20px;"] {
    margin: 4px 0 10px 0!important
}
td#header_right_cell,
div > div.page > div > br {
    display: none!important
}
option {
    background: #404040!important;
    color: #999!important;
}
div.smallfont[style="margin-bottom:2px"] {
    color: #fff;
    clear: both!important;
    width: 80px;
    margin: 0 auto 0 0px!important;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    border-bottom: 0px solid #32bf38;
    background: #32bf38;
    text-align: center!important;
    text-transform: uppercase!important;
}
pre.alt2 {
    height: auto!important;
    width: 700px!important;
    border: 1px solid #32bf38!important;
}
td.alt1 {
    border: 0 !important;
}
td.alt2 {
    border: 0 !important;
    opacity: 1.0!important;
    margin: 2px!important
}
div[style="margin: 5px 20px 20px;"] div.alt2 {
    background: #aaa!important;
}
