style.css:
@-moz-document domain("trakt.tv") {
:root {
    --text-color: #ffffffde;
    --text-color-strong: #fffffff7;
    --color-red: #e84046;
    --color-red-light: #ff6f74;
    --color-primary-black: #161616;
    --color-secondary-black: #1d1d1d;
    --color-gray: #dddddd;
}

section {
    background: var(--color-primary-black);
}
.subnav-wrapper {
    background-color: var(--color-secondary-black) !important;
}
#progress-wrapper {
    background-color: var(--color-primary-black);
}
.comment-wrapper {
    background-color: transparent;
}
#progress-wrapper .row .main-info .seasons {
    background-color: var(--color-secondary-black);
}
body, .subnav-wrapper .left h2 .right a {
    color: var(--text-color);
}
.posters .poster,
.posters>div .quick-icons {
    border-color: var(--color-secondary-black);
}
.btn.btn-default.dropdown-toggle {
    background-color: transparent;
    color: var(--text-color);
}
a {
    color: var(--color-red);
}
a:hover, a:focus, 
#ondeck-wrapper .ignore:hover {
    color: var(--color-red-light);
}
.progress-bar {
    background-color: var(--color-red);
}
#progress-wrapper .row .main-info .seasons .season-toggle.link, #progress-wrapper .row .main-info .seasons .all-toggle.link {
    color: var(--color-red);
}
.btn.btn-recommendations {
    background-color: transparent;
}
.btn-recommendations:hover, .btn-recommendations.selected {
    background-color: #e85d04;
}
#main-search {
    background-color: var(--color-red);
}
.fanarts .fanart h4 {
    background-color: var(--color-red);
}
#progress-wrapper .row .main-info .seasons .episodes .episode.delete a {
    color: var(--color-red);
}
.filter-dropdown.selected .icon, .filter-dropdown.selected .caret, .filter-dropdown .selected .icon, .filter-dropdown .selected .caret {
    color: var(--color-red) !important;
}
.comment-wrapper .interactions a .text-wrapper .under-count, #main-comment .interactions a .text-wrapper .under-count {
    color: #999;
}
#progress-wrapper .row .main-info h3.show-title .ignore:hover, #progress-wrapper .row .main-info h3.show-title .reset:hover {
    color: var(--color-red);
}
.subnav-text {
    background-color: rgba(255, 255, 255, 0.15);
}
.pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
    background-color: rgba(0, 0, 0, .25);
}
.pagination>li>a, .pagination>li>span {
    color: var(--text-color);
    background-color: rgba(0, 0, 0, .5);
}
.pagination .trakt-icon-ellipse {
     color: var(--text-color);
}
ul.pagination a:hover {
    color: var(--text-color-strong);
    background-color: var(--color-red);
}
.pagination>.active>a, .pagination>.active>a:hover, .pagination>.active>a:focus, .pagination>.active>span, .pagination>.active>span:hover, .pagination>.active>span:focus {
    color: var(--text-color-strong);
    background-color: var(--color-red);
}
.filter-dropdown .icon.trakt-icon-divider {
    color: var(--text-color);
}
.filter-dropdown .metadata .trakt-icon-wand,
.btn-list-edit-items, #btn-list-edit-lists,
.custom-list .list-info .user-actions .icon.btn-list-clone, 
.custom-list .list-info .user-actions .icon.btn-list-subscribe,
header#top-nav #header-search #header-search-type a {
    color: var(--text-color);
}
.btn-primary {
    background-color: var(--color-red);
    border-color: #7F0405;
}
.dropdown-menu {
    background-color: var(--color-secondary-black);
}
.dropdown-menu>li>a {
    color: var(--text-color);
}
.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
    background-color: var(--color-primary-black);
    color: var(--text-color-strong);
}
.btn-default {
    border-color: #666;
}
.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open>.btn-default.dropdown-toggle {border-color: #777;}
#progress-wrapper .row .main-info .seasons .season-toggle.link .fa, #progress-wrapper .row .main-info .seasons .all-toggle.link .fa {
    color: var(--color-red);
}
.comment-wrapper .above-comment {
    background-color: var(--color-secondary-black);
}
.comment-wrapper .above-comment .date {
    color: #999;
}
#info-wrapper .sidebar .external li a {
    background-color: var(--color-primary-black);
    border-color: #666;
}
.label-vip {
    background-color: var(--color-red);
}
.comment-wrapper.list .above-comment .user-name h4 a.username {
    color: var(--color-gray);
}
#info-wrapper .sidebar .poster {
    border-color: var(--color-secondary-black) !important;
}
#info-wrapper .sidebar .poster, #info-wrapper .sidebar .streaming-links, #info-wrapper .sidebar .btn-watch-now {
    box-shadow: none;
}
.btn-watch, .btn-collect, .btn-list, .btn-comment {
    background-color: transparent;
}

.summary-activity .tabs .tab.selected {
    background-color: var(--color-secondary-black);
    border-bottom-color: var(--color-secondary-black);
}
.summary-activity .tabs .tab {
    border: solid 1px var(--color-secondary-black);
    color: var(--text-color);
}
.users-wrapper {
    background-color: var(--color-secondary-black);
    border: solid 1px var(--color-secondary-black);
}
.user-avatar img {
    border: solid 2px var(--text-color);
    background-color: var(--text-color);
}

#info-wrapper .sidebar .sections li.active a, #comments .active h3, #replies .active h3 {
    color: var(--color-red);
}
#info-wrapper .sidebar .sections li a:hover, #info-wrapper .sidebar .sections li a:focus, #comments .tab-links h3:hover, #replies .tab-links h3:hover {
    color: var(--text-color);
}
#actors .tab-links h3:hover, #credits .tab-links h3:hover {
    color: var(--text-color);
}
#actors .active h3:hover .count, #credits .active h3:hover .count, #actors .tab-links h3:hover .count, #credits .tab-links h3:hover .count {
    border-color: var(--text-color);
    background-color: var(--text-color);
    color: var(--color-primary-black);
}
#info-wrapper .info .actors-wrapper .posters ul li .character {
    color: var(--text-color-strong);
}
#actors .active h3 .count, #credits .active h3 .count {
    border-color: var(--color-red);
    background-color: var(--color-red);
}
#info-wrapper .info .actors-wrapper .posters ul:hover li .titles .name {
    color: var(--text-color);
}
#info-wrapper .info #overview[data-readmore][aria-expanded=false] .shade {
    background-image: linear-gradient(to bottom,rgba(243,243,243,0) 0%, var(--color-primary-black) 100%);
}
.grid-item.hu-ck-st-er-grid-item {
    display: none;
}
#main-search {
    background-color: var(--color-secondary-black);
}
#info-wrapper .season-links .links ul li a.selected {
    color: #fafafa;
}
.fanarts .fanart h3,
#cover-wrapper,
#cover-wrapper #avatar-wrapper h1 a {
    color: var(--text-color);
}
#progress-wrapper .row .main-info .seasons .season-toggle .season {
    color: var(--text-color);
}
#cover-wrapper #avatar-wrapper .avatar {
    border: 3px solid var(--text-color);
    background-color: var(--text-color);
}
.dropdown-menu .divider {
    background-color: var(--text-color);
}
#main-search input.footer-search, #header-search-query {
    background-color: var(--color-gray);
    color: var(--color-secondary-black);
}
#main-search h2 {
    color: inherit;
}
#ondeck-wrapper,
#schedule-wrapper,
#network-wrapper,
.alert-no-data {
    background-color: var(--color-secondary-black);
}
#recommendations-wrapper,
#network-wrapper .alert-no-data {
    background-color: var(--color-primary-black);
}
#info-wrapper .sidebar .vip-actions li a .fa, #info-wrapper .sidebar .mod-actions li a .fa,
.subnav-wrapper .right .feed-icon:hover,
#actors .active h3, 
#credits .active h3,
.filter-dropdown li a.selected,
#ondeck-wrapper .posters .titles-link, 
#recommendations-wrapper .grid-item h3,
header#top-nav .navbar-nav li>a:hover,
#links-wrapper a.selected,
#links-wrapper a:hover,
#sort-direction.selected {
    color: var(--color-red);
}
#links-wrapper a, 
#links-wrapper i,
footer a, 
#results-top-wrapper.user-wrapper .stats-wrapper a, 
#recommendations-wrapper h2, 
header#top-nav a, 
header#top-nav i,
#user-menu ul li a,
header#top-nav .navbar-toggle, 
header#top-nav #xs-sign-in ,
.mobile-nav ul li a{
    color: var(--color-gray);
}
#top-brand img {
    opacity: 0.7;
}
.popover-title {
    background-color: var(--color-gray);
    border-bottom: 1px solid var(--color-gray);
    color: var(--color-primary-black);
}
.popover.bottom>.arrow:after {
    border-bottom-color: var(--color-gray);
}
#header-search .btn-default:hover, 
#header-search .btn-default:focus, 
#header-search .btn-default:active, 
#header-search .btn-default.active, 
#header-search .open>.btn-default.dropdown-toggle,
#header-search .btn.btn-default.dropdown-toggle{
    background-color: var(--color-primary-black);
    border-color: var(--color-primary-black);
}
#cover-wrapper .full-bg {
    opacity: .3 !important;
}
#hu-ck-s-t-er-content-page,
#hu-ck-s-t-er-de-sk-top-wrapper,
#hu-ck-s-t-er-mo-b-ile-square-wrapper,
#hu-ck-s-t-er-de-sk-top-mi-d-dle,
#hu-ck-s-t-er-de-sk-top-bo-t-tom,
#hu-ck-s-t-er-de-sk-top-sidenav,
.hu-ck-s-t-er-grid-item, 
.feed-icon,
.frame-wrapper .sidenav .feeds,
.frame-wrapper .sidenav nav a.notifications,
#user-menu ul li.separator + li + li,
#user-menu ul li:nth-child(10),
#user-menu ul li.dark-knight,
#user-menu ul li a[href^="/widgets"] ,
#user-menu ul li a.yir-loader,
.show-advanced-filters-link,
#filter-watchnow-launcher {
    display: none;
}

input#header-search-query {
    color: var(--color-secondary-black);
}

body.apps #apps-icon-wrapper,
#standard-features {
    background: var(--color-secondary-black);
}
body.apps .hero-wrapper.light {
    border-color: var(--color-secondary-black);   
}
body.apps #api-wrapper .shade {
   background: rgba(0, 0, 0, 0.87) !important;
}
body.apps #api-wrapper {
    border-top: solid 1px rgba(0, 0, 0, 0.87) !important;
}
body.apps #apps-icon-wrapper .apps-icons #iphone .app .icon {
    box-shadow: none;
}

body.settings table.table,
body.settings .panel .control-label {
    color: var(--color-secondary-black);
}

.comment-wrapper.featured {
    background-color: inherit;
}
.comment-wrapper.featured .above-comment {
    background-color: var(--color-secondary-black);
}

#seasons-episodes-sortable .under-info .titles, #seasons-episodes-sortable .episode-stats {
    background: var(--color-secondary-black);
}

#seasons-episodes-sortable .under-info h3 a .main-title-sxe, #seasons-episodes-sortable .under-info h3 a .main-title-abs {
    color: var(--color-red);
}
#seasons-episodes-sortable .under-info h3 a {
    color: var(--text-color-strong);
}

#seasons-episodes-sortable .under-info h3 a:hover .main-title-sxe, #seasons-episodes-sortable .under-info h3 a:focus .main-title-sxe,
#seasons-episodes-sortable .under-info h3 a:hover, #seasons-episodes-sortable .under-info h3 a:focus {
    color: var(--color-red-light);
}

body.discover #recent-comments-wrapper #recent-comments {
	background-color:  var(--color-secondary-black);
}
body.discover #recent-comments-wrapper #recent-comments-title h4 {
	color: var(--text-color-strong);
}
body.discover #recent-comments-wrapper #recent-comments-title h5 {
	color: var(--text-color);
}
body.discover #recent-comments-wrapper #recent-comments .comment-outer-wrapper .above-comment {
	background-color:  var(--color-primary-black);
}

.comment-wrapper.featured .comment-text[data-readmore][aria-expanded=false] .shade,
.subnav-text .text[data-readmore][aria-expanded=false] .shade {
    background-image: linear-gradient(to bottom, transparent 0%,var(--color-primary-black) 100%);
    background-repeat: repeat-x;
}

@media (max-width: 767px) {
    header#top-nav #header-search.open {
        right: -1px;
    }
    body.show_progress .list-stats-wrapper {
        background-color: var(--color-secondary-black);

    }
}
}
