style.css:
@-moz-document domain("auth.lyceesudmedoc.fr") {
/* gujarati */
 @font-face {
    font-family:'Baloo Bhai';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Baloo Bhai Regular'), local('BalooBhai-Regular'), url(https://fonts.gstatic.com/s/baloobhai/v5/ZgNWjP5GM7bCUdmXgWyVvHnOMrCO.woff2) format('woff2');
    unicode-range: U+0964-0965, U+0A80-0AFF, U+200C-200D, U+20B9, U+25CC, U+A830-A839;
}
/* vietnamese */
 @font-face {
    font-family:'Baloo Bhai';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Baloo Bhai Regular'), local('BalooBhai-Regular'), url(https://fonts.gstatic.com/s/baloobhai/v5/ZgNWjP5GM7bCUdmXgWyVvG3OMrCO.woff2) format('woff2');
    unicode-range: U+0102-0103, U+0110-0111, U+1EA0-1EF9, U+20AB;
}
/* latin-ext */
 @font-face {
    font-family:'Baloo Bhai';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Baloo Bhai Regular'), local('BalooBhai-Regular'), url(https://fonts.gstatic.com/s/baloobhai/v5/ZgNWjP5GM7bCUdmXgWyVvGzOMrCO.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
 @font-face {
    font-family:'Baloo Bhai';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Baloo Bhai Regular'), local('BalooBhai-Regular'), url(https://fonts.gstatic.com/s/baloobhai/v5/ZgNWjP5GM7bCUdmXgWyVvGLOMg.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
body {
    background: #222;
}
#header img {
    display: none!important;
}
#header:before {
    content:"Lycée Sud Médoc";
    font-size: 50px;
    color: white;
    display: block;
    text-align: center;
    font-family:'Baloo Bhai', cursive;
    bottom: 50px;
}
.message {
    border-radius: 0;
    border: none;
}
.message-warning {
    color: white;
    background: #f1c40f;
}
.alert-danger {
    color: white;
    background: #e74c3c;
}
#lform {
    border: none!important;
    border-radius: 0!important;
}
#footer {
    background-color: transparent;
    opacity: .5;
}
#footer:hover {
    opacity: 1;
}
#footer .col-md-8.col-10 {
    color: white;
}
#footer .col-md-8.col-10:before {
    content:"Thème réalisé par Enzo PB";
    color: white;
    margin-bottom: -15px;
}
#footer .fa {
    display: none;
}
#footer .col-md-2.col-2.text-right {
    display: none;
}
#footer .btn.btn-secondary {
    display: initial;
    background: transparent;
    box-shadow: none;
    padding: 0;
    vertical-align: initial;
    font-size: initial;
}
.input-group.mb-3:nth-child(3), .btn.btn-secondary {
    display: none;
}
#lform {
    background: #444;
}
.card {
    border: 0;
}
input.form-control {
    background: #555;
    border: 0;
    color: white;
    transition: all .2s;
}
input.form-control:focus {
    background: #515151;
    box-shadow: none;
    color: white;
}
.input-group:hover {
    -webkit-box-shadow: 0px 0px 20px -10px rgba(0, 0, 0, 0.75);
    -moz-box-shadow: 0px 0px 20px -10px rgba(0, 0, 0, 0.75);
    box-shadow: 0px 0px 20px -10px rgba(0, 0, 0, 0.75);
    transition: all .2s;
}
input.form-control::placeholder {
    color: rgba(255, 255, 255, .5);
}
.input-group-text {
    background: #555;
    border: none;
    color: white;
}
.btn.btn-success {
    background: #2ecc71;
}
.btn.btn-success:hover {
    background: #27ae60
}
.btn {
    border: none;
}
.btn:hover {
    -webkit-box-shadow: 0px 0px 25px -10px rgba(0, 0, 0, 0.75);
    -moz-box-shadow: 0px 0px 25px -10px rgba(0, 0, 0, 0.75);
    box-shadow: 0px 0px 25px -10px rgba(0, 0, 0, 0.75);
}
/* Page d'accueil */
 .star {
    display: none;
}
.navbar, .card-body, .dropdown-menu {
    background-color: #333!important;
}
.dropdown-item {
    margin: 0!important;
}
.navbar a {
    color: white!important;
}
.navbar li {
    margin: 2px;
}
.navbar .ui-state-active, .navbar li:hover {
    background-color: #444;
}
.bg-secondary {
    background-color: #3498db!important;
}
.text-muted {
    color: #999!important;
}
/* Page historique de connexions */
 table {
    color: white!important;
}
/* Page déconnexion */
 #logout .buttons {
    margin-top: 0;
}
}
