style.css:
@-moz-document url-prefix("https://music.amazon.") {
#dragonflyView .card .titles h2 {
    color: #f2f2f2;
}
#dragonflyView .card .titles .subTitleText {
    color: #b9c7e2;
}
.listViewStatusButtonInLibrary .add {
    background: #eaeaea;
}
.navButton.left-align-text * {
    color: #f2f2f2 !important;
}
#dragonflyView > div > div > div:nth-child(3) > a:nth-child(1) > span > span.title, #dragonflyView > div > div > div:nth-child(3) > a:nth-child(2) > span > span.title, #dragonflyView > div > div > div:nth-child(3) > a:nth-child(3) > span > span.title{
     color: #000 !important;
}
.border-gradient2 .center {
    background: linear-gradient(180deg,#252d42,#1d1f26);
}
.border-gradient2 .center *{
    color: #eaeaea;
}
.navButton.left-align-text {
    background-color: #232939;
}
#dragonflyLeftNav .mainLinks .mainLinksTitle, #subscriptionStatusUpsell #subscriptionHeader .subscriptionTitle {
    color: #b4ccfb;
}
.horizontalTile .details .primaryTitle, .horizontalTile .details .primaryTitle a, .horizontalTile .detailsWithIndex .primaryTitle, .horizontalTile .detailsWithIndex .primaryTitle a {
    color: #657fc9;
    font-size: .9375rem;
}
#dragonflyView .playlistDetailsHeader .viewSubDescription {
    color: #e4f0ff;
}
.navigationToggle .backgroundImageGradient {
    background-image: linear-gradient(180deg,hsla(0, 0%, 0%, 0.19),hsla(0, 0%, 0%, 0.42),#000);
}
.playlistDetailsListItem:hover:not(.emptyRow):not(.header) td {
    background-color: #3d759f61;
}
.playlistDetailsListItem:hover:not(.emptyRow):not(.header) .hoverColumn, .playlistDetailsListItem:hover:not(.emptyRow):not(.header) .selectColumn {
    border-bottom: 1px solid #3d759f61;
}
.playlistDetailsListItem:not(.emptyRow) td {
    border-bottom: 1px solid #3d759f61;
}
.listViewDurationContextButton .listViewContextButton {
    background-color: #478fc8;
}
.quickAddToPlaylistButton .primaryTitle, .quickAddToPlaylistButton .secondaryTitle {
    color: #d8d8d8;
}
.quickAddToPlaylist .topBar, .quickAddToPlaylist .quickAddToPlaylistBar {     
     background: linear-gradient(180deg,#2b354f,#23252f);
}
.quickAddToPlaylist .headerSection{
    color:#eaeaea
}
.playlistDetailsList .selectedItem {
    background-color: #313b55;
}
#contextMenuContainer li{
    color:#f2f2f2;
}
#contextMenuContainer li:hover{
    background-color: #2d3149;
}
.topHitTile .details .primaryTitle, .topHitTile .details .primaryTitle a, .topHitTile .details .secondaryTitle, .topHitTile .details .secondaryTitle a {
    color: #fff;
}
.topHitTile {
    background: #40557d;
}
.searchViewHeader .libraryCatalogTabsToggle li a {
    color: #f2f2f2;
}
/*Stations*/
.stationTile.genericTile .details .primaryTitle {
    color: #f2f2f2;
}
/**/
/*last listened*/
.albumList .selectable, .ie .albumList td, .ie .songList td, .songList .hasError, .songList .selectable, .playlistDetailsListItem:not(.emptyRow) td{
    border: solid #53628b;
    border-width: 1px 0 0;
    transition: all .2s ease-out;
}
@media (max-width: 1391px){
#dragonflyView .card .horizontalListItem:nth-of-type(n) {
    background-color: #53628b12;
}
}
@media (min-width: 1392px){
#dragonflyView .card .horizontalListItem:nth-of-type(n){
    background-color: #53628b12;
}
}
/**/
/*competitions*/
.contentSelectorHeader {
    background-color: #252D42;
}
.singleList .titles h2 {
    color: #b4ccfb;
}
div.doubleHorizontalTile .right .rightTitle {
    font-size: 1.25rem;
    color: #b4ccfb;
}
.dropdownSelector .dropdownMenu h3 {
    color: #fff;
}
.dropdownSelector .dropdownMenu li a[data-selected=true] {
    background-color: #1f283b;
}
div.doubleHorizontalTile {
    background-color: #53628b12;
}
/**/
/*Song History*/
#browsePanel .browsePanelHeader {
    background-color: #3e4a6b;
}
#browsePanel .browsePanelHeader h1 {
    color: #fff;
}
#browsePanel {
    background-color: #202532;
}
.horizontalListItem .timestamp {
    color: #f1f1f1;
}
#browsePanel .horizontalTile {
    border-bottom: 1px solid #53628b;
}
/**/
/*songs*/
#headerWrapper > table > thead > tr.subHeader > th:nth-child(2) {
    background: #232a3d;
}
/**/
/*uploaded*/
th.sortable, th.playCell, th.checkAllHeader{
    background: #232a3d;
}
tr.selectable.smartList:hover, .albumBody tr:hover, .songTracks tr:hover {
    background-color: #3d759f61;
}
.songTracks td.playCell a.playLinkIcon {
    content: "\F130";
    color: #d9d9d9;
}
.songTracks td.playCell:hover a.playLinkIcon {
    color: #53aef5;
}
.albumBody tr:hover td .optionSprite:before, .nowPlayingOptionPanel a.optionSprite:before, .songTracks tr:hover td .optionSprite:before {
    color: #d9d9d9 ;
}
#optionPanel ul {
    color: #f2f2f2;
    background-color: #313B55;
}
#optionPanel a {
    color: #f2f2f2;
}
#optionPanel a:hover {
    background: #2d3149;
}
.albumList .selectable.selected td, .songList .selectable.selected td{
    background-color: #313b55;
}
/**/
/*follow playlist*/
.dialogContainer .dialogBody {
    color: #f2f2f2;
}
/**/
/*share*/
.sharingContainer h1 {
    color: #edf4ff;
}
.sharingContainer .sharingDescription h2 {
    color: #e1e8ff;
}
.sharingContainer .sharingContent {
    color: #d8dce7;
}
.sharingContainer .shareLabelRow .linkLabel {
     color: #edf4ff;
}
.sharingContainer .linkToShare input {
    color: #fff;
    background-color: #313b55;
}
.sharingContainer .buttonContainer .shareButton {
    color: #edf4ff;
}
/**/
/*download*/
.ap_content .deviceAuthPopover .content,.ap_content .deviceAuthPopover .footer {
    color: #f2f2f2;
}
/*playlist*/
.dragonfly section.menuPanel, .playqueue-view .current-track {
    background-color: #313b55;
}
.playqueue-view .title{
    color: #f7f6f6;
}
.playqueue-view .current-track .song-details .artist{
    color: #ececec;
}
.truncate{
    color: #c3c2c2;
}
.horizontalTile .details .primaryTitle, .horizontalTile .details .primaryTitle a, .horizontalTile .detailsWithIndex .primaryTitle, .horizontalTile .detailsWithIndex .primaryTitle a {
    color: #baccff;
}
.horizontalTile .details .secondaryTitle, .horizontalTile .details .secondaryTitle a, .horizontalTile .detailsWithIndex .secondaryTitle, .horizontalTile .detailsWithIndex .secondaryTitle a {
    color: #c3c2c2;
}
.dialogContainer .dialogHeader{
    color: #f2f2f2;
}
.selectContainer .selectDropdownOptions {
    background: #313b55;    
}
#dialogBoxView .addToPlaylistDialog .selectDropdownOption .playlistTitle, #dialogBoxView .addToPlaylistDialog .sectionHeader{
    color:#f2f2f2
}
.playerIconPlaylist:before,.playerIconAddButtonCircle:before {
    color: #dedede;
}
.selectContainer .selectDropdownOptions .selectDropdownOption:focus, .selectContainer .selectDropdownOptions .selectDropdownOption:hover {
    background: #4b5a80;
}
.ap_popover .ap_titlebar {
    background-color: #313b55!important;
}
.ap_popover .ap_titlebar .ap_title {
    color: #fffffff2;
}
.ap_body {
    background-color: #313b55;
    box-shadow: 0px 0px 3px 1px #fff;
}
#newPlaylistName {
    background-color: #4b5a80;
    color: #e8e8e8;
}
.playqueue-view .horizontalListItem {
    border-top: 1px solid #53628b;
}
/**/
[hide-scrollbar] :not(#guide-inner-content)::-webkit-scrollbar {
  display: none;
}
::-webkit-scrollbar,
::-webkit-scrollbar-corner {
  background: #2b344b;
  width: 14px;
  height: 14px;
}
::-webkit-scrollbar-button {
  display: none;
}
::-webkit-scrollbar-thumb {
  background: #53628b;
}
::-webkit-scrollbar-thumb:not(:active):hover {
  background: #5b6c99;
}
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active {
  background: #5b6c99;
}
}
