style.css:
@-moz-document domain("brick-hill.com") {
body {
    background-color: /*[[BkgColor]]*/;
    font-family: /*[[Font]]*/ !important;
}



.card,.modal .modal-content {
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0);
    background-color: #C0C0C0;
    border-color: #0000;
    border: 4.5px #fff;
    border-right: 4.5px #808080;
    border-bottom: 4.5px #808080;
    border-style: ridge;
    border-radius: 0px !important; }

.card .content {
    padding: 10px;
    background-color: #C0C0C0;
    border-right: #808080;
    border-bottom: #808080;
    ; }

.card .top:not(.absolute),.card .top:not(.absolute).blue,.card .top:not(.absolute).green,.card .top:not(.absolute).orange,.card .top:not(.absolute).red {
    background-image: url("https://i.imgur.com/Y5iCX4b.png");
    background-repeat: no-repeat;
    background-position: 99% center;
    background-size: 100px;
    image-rendering: pixelated;
    color: #fff;
    background-color: /*[[mColor]]*/;
    border: 0px;
    border-radius: 0px; }

.thread-label,.thread-label.blue,.thread-label.green,.thread-label.orange,.thread-label.red {
    color: #fff;
    background-color: /*[[mColor]]*/;
    border-radius: 0px; }

.button,.button.blue,button.blue,.button.red,button.red,.button.green,button.green,.button.orange,button.orange,.modal .cancel-button, .input-group .input-button  {
    color: #000;
    background-color: #C0C0C0;
    border-color: #0000;
    border: 3.5px #fff;
    border-right: #808080;
    border-bottom: #808080;
    border-style: ridge;
    font-family: /*[[Font]]*/ !important;
    border-radius: 0px }

.button.bits,button.bits {
    color: #fff;
    background-color: #f49b00;
    border-color: #f49b00;
    border: 3.5px #ca9438;
    border-style: outset;
    border-radius: 0px }

.button.bucks,button.bucks {
    color: #fff;
    background-color: #009624;
    border-color: #009624;
    border: 3.5px #03c131;
    border-style: outset;
    border-radius: 0px }

.button.free,button.free {
    color: #fff;
    background-color: #00a8ff;
    border-color: #000000;
    border: 3.5px outset #0594de;
    border-radius: 0px; }

.crate-types .active,.crate-types :hover {
    background-color: /*[[mColor]]*/;
    border-left: 0px solid #fe5746;
    color: #fff;
    background-image: url("https://i.imgur.com/A1WUBTP.png");
    background-repeat: no-repeat;
    background-position: 90% center;
    image-rendering: pixelated;
    border-radius: 0px; }

.crate-types li:first-child,.crate-types li:last-child {
    border-radius: 0px 0px 0px 0; }

.crate-types li {
    -webkit-transition: background-color .0s linear;
    transition: background-color .0s linear;
    background-image: url("https://i.imgur.com/9keFlfd.png");
    background-repeat: no-repeat;
    background-position: 90% center;
    image-rendering: pixelated; }

.tabs .tab-body {
    border: 4.5px solid #dcdcdc;
    border-style: outset;
    border-top: 0;
    border-radius: 0 0 0px 0px;
    background-color: #C0C0C0; }

.tabs .tab {
    text-align: center;
    color: #000;
    border-radius: 5px 5px 0 0;
    background-color: #C0C0C0;
    border: 4.5px solid #dcdcdc;
    border-style: outset;
    border-bottom: 3px solid #dcdcdc; }

.tabs .tab.active {
    background-color: #C0C0C0;
    font-weight: 600;
    border-bottom: 3px solid #C0C0C0; }

.modal .modal-content {
    background-color: #c0c0c0;
    border-top: 10px solid /*[[mColor]]*/;
    margin: 15% auto;
    padding: 20px;
    width: 20%;
    min-width: 250px; }


.friend-card:hover {
    background-color:/*[[mColor]]*/;
    border-radius: 1px;
    color: #fff }

.profile-card:hover {
    background-color: /*[[mColor]]*/;
    border-radius: 1px;
    color: #fff }

.hover-card {
    -webkit-transition: background-color .0s linear;
    transition: background-color .0s linear;
    border-radius: 1px; }

.profile-card.crate img {
    width: 100%;
    border: 2px solid #808080;
    background-color: #fff;
    border-radius: 0px; }

.avatar-card.crate img {
    height: 95px;
    background-color: #f9f9f9; }

.box.shaded {
    background-color: #f9f9f9;
    border: 3px solid #808080;
    border-radius: 0px; }

.avatar-card.crate .img-holder {
    border-radius: 0px;
    background-color: #f9f9f9;
    border: 3px solid #808080; }

.line,hr {
    border-top: 1px solid #808080;
    border-bottom: 1px solid #FFFFFF; }

.thread-row {
    border-bottom: 2px solid #808080; }

.divide {
    width: 1px;
    height: 100%;
    background-color: #808080; }

.thread {
    border-bottom: 1px solid #808080; }

.trade-item-box {
    border: 1px solid #808080;
    border-radius: 0px;    
}

.trade-item img {
    border: 1px solid #808080;
    border-radius: 0px;
}

.crate-types {
    border-right: 2px solid #808080;
    padding-right: 5px; }
}

@-moz-document domain("brick-hill.com") {
.label {
    color: #363636; }

.label.dark {
    color: #000; }

.card .item-creator {
    color: #4d4b4b }

.agray-text {
    color: #363636; }

.ablack-text {
    color: #000; }

.light-gray-text {
    color: #2d2d2d; }

.forum-options {
    color: #363636; }

.gray-text {
    color: #333333; }

.forum-links a {
    color: #555; }

.toggle-user-desc:not(.open):after {
    background-color: #c0c0c0;
    color: grey; }

.read-more-desc {
    background-color: #c0c0c0; }

.pages .page {
    color: #555; }

.pages .page.active {
    color: #000097; }

.forum-tag {
    background: #808080;
    color: #fff; }

.forum-tag:after {
    border: 5px solid transparent;
    border-right-color: #808080; }

.favorite-text {
    color: #fff700; }

.mint-membership {
    color: #000097; }

.ace-membership {
    color: #9e0000; }

.royal-membership {
    color: #a96704; }


.mem-cols .col {
    border: 1px solid #808080;
    border-radius: 0px;
}

.select,
select {
    background-color: #fff;
    border-radius: 0px;
    border: 2.5px #808080;
    border-right: #fff;
    border-bottom: #fff;
    border-style: outset; }

input,
textarea {
    border-radius: 0px;
    border: 2.5px #808080;
    border-right: #fff;
    border-bottom: #fff;
    border-style: outset; }

input.rigid,
textarea.rigid {
    border-radius: 0px;
    border: 2.5px #808080;
    border-right: #fff;
    border-bottom: #fff;
    border-style: outset; }



.input-group {
    border-radius: 0px;
    border: 2.5px #808080;
    border-right: #fff;
    border-bottom: #fff;
    border-style: outset; 
}
}

@-moz-document url-prefix("https://www.brick-hill.com/forum/thread/") {
.card .top:not(.absolute),
.card .top:not(.absolute).blue,
.card .top:not(.absolute).green,
.card .top:not(.absolute).orange,
.card .top:not(.absolute).red {
    background-repeat: no-repeat;
    background-position: 96% center;
    background-size: 100px; }
}

@-moz-document url-prefix("https://www.brick-hill.com/shop/") {
.card,
.modal .modal-content {
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0);
    background-color: #C0C0C0;
    border-color: #0000;
    border: 1px #fff;
    border-right: #808080;
    border-bottom: #808080;
    border-style: ridge; }

.card .content {
    padding: 8px;
    background-color: #C0C0C0;
    border-right: #808080;
    border-bottom: #808080; }
}
