style.css:
@-moz-document regexp(".*") {   
    html[hide-scrollbar="true"] ::-webkit-scrollbar {
        display: none;
    }
    ::-webkit-scrollbar {
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
    ::-webkit-scrollbar-thumb {
        -webkit-appearance: none !important;
        background: /*[[bar]]*/ !important;
        border-radius: /*[[br]]*/ !important;
        box-shadow: none !important;
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:hover {
        -webkit-appearance: none !important;
        background: /*[[hover]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:active {
        -webkit-appearance: none !important;
        background: /*[[active]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:vertical {
        border-left: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-top: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:horizontal {
        border-top: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-left: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
    }
    ::-webkit-scrollbar-track {
        -webkit-appearance: none !important;
        background: /*[[bg]]*/ !important;
        border-radius: 0 !important;
        box-shadow: none !important;
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
    ::-webkit-scrollbar-track:vertical {
        -webkit-appearance: none !important;
        border-left: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-right: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-top: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
        border-bottom: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
    }
    ::-webkit-scrollbar-track:horizontal {
        -webkit-appearance: none !important;
        border-top: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-bottom: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-left: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
        border-right: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
    }
    ::-webkit-scrollbar-track-piece {
        -webkit-appearance: none !important;
        background: /*[[bg]]*/ !important;
        border-radius: 0 !important;
        box-shadow: none !important;
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
::-webkit-scrollbar-track-piece:vertical {
        -webkit-appearance: none !important;
        border-left: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-right: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-top: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
        border-bottom: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
    }
    ::-webkit-scrollbar-track-piece:horizontal {
        -webkit-appearance: none !important;
        border-top: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-bottom: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-left: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
        border-right: /*[[borderbgp]]*/ solid /*[[bgbc]]*/ !important;
    }
    ::-webkit-scrollbar-corner {
        -webkit-appearance: none !important;
        background: /*[[bg]]*/ !important;
        border: /*[[borderbg]]*/ solid /*[[bgbc]]*/ !important;
        border-radius: 0 !important;
        box-shadow: none !important;
    }
    ::-webkit-scrollbar-button {
        -webkit-appearance: none !important;
        width: /*[[button]]*/ !important;
        height: /*[[button]]*/ !important;
        border-radius: 0 !important;
    }
    ::-webkit-scrollbar-button:vertical {
        border-left: /*[[borderb]]*/ solid /*[[bbc]]*/ !important;
        border-right: /*[[borderb]]*/ solid /*[[bbc]]*/ !important;
        border-top: /*[[borderbp]]*/ solid /*[[bbc]]*/ !important;
        border-bottom: /*[[borderbp]]*/ solid /*[[bbc]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal {
        border-top: /*[[borderb]]*/ solid /*[[bbc]]*/ !important;
