style.css:
@-moz-document url-prefix("https://www.messenger.com/"), url-prefix("http://www.messenger.com/"), url-prefix("https://www.facebook.com/messages/"), url-prefix("http://www.facebook.com/messages/") {
/* Background  */

body {
    background: linear-gradient(132deg, #6bf43f 0%, #16A085 100%);
    background-color: #16A085;
}

/* Text Selection  */

::selection {
  background: #CECECE !important; /* WebKit/Blink Browsers */
}
::-moz-selection {
  background: #CECECE  !important;; /* Gecko Browsers */
}

/* White Overlay Remove */

._4sp8, ._4gd0 {
    background-color: #FFF0;
}

._1lj1, .sp_wViQ_eGMWFr, ._569x ._56ck, ._5iwm._6-_b ._58ak::before{
        filter: none;
}

/* Panel Transperency */

._1enh._7q1s, ._6ynl, ._4_j5, ._5irm._7mkm, ._7t1o {
    background: #ffffff35;
    
}

/* Light Text  */

._1htf, ._1lj0._6ybm, ._ih3, ._fy2._fy2, ._3eus{
    color: rgb(68, 68, 68) !important;
}

/* Fly Out + Menu Above New Message Box  */

._7t0j {
    transform: translateY(0%);
    visibility: hidden;
}

/* Multi-Color SVG to Black  */
    
svg, ._1lj1, .sp_wViQ_eGMWFr, ._569x ._56ck, ._5iwm._6-_b ._58ak::before{
    filter: grayscale() brightness(0);
}

/* Chat Link Background  */

._tih._310t._3xn3._3xn7, ._5swm {
    background: white !important;
}

._tih, _52mr _2poz _ui9 _4skb _5i_d {
    border-radius: 1.3em !important;
    border-color: #FFF0 !important; 
}
._5i_d {
    border: 1px solid rgba(0, 0, 0, 0);

}

/* Chat Reply Background  */

._3egs {
    background: #FFF0;
}
}
