style.css:
@-moz-document url-prefix("https://e.mail.ru/"), url-prefix("https://mail.ru/") {
:root {
    --generalBlueBackgroundColor: rgba(0, 0, 30, 0.72);
    --brightBlueBackgroundColor: rgba(0, 0, 30, 0.3);
    --linkColor: #a3cbff;
    --visitedLinkColor: #fff;
    --darkGreyColor: #111;
    --greyTransparentColor: rgba(0, 0, 25, 0.5);
    --greyerTransparentColor: rgba(0, 0, 25, 0.5);
}

/* Here you can put your own background for list of letters */
body.page .application-mail {
    --background-image: url(https://img.imgsmail.ru/themes/1.31.4/images/t2024/bg/2560x1600.jpg),url(https://img.imgsmail.ru/themes/1.31.4/images/t2024/bg/2560x1600.jpg);
    --background-size: 2560px 1600px;
}

.llc__background {
    background-color: rgba(0, 0, 10, 0.7) !important; 
}

.llc:hover .llc__panel, .llc:hover .llc__background {
    background-color: rgba(30, 30, 30, 1) !important; 
}

.ll-sj__normal, .ll-sj__search, .ll-crpt, .ll-crpt-t {
    color: #eee !important;
}

.list-letter-preview-action__message,
.i-color-warm-grey,
.nav__folder-name,
.llc__subject_unread .ll-sj__normal {
    color: white !important;
}

.ll-sp__normal, .ll-sp__search {
    color: #bbb !important;
}

.delimeter, .r-hr {
    border-top-color: #555 !important;
}

.dataset-letters {
    background-color: rgba(0, 0, 0, 0) !important; 
}

.x-ph .w-x-ph {
    background-color: rgba(30, 30, 30, 1) !important;
}

.x-ph__menu__button, .x-ph__menu__button:link, .x-ph__menu__button:visited, .x-ph__menu__button:hover, .x-ph__menu__button:active, .x-ph__menu__button:focus {
    border-left-color: rgba(30, 30, 30, 1) !important;
    border-right-color: rgba(30, 30, 30, 1) !important;
}

.x-ph__link, a.x-ph__link {
    color: white !important;
}

.search-panel__wrapper,
.portal-menu {
    background-color: rgba(0, 0, 60, 0.8) !important;
}

.r-search-panel,
.search-panel__wrapper .search-panel-button {
    background-color: var(--darkGreyColor) !important;
}

.search-panel__wrapper .search-panel-button__icon {
    background-color: var(--darkGreyColor) !important;
}

.search-panel__wrapper .search-panel-button__icon:hover {
    background-color: #ff9e00 !important;
}

.r-search-panel input, .r-text {
    color: white !important;
}



/* NEW SERVICE - TASKS */
.c0111 {
    background-color: #222;
}

.c0131 {
    color: white;
}

.r-search-suggest {
    background-color: #222 !important; 
    color: white !important;
}

.list-item__ico:not(.list-item__ico_flag):not(.list-item__ico_unread) {
    filter:invert(1);
}

.list__hr {
    background-color: #555 !important;   
}



/* MAIN MAIL.RU PAGE */
body {
    background-color: #001F50;
    background-image: url(https://i.ibb.co/8gyFp09/clouds.jpg) !important;   
    background-repeat: no-repeat !important;
    background-attachment: fixed;
}

@media only screen and (max-width: 1200px) {
  body {
    background-size: cover !important;
  }
}

@media only screen and (min-width: 1201px) {
  body {
    background-size: 120% !important;
  }
}

@keyframes newsHighlight {
  to {background-color: rgba(0, 0, 25, 0.5);}
}

.is-highlight {
    -webkit-animation: 3s newsHighlight ease forwards .1s infinite !important;
    -o-animation: 3s newsHighlight ease forwards .1s infinite !important;
    animation: 3s newsHighlight ease forwards .1s infinite !important;
    
}

.is-highlight:before {
    content: none !important;
}

.grid__container .grid__main-col,
.mailbox-container {
    background-color: transparent !important;   
}

.weather,
.horoscope,
.rates,
.mailbox {
    background-color: var(--greyTransparentColor) !important;
}

.rate__icon {
    filter:invert(1);
}

.tabs__item.is-active,
.head a,
.horoscope .info .title,
.rate__currency,
.account__name,
.unread-messages,
.weather__city,
.weather__block {
    color: white !important;
}

.list__item:after,
.account__email:after, 
.account__name:after
{
    background: transparent !important;
}

.grid__ccol {
    background-color: var(--greyerTransparentColor) !important;
    padding-left: 25px;
    border-radius: 0px 0px 10px 10px;
}

.tabs {
    background-color: var(--greyerTransparentColor) !important;
    padding-left: 25px;
    border-radius: 0px 10px 10px 0px;
}

.search__top {
    padding-top: 10px;
    padding-left: 25px;
    
}

a.search__category {
    color: var(--linkColor) !important;
}

.searchContainer {
    background-color: var(--greyerTransparentColor) !important;
    border-radius: 0px 0px 10px 0px;
}

.ph-avatar-icon {
    background-color: black !important;
}

.ph-container svg {
    fill: white !important;
}

.ph-sidebar {
 background-color: #222 !important;   
}

.ph-container {
    color: white !important;
}

.search__sample,
.search__input {
    background-color: var(--darkGreyColor) !important;
    color: white !important;
}

.weather__more,
.tabs__item:not(.is-active) {
    color: var(--linkColor) !important;
}

.restore,
.saveauth,
.i-link-deco,
.news-item a {
    color: white !important;
}

.head__email, 
.head__icon {
    color: white !important;   
}

.head__email:after {
    background: none !important;
}

.news-main__text-wrapper .i-link-deco {
    font-weight: bold;
}

.news-main:not(.is-highlight):after,
.news-item::after {
    box-shadow: none !important;
}

.resplash-btn_secondary
{
    background-color: #222 !important;
    color: var(--linkColor) !important;
}

.news-item .news-item__label {
    color: var(--linkColor) !important;
    padding-right: 20px;
}

.news-main__desc {
    color: #bbb !important;
}

.ph-container:before {
    background-color: #222 !important;
}

.ph-dropdown-icon {
    filter:invert(1);    
}

.ph-project__all-projects,
.ph-project__user-name,
.ph-container a:link, 
.ph-container a:visited {
    color: white !important;
}

.news-item.is-highlight {
    margin-right: 0px !important;
}

.news-item.is-highlight:after,
.news-item.is-highlight:before
{
    animation: none !important;
    -webkit-animation: none !important;
}

}
