style.css:
@-moz-document url-prefix("https://translation.frozendistrict.com") {
:root {
    --body: #2a2a2a;
    --body-lighter: #3F3F3F;
    --font: #eee;
    --font-dark: #3b3b3b;
    --green: #1FA385
}
body, .btn-info, .panel, .zen-unit:nth-child(odd) .translatetext, .multi-wrapper .non-selected-wrapper, .multi-wrapper .selected-wrapper {
    background-color: var(--body);
    color: var(--font);
}
a, footer a, .object-link a, .navbar-inverse .navbar-brand, .navbar-inverse .navbar-nav > li > a, .btn-group-settings a, .ct-label, legend, .full-cell a:hover, a.list-group-item {
    color: var(--font);
}
.breadcrumb a {
    color: var(--font) !important;
}
.navbar, .navbar-default, .navbar-static-top, .navbar-inverse, .btn-primary, .badge {
    background-color: var(--green);
}
.table-striped > tbody > tr:nth-of-type(odd), .btn-default, .zen-unit:nth-child(even), .zen-unit:nth-child(even) .translatetext, .table > tbody > tr > th.warning {
    background-color: var(--body-lighter);
}
.alert-info a {
    color: var(--font-dark);
    text-decoration: underline;
}
.select, .btn-ok, .textinput, .form-control, code, pre, .comment-content, .list-group-item, .btn-link, .search-input {
    background-color: var(--font-dark);
    color: var(--font);
}
.progress {
    background-color: #ADADAD;
}
.btn-link path, .ct-grid {
    fill: var(--font);
    stroke: var(--font);
}
.table > tbody > tr > td {
    border-top: 0;
}
.dropdown-menu, .dropdown-menu > li > a, .clickable-row:hover, .table > tbody > tr.info > th, .current_translation {
    background-color: var(--body-lighter);
    color: var(--font);
}
del {
    background-color: #993232;
}
ins {
    background-color: #428042;
}
}
